};

type ContainerOverrides = {
	text: {
		cardHeadline: Colour;
		cardStandfirst: Colour;
		cardKicker: Colour;
		cardByline: Colour;
		cardFooter: Colour;
		cardCommentCount: Colour;
		dynamoHeadline: Colour;
		dynamoKicker: Colour;
		dynamoSublinkKicker: Colour;
		dynamoMeta: Colour;
		container: Colour;
		containerToggle: Colour;
	};
	border: {
		container: Colour;
		lines: Colour;
	};
	background: {
		container: Colour;
		card: Colour;
	};
	topBar: {
		card: Colour;
	};
