type Edition = 'UK' | 'US' | 'INT' | 'AU';

type SharePlatform =
	| 'facebook'
	| 'twitter'
	| 'email'
	| 'whatsApp'
	| 'linkedIn'
	| 'messenger';

// shared type declarations
interface SimpleLinkType {
	url: string;
	title: string;
}

interface AdTargetParam {
	name: string;
	value: string | string[];
}

type CustomParams = {
	sens: 't' | 'f';
	urlkw: string[];
	[key: string]: string | string[] | number | number[] | boolean | boolean[];
};

type AdTargeting =
	| {
			adUnit: string;
			customParams: CustomParams;
			disableAds?: false;
	  }
	| {
			disableAds: true;
	  };

interface SectionNielsenAPI {
	name: string;
	apiID: string;
}

interface EditionCommercialProperties {
	adTargeting: AdTargetParam[];
	branding?: Branding;
}

type CommercialProperties = { [E in Edition]: EditionCommercialProperties };

type BrandingLogo = {
	src: string;
	link: string;
	label: string;
	dimensions: { width: number; height: number };
};

interface Branding {
	brandingType?: { name: string };
	sponsorName: string;
	logo: BrandingLogo;
	aboutThisLink: string;
	logoForDarkBackground?: BrandingLogo;
}

interface LinkType extends SimpleLinkType {
	longTitle: string;
	children?: LinkType[];
	mobileOnly?: boolean;
	pillar?: ArticlePillar;
	more?: boolean;
}

interface PillarType extends LinkType {
	pillar: ArticlePillar;
}

interface MoreType extends LinkType {
	more: true;
}

interface ReaderRevenueCategories {
	contribute: string;
	subscribe: string;
	support: string;
	supporter: string;
	gifting?: string;
}

type ReaderRevenueCategory = 'contribute' | 'subscribe' | 'support';
interface ReaderRevenuePositions {
	header: ReaderRevenueCategories;
	footer: ReaderRevenueCategories;
	sideMenu: ReaderRevenueCategories;
	ampHeader: ReaderRevenueCategories;
	ampFooter: ReaderRevenueCategories;
}

type ReaderRevenuePosition =
	| 'header'
	| 'footer'
	| 'sideMenu'
	| 'ampHeader'
	| 'ampFooter';

interface BaseNavType {
	otherLinks: MoreType;
	brandExtensions: LinkType[];
	currentNavLink: string;
	subNavSections?: SubNavType;
	readerRevenueLinks: ReaderRevenuePositions;
}

// TODO rename
interface SimpleNavType {
	pillars: PillarType[];
	otherLinks: MoreType;
	brandExtensions: LinkType[];
	readerRevenueLinks: ReaderRevenuePositions;
}

interface NavType extends BaseNavType {
	pillars: PillarType[];
}

interface SubNavBrowserType {
	currentNavLink: string;
	subNavSections?: SubNavType;
}

interface SubNavType {
	parent?: LinkType;
	links: LinkType[];
}

interface AuthorType {
	byline?: string;
	twitterHandle?: string;
	email?: string;
}

interface BlockContributor {
	name: string;
	imageUrl?: string;
	largeImageUrl?: string;
}

interface Block {
	id: string;
	elements: CAPIElement[];
	blockCreatedOn?: number;
	blockCreatedOnDisplay?: string;
	blockLastUpdated?: number;
	blockLastUpdatedDisplay?: string;
	title?: string;
	blockFirstPublished?: number;
	blockFirstPublishedDisplay?: string;
	blockFirstPublishedDisplayNoTimezone?: string;
	primaryDateLine: string;
	secondaryDateLine: string;
	createdOn?: number;
	createdOnDisplay?: string;
	lastUpdated?: number;
	lastUpdatedDisplay?: string;
	firstPublished?: number;
	firstPublishedDisplay?: string;
	contributors?: BlockContributor[];
}

interface Pagination {
	currentPage: number;
	totalPages: number;
	newest?: string;
	newer?: string;
	oldest?: string;
	older?: string;
}

interface FooterLink {
	text: string;
	url: string;
	dataLinkName: string;
	extraClasses?: string;
}
interface FooterType {
	footerLinks: FooterLink[][];
}

type ContentType =
	| 'article'
	| 'network'
	| 'section'
	| 'imageContent'
	| 'interactive'
	| 'gallery'
	| 'video'
	| 'audio'
	| 'liveBlog'
	| 'tag'
	| 'index'
	| 'crossword'
	| 'survey'
	| 'signup'
	| 'userid';

type PageTypeType = {
	hasShowcaseMainElement: boolean;
	isFront: boolean;
	isLiveblog: boolean;
	isMinuteArticle: boolean;
	isPaidContent: boolean;
	isPreview: boolean;
	isSensitive: boolean;
};

type MatchType = 'CricketMatchType' | 'FootballMatchType';

type CricketTeam = {
	name: string;
	home: boolean;
};

type FallOfWicket = {
	order: number;
};

type CricketInnings = {
	order: number;
	battingTeam: string;
	runsScored: string;
	declared: boolean;
	forfeited: boolean;
	fallOfWicket: FallOfWicket[];
	overs: string;
};

type CricketMatch = {
	matchId: string;
	competitionName: string;
	venueName: string;
	teams: CricketTeam[];
	innings: CricketInnings[];
	gameDate: string;
};

// Data types for the API request bodies from clients that require
// transformation before internal use. If we use the data as-is, we avoid the
// CAPI prefix. Note also, the 'CAPI' prefix naming convention is a bit
// misleading - the model is *not* the same as the Content API content models.

interface CAPILinkType {
	url: string;
	title: string;
	longTitle?: string;
	iconName?: string;
	children?: CAPILinkType[];
	pillar?: LegacyPillar;
	more?: boolean;
	classList?: string[];
}

interface CAPINavType {
	currentUrl: string;
	pillars: CAPILinkType[];
	otherLinks: CAPILinkType[];
	brandExtensions: CAPILinkType[];
	currentNavLink?: CAPILinkType;
	currentNavLinkTitle?: string;
	currentPillarTitle?: string;
	subNavSections?: {
		parent?: CAPILinkType;
		links: CAPILinkType[];
	};
	readerRevenueLinks: ReaderRevenuePositions;
}

// WARNING: run `gen-schema` task if changing this to update the associated JSON
// schema definition.
interface CAPIArticleType {
	headline: string;
	standfirst: string;
	webTitle: string;
	mainMediaElements: CAPIElement[];
	main: string;
	keyEvents: Block[];
	blocks: Block[];
	pinnedPost?: Block;
	pagination?: Pagination;
	author: AuthorType;

	/**
	 * @TJS-format date-time
	 */
	webPublicationDateDeprecated: string;
	webPublicationDate: string;
	webPublicationDateDisplay: string;
	webPublicationSecondaryDateDisplay: string;
	editionLongForm: string;
	editionId: Edition;
	pageId: string;
	version: number; // TODO: check who uses?
	tags: TagType[];
	format: CAPIFormat;

	// Include pillar and designType until we remove them upstream
	// We type designType as `string` for now so that the field is present,
	// but we don't care what's in it. Pillar we have a type for so we use it
	// but it shouldn't be important.
	designType: string;
	pillar: LegacyPillar;

	isImmersive: boolean;
	sectionLabel: string;
	sectionUrl: string;
	sectionName?: string;
	subMetaSectionLinks: SimpleLinkType[];
	subMetaKeywordLinks: SimpleLinkType[];
	shouldHideAds: boolean;
	isAdFreeUser: boolean;
	openGraphData: { [key: string]: string };
	twitterData: { [key: string]: string };
	webURL: string;
	linkedData: { [key: string]: any }[];
	config: ConfigType;

	showBottomSocialButtons: boolean;
	shouldHideReaderRevenue: boolean;

	// AMP specific (for now)
	guardianBaseURL: string;
	contentType: string;
	hasRelated: boolean;
	publication: string; // TODO: check who uses?
	hasStoryPackage: boolean;
	beaconURL: string;
	isCommentable: boolean;
	commercialProperties: CommercialProperties;
	starRating?: number;
	trailText: string;
	badge?: BadgeType;

	nav: CAPINavType; // TODO move this out as most code uses a different internal NAV model.

	pageFooter: FooterType;

	contributionsServiceUrl: string;
	slotMachineFlags?: string;

	pageType: PageTypeType;

	matchUrl?: string;
	matchType?: MatchType;
	isSpecialReport: boolean;

	// Interactives made on Frontend rather than DCR require special handling.
	// The logic is date-driven. See:
	// https://github.com/guardian/frontend/blob/main/common/app/model/dotcomrendering/InteractiveSwitchOver.scala#L7.
	isLegacyInteractive?: boolean;
	filterKeyEvents: boolean;

	// Included on live and dead blogs. Used when polling
	mostRecentBlockId?: string;
}

type StageType = 'DEV' | 'CODE' | 'PROD';

// FE* types are coming from Frontend
interface FEFrontType {
	pressedPage: FEPressedPageType;
	nav: CAPINavType;
	editionId: Edition;
	editionLongForm: string;
	guardianBaseURL: string;
	pageId: string;
	webTitle: string;
	webURL: string;
	config: FEFrontConfigType;
	commercialProperties: Record<string, unknown>;
	pageFooter: FooterType;
}

type DCRFrontType = {
	pressedPage: DCRPressedPageType;
	nav: CAPINavType;
	editionId: Edition;
	webTitle: string;
	config: FEFrontConfigType;
	pageFooter: FooterType;
};

type FEPressedPageType = {
	id: string;
	seoData: FESeoDataType;
	frontProperties: FEFrontPropertiesType;
	collections: FECollectionType[];
};

type DCRPressedPageType = {
	id: string;
	seoData: FESeoDataType;
	frontProperties: FEFrontPropertiesType;
	collections: DCRCollectionType[];
};

type FESeoDataType = {
	id: string;
	navSection: string;
	webTitle: string;
	description: string;
};

type FEFrontPropertiesType = {
	isImageDisplayed: boolean;
	commercial: Record<string, unknown>;
};

type FESupportingContent = {
	properties: {
		href?: string;
	};
	header?: {
		kicker?: {
			item?: {
				properties: {
					kickerText: string;
				};
			};
		};
		headline: string;
		url: string;
	};
	format?: CAPIFormat;
};

type DCRSupportingContent = {
	headline: string;
	url?: string;
	kickerText?: string;
	format: ArticleFormat;
};

type FEContainerType =
	| 'dynamic/fast'
	| 'dynamic/package'
	| 'dynamic/slow'
	| 'dynamic/slow-mpu'
	| 'fixed/large/slow-XIV'
	| 'fixed/medium/fast-XI'
	| 'fixed/medium/fast-XII'
	| 'fixed/medium/slow-VI'
	| 'fixed/medium/slow-VII'
	| 'fixed/medium/slow-XII-mpu'
	| 'fixed/small/fast-VIII'
	| 'fixed/small/slow-I'
	| 'fixed/small/slow-III'
	| 'fixed/small/slow-IV'
	| 'fixed/small/slow-V-half'
	| 'fixed/small/slow-V-mpu'
	| 'fixed/small/slow-V-third'
	| 'fixed/thrasher'
	| 'fixed/video'
	| 'nav/list'
	| 'nav/media-list'
	| 'news/most-popular';

type FEContainerPalette =
	| 'EventPalette'
	| 'SombreAltPalette'
	| 'EventAltPalette'
	| 'InvestigationPalette'
	| 'LongRunningAltPalette'
	| 'LongRunningPalette'
	| 'SombrePalette'
	| 'Canonical'
	| 'Dynamo'
	| 'Special'
	| 'DynamoLike'
	| 'Special'
	| 'Breaking'
	| 'Podcast'
	| 'Branded'
	| 'BreakingPalette';

type DCRContainerPalette =
	| 'EventPalette'
	| 'SombreAltPalette'
	| 'EventAltPalette'
	| 'InvestigationPalette'
	| 'LongRunningAltPalette'
	| 'LongRunningPalette'
	| 'SombrePalette'
	| 'BreakingPalette';

// TODO: These may need to be declared differently than the front types in the future
type DCRContainerType = FEContainerType;

type FEFrontCard = {
	properties: {
		isBreaking: boolean;
		showMainVideo: boolean;
		showKickerTag: boolean;
		showByline: boolean;
		imageSlideshowReplace: boolean;
		maybeContent?: {
			trail: {
				trailPicture?: {
					allImages: {
						index: number;
						fields: {
							displayCredit?: string;
							source?: string;
							photographer?: string;
							isMaster?: string;
							altText?: string;
							height: string;
							credit: string;
							mediaId: string;
							width: string;
						};
						mediaType: string;
						url: string;
					}[];
				};
				byline?: string;
				thumbnailPath?: string;
				webPublicationDate: number;
			};
			metadata: {
				id: string;
				webTitle: string;
				webUrl: string;
				type: string;
				sectionId?: { value: string };
				format: CAPIFormat;
			};
			fields: {
				main: string;
				body: string;
				standfirst?: string;
			};
			elements: Record<string, unknown>;
			tags: Record<string, unknown>;
		};
		maybeContentId?: string;
		isLiveBlog: boolean;
		isCrossword: boolean;
		byline?: string;
		webTitle: string;
		linkText?: string;
		webUrl?: string;
		editionBrandings: { edition: { id: Edition } }[];
	};
	header: {
		isVideo: boolean;
		isComment: boolean;
		isGallery: boolean;
		isAudio: boolean;
		kicker?: {
			type: string;
			item?: {
				properties: {
					kickerText: string;
				};
			};
		};
		seriesOrBlogKicker?: {
			properties: {
				kickerText: string;
			};
			name: string;
			url: string;
			id: string;
		};
		headline: string;
		url: string;
		hasMainVideoElement: boolean;
	};
	card: {
		id: string;
		cardStyle: {
			type: string;
		};
		webPublicationDateOption?: number;
		lastModifiedOption?: number;
		trailText?: string;
		starRating?: number;
		shortUrlPath?: string;
		shortUrl: string;
		group: string;
		isLive: boolean;
	};
	discussion: {
		isCommentable: boolean;
		isClosedForComments: boolean;
		discussionId?: string;
	};
	display: {
		isBoosted: boolean;
		showBoostedHeadline: boolean;
		showQuotedHeadline: boolean;
		imageHide: boolean;
		showLivePlayable: boolean;
	};
	format?: CAPIFormat;
	enriched?: Record<string, unknown>;
	supportingContent?: FESupportingContent[];
	cardStyle?: {
		type: string;
	};
	type: string;
};

type DCRFrontCard = {
	format: ArticleFormat;
	url: string;
	headline: string;
	trailText?: string;
	webPublicationDate?: string;
	image?: string;
	kickerText?: string;
	/** @see JSX.IntrinsicAttributes["data-link-name"] */
	dataLinkName: string;
};

type FECollectionType = {
	id: string;
	displayName: string;
	curated: FEFrontCard[];
	backfill: FEFrontCard[];
	treats: FEFrontCard[];
	lastUpdate?: number;
	href?: string;
	groups?: string[];
	collectionType: FEContainerType;
	uneditable: boolean;
	showTags: boolean;
	showSections: boolean;
	hideKickers: boolean;
	showDateHeader: boolean;
	showLatestUpdate: boolean;
	config: {
		displayName: string;
		metadata?: { type: FEContainerPalette }[];
		collectionType: FEContainerType;
		href?: string;
		groups?: string[];
		uneditable: boolean;
		showTags: boolean;
		showSections: boolean;
		hideKickers: boolean;
		showDateHeader: boolean;
		showLatestUpdate: boolean;
		excludeFromRss: boolean;
		showTimestamps: boolean;
		hideShowMore: boolean;
		platform: string;
	};
