const blockStyle = (pillar: ArticleTheme) => css`
	padding: 6px 10px 12px;
	background-color: ${neutral[100]};
	border-top: 1px solid ${pillarPalette_DO_NOT_USE[pillar].dark};
	border-bottom: 1px solid ${neutral[93]};
