										<p css={labelStyle}>
											{moustacheVariable('bottomLeft')}
										</p>
									</div>
									<div css={rightStyle}>
										<p css={topRightStyle}>
											{moustacheVariable('topRight')}
										</p>
										<p css={labelStyle}>
