const makeFallback = (html: string): string | null => {
	const { window } = new JSDOM(html);
	const blockquotes = window.document.getElementsByTagName('blockquote');

	if (blockquotes.length !== 1) {
		return null;
	}

	const q = blockquotes[0];
	return q.innerHTML;
};

export const TwitterBlockComponent: React.FC<{
	element: TweetBlockElement;
	pillar: ArticleTheme;
}> = ({ element, pillar }) => {
	const fallbackHTML = makeFallback(element.html);

	return (
		<amp-twitter
			width="2"
			height={element.hasMedia ? 2 : 1}
			layout="responsive"
			data-tweetid={element.id}
			data-dnt="true"
		>
			{fallbackHTML && (
				<div placeholder="" css={TextStyle(pillar)}>
					<blockquote
						dangerouslySetInnerHTML={{ __html: fallbackHTML }}
					/>
				</div>
			)}
		</amp-twitter>
	);
};

import React from 'react';
import { constructQuery } from '../../../lib/querystring';
import { Caption } from '../Caption';

type EmbedConfig = {
	adsConfig:
		| {
				adTagParameters: {
					iu: string;
					cust_params: string;
				};
				disableAds?: false;
		  }
		| {
				disableAds: true;
		  };
};

const buildEmbedConfig = (adTargeting: AdTargeting): EmbedConfig => {
	switch (adTargeting.disableAds) {
		case true:
			return {
				adsConfig: {
					disableAds: true,
				},
			};
		case false:
		case undefined:
			return {
				adsConfig: {
					adTagParameters: {
						iu: `${adTargeting.adUnit || ''}`,
						cust_params: encodeURIComponent(
							constructQuery(adTargeting.customParams || {}),
						),
					},
				},
			};
	}
};

export const YoutubeBlockComponentAMP: React.FC<{
	element: YoutubeBlockElement;
	pillar: ArticleTheme;
	adTargeting?: AdTargeting;
}> = ({ element, pillar, adTargeting }) => {
	// https://www.ampproject.org/docs/reference/components/amp-youtube
	// https://developers.google.com/youtube/player_parameters
	const attributes: { [key: string]: any } = {
		id: `gu-video-youtube-${element.id}`,
		'data-videoid': element.assetId,
		layout: 'responsive',
		width: '16',
		height: '9',
		'data-block-on-consent': '', // Block player until consent is obtained
		'data-param-modestbranding': true, // Remove YouTube logo
		credentials: 'omit',
	};

	if (adTargeting) {
		attributes['data-param-embed_config'] = JSON.stringify(
			buildEmbedConfig(adTargeting),
		);
	}

	if (element.channelId) {
		// related videos metadata
		attributes['data-param-rel'] = '0';
	}

	return (
		<Caption
			captionText={element.mediaTitle}
			pillar={pillar}
			padCaption={true}
		>
			<amp-youtube {...attributes} />
		</Caption>
	);
};

