		it('should intersperse ads every 5 blocks up to a maximum of 8', () => {
			const blocks = Array(100).fill('foo');

			const slots = findBlockAdSlots(blocks);

			expect(slots).toEqual([4, 9, 14, 19, 24, 29, 34, 39]);
