	}
};
export const ASSET_ORIGIN = decideAssetOrigin(process.env.GU_STAGE);

export const getScriptArrayFromFile = (
	file: `${string}.js`,
): { src: string; legacy?: boolean }[] => {
	if (!file.endsWith('.js'))
		throw new Error('Invalid filename: extension must be .js');

	const isDev = process.env.NODE_ENV === 'development';

	const filename = isDev ? file : manifest[file];
	const legacyFilename = isDev
		? file.replace('.js', '.legacy.js')
		: legacyManifest[file];

	const scripts = [];

	if (filename) {
		scripts.push({
			src: `${ASSET_ORIGIN}assets/${filename}`,
			legacy: false,
		});
	}
	if (legacyFilename) {
		scripts.push({
			src: `${ASSET_ORIGIN}assets/${legacyFilename}`,
			legacy: true,
		});
	}

	return scripts;
};

export const getContributorTags = (tags: TagType[]): TagType[] => {
	return tags.filter((t) => t.type === 'Contributor');
};

export const getContributorTagsForToken = (
	tags: TagType[],
	token: string,
): TagType[] => {
	return getContributorTags(tags).filter((t) => t.title === token);
};

type BylineComponent = { token: string; tag: TagType } | string;

export const getBylineComponentsFromTokens = (
	tokens: string[],
	tags: TagType[],
): BylineComponent[] => {
	const [bylineComponents] = tokens.reduce<[BylineComponent[], TagType[]]>(
		([bylines, remainingTags], token) => {
			const [firstContributorTag] = getContributorTagsForToken(
				remainingTags,
				token,
			);
			if (!firstContributorTag) {
				return [bylines.concat(token), remainingTags];
			}

			// We've used this tag, so remove it from the list of possible tags.
			// This ensures we don't use the same contributor tag when two identical
			// names with different contributor tags are used.
			const newRemainingTags = remainingTags.filter(
				(tag) => !(tag.id === firstContributorTag.id),
			);

			return [
				bylines.concat({ tag: firstContributorTag, token }),
				newRemainingTags,
			];
		},
		[[], tags],
	);

	return bylineComponents;
};

export enum TickerCountType {
	money = 'money',
	people = 'people',
}

export interface TickerData {
	total: number;
	goal: number;
}

const tickerUrl = (countType: TickerCountType): string =>
	countType === TickerCountType.people
		? 'https://support.theguardian.com/supporters-ticker.json'
		: 'https://support.theguardian.com/ticker.json';

const checkForErrors = (response: Response): Promise<Response> => {
	if (!response.ok) {
		return Promise.reject(
			response.statusText ||
				`Ticker api call returned HTTP status ${response.status}`,
		);
	}
	return Promise.resolve(response);
};

const parse = (json: { total: string; goal: string }): Promise<TickerData> => {
	const total = parseInt(json.total, 10);
	const goal = parseInt(json.goal, 10);

	if (!Number.isNaN(total) && !Number.isNaN(goal)) {
		return Promise.resolve({
			total,
			goal,
		});
	}
	return Promise.reject(Error(`Failed to parse ticker data: ${json}`));
};

export const fetchTickerData = (
	tickerType: TickerCountType,
): Promise<TickerData> =>
	fetch(tickerUrl(tickerType))
		.then((response) => checkForErrors(response))
		.then((response) => response.json())
		.then(parse);

