import { constructQuery } from './querystring';

export type AuthenticationComponentId =
	| 'amp_sidebar_signin'
	| 'guardian_signin_header'
	| 'signin_to_comment'
	| 'register_to_comment';

type AuthenticationEventParams = {
	componentType: string;
	componentId: AuthenticationComponentId;
	viewId?: string;
};

export const createAuthenticationEventParams = (
	componentId: AuthenticationComponentId,
	pageViewId?: string,
) => {
	const params: AuthenticationEventParams = {
		componentType: 'identityauthentication',
		componentId,
	};

	if (pageViewId) {
		params.viewId = pageViewId;
	}

	return `componentEventParams=${encodeURIComponent(constructQuery(params))}`;
};

export const joinUrl = (parts: string[]) => {
	// Remove any leading or trailing slashes from all parts and then join cleanly on
	// a single slash - prevents malformed urls
	const trimmed = parts
		.map((part) => {
			// Trim left
			if (part.substr(0, 1) === '/') return part.slice(1);
			return part;
		})
		.map((part) => {
			// Trim right
			if (part.substr(part.length - 1, 1) === '/')
				return part.slice(0, -1);
			return part;
		});

	return trimmed.join('/');
};

