import { JSDOM } from 'jsdom';

const ishalfWidthImage = (element: CAPIElement): boolean => {
	if (!element) return false;
	return (
		element._type ===
			'model.dotcomrendering.pageElements.ImageBlockElement' &&
		element.role === 'halfWidth'
	);
};

const isMultiImage = (element: CAPIElement): boolean => {
	if (!element) return false;
	return (
		element._type ===
		'model.dotcomrendering.pageElements.MultiImageBlockElement'
	);
};

const isImage = (element: CAPIElement): boolean => {
	if (!element) return false;
	return (
		element._type === 'model.dotcomrendering.pageElements.ImageBlockElement'
	);
};

const isTitle = (element: CAPIElement): boolean => {
	if (!element) return false;
	// Checks if this element is a 'title' based on the convention: <h2>Title text</h2>
	if (
		element._type !==
		'model.dotcomrendering.pageElements.SubheadingBlockElement'
	)
		return false;
	const frag = JSDOM.fragment(element.html);
	return frag.firstElementChild?.nodeName === 'H2';
};

const extractTitle = (element: CAPIElement): string => {
	// We cast here because we're know this element is a subheading but TS isn't sure
	const subHeading = element as SubheadingBlockElement;
	// Extract 'title' based on the convention: <h2>Title text</h2>
	const frag = JSDOM.fragment(subHeading.html);
	if (!frag || !frag.firstElementChild) return '';
	const isH2tag = frag.firstElementChild.nodeName === 'H2';
	if (isH2tag) {
		// element is an essay title
		return (frag.textContent && frag.textContent.trim()) || '';
	}
	return '';
};

const isCaption = (element: CAPIElement): boolean => {
	if (!element) return false;
	// Checks if this element is a 'caption' based on the convention: <ul><li><Caption text</li></ul>
	if (
		element._type !== 'model.dotcomrendering.pageElements.TextBlockElement'
	) {
		return false;
	}
	const frag = JSDOM.fragment(element.html);
	if (!frag || !frag.firstElementChild) return false;
	const hasULwrapper = frag.firstElementChild.nodeName === 'UL';
	const containsLItags = frag.firstElementChild.outerHTML.includes('<li>');
	return hasULwrapper && containsLItags;
};

const extractCaption = (element: CAPIElement): string => {
	// Extract 'caption' based on the convention: <ul><li><Caption text</li></ul>
	// We cast here because we're know this element is a text element but TS isn't sure
	const textElement = element as TextBlockElement;
	const frag = JSDOM.fragment(textElement.html);
	if (!frag || !frag.firstElementChild) return '';
	const hasULwrapper = frag.firstElementChild.nodeName === 'UL';
	const containsLItags = frag.firstElementChild.outerHTML.includes('<li>');
	if (hasULwrapper && containsLItags) {
		return textElement.html;
	}
	return '';
};

const constructMultiImageElement = (
	thisElement: CAPIElement,
	nextElement: CAPIElement,
): MultiImageBlockElement => {
	// We cast here because we're certain each element is an image (because of the guards we set earlier) but
	// TS isn't inferring that and needs a little help
	const first = thisElement as ImageBlockElement;
	const second = nextElement as ImageBlockElement;
	return {
		_type: 'model.dotcomrendering.pageElements.MultiImageBlockElement',
		elementId: first.elementId,
		images: [
			{
				...first,
				data: {
					...first.data,
					caption: '', // Delete any existing caption (special captions might be added later)
				},
			},
			{
				...second,
				data: {
					...second.data,
					caption: '', // ibid
				},
			},
		],
	};
};

const addMultiImageElements = (elements: CAPIElement[]): CAPIElement[] => {
	const withMultiImageElements: CAPIElement[] = [];
	elements.forEach((thisElement, i) => {
		const nextElement = elements[i + 1];
		if (ishalfWidthImage(thisElement) && ishalfWidthImage(nextElement)) {
			// Pair found. Add a multi element and remove the next entry
			withMultiImageElements.push(
				constructMultiImageElement(thisElement, nextElement),
			);
			// Remove the next element
			elements.splice(i + 1, 1);
		} else {
			// Pass through
			withMultiImageElements.push(thisElement);
		}
	});
	return withMultiImageElements;
};

const addTitles = (elements: CAPIElement[]): CAPIElement[] => {
	const withTitles: CAPIElement[] = [];
	elements.forEach((thisElement, i) => {
		const nextElement = elements[i + 1];
		const subsequentElement = elements[i + 2];
		if (isImage(thisElement) && isTitle(nextElement)) {
			// This element is an image and is immediately followed by a title
			withTitles.push({
				...thisElement,
				title: extractTitle(nextElement),
			} as ImageBlockElement);
			// Remove the element
			elements.splice(i + 1, 1);
		} else if (
			isImage(thisElement) &&
			isCaption(nextElement) &&
			isTitle(subsequentElement)
		) {
			// This element is an image, was followed by a caption, and then had a title after it
			withTitles.push({
				...thisElement,
				title: extractTitle(subsequentElement),
			} as ImageBlockElement);
			// Remove the element
			elements.splice(i + 2, 1);
		} else {
			// Pass through
			withTitles.push(thisElement);
		}
	});
	return withTitles;
};

const addCaptionsToImages = (elements: CAPIElement[]): CAPIElement[] => {
	const withSpecialCaptions: CAPIElement[] = [];
	elements.forEach((thisElement, i) => {
		const nextElement = elements[i + 1];
		const subsequentElement = elements[i + 2];
		if (isImage(thisElement) && isCaption(nextElement)) {
			const thisImage = thisElement as ImageBlockElement;
			withSpecialCaptions.push({
				...thisImage,
				data: {
					...thisImage.data,
					caption: extractCaption(nextElement),
				},
			} as ImageBlockElement);
			// Remove the next element
			elements.splice(i + 1, 1);
		} else if (
			isImage(thisElement) &&
			isTitle(nextElement) &&
			isCaption(subsequentElement)
		) {
			const thisImage = thisElement as ImageBlockElement;
			withSpecialCaptions.push({
				...thisImage,
				data: {
					...thisImage.data,
					caption: extractCaption(subsequentElement),
				},
			} as ImageBlockElement);
			// Remove the subsequent element
			elements.splice(i + 2, 1);
		} else {
			// Pass through
			withSpecialCaptions.push(thisElement);
		}
	});
	return withSpecialCaptions;
};

const addCaptionsToMultis = (elements: CAPIElement[]): CAPIElement[] => {
	const withSpecialCaptions: CAPIElement[] = [];
	elements.forEach((thisElement, i) => {
		const nextElement = elements[i + 1];
		const subsequentElement = elements[i + 2];
		if (isMultiImage(thisElement) && isCaption(nextElement)) {
			withSpecialCaptions.push({
				...thisElement,
				caption: extractCaption(nextElement),
			} as MultiImageBlockElement);
			// Remove the next element
			elements.splice(i + 1, 1);
		} else if (
			isMultiImage(thisElement) &&
			isTitle(nextElement) &&
			isCaption(subsequentElement)
		) {
			withSpecialCaptions.push({
				...thisElement,
				caption: extractCaption(subsequentElement),
			} as MultiImageBlockElement);
			// Remove the subsequent element
			elements.splice(i + 2, 1);
		} else {
			// Pass through
			withSpecialCaptions.push(thisElement);
		}
	});
	return withSpecialCaptions;
};

const stripCaptions = (elements: CAPIElement[]): CAPIElement[] => {
	// Remove all captions from all images
	const withoutCaptions: CAPIElement[] = [];
	elements.forEach((thisElement) => {
		if (
			thisElement._type ===
			'model.dotcomrendering.pageElements.ImageBlockElement'
		) {
			// Remove the caption from this image
			withoutCaptions.push({
				...thisElement,
				data: {
					...thisElement.data,
					caption: '',
				},
			} as ImageBlockElement);
		} else {
			// Pass through
			withoutCaptions.push(thisElement);
		}
	});
	return withoutCaptions;
};

const removeCredit = (elements: CAPIElement[]): CAPIElement[] => {
	// Remove credit from all images
	const withoutCredit: CAPIElement[] = [];
	elements.forEach((thisElement) => {
		if (
			thisElement._type ===
			'model.dotcomrendering.pageElements.ImageBlockElement'
		) {
			// Remove the credit from this image
			withoutCredit.push({
				...thisElement,
				data: {
					...thisElement.data,
					credit: '',
				},
			} as ImageBlockElement);
		} else {
			// Pass through
			withoutCredit.push(thisElement);
		}
	});
	return withoutCredit;
};

class Enhancer {
	elements: CAPIElement[];

	constructor(elements: CAPIElement[]) {
		this.elements = elements;
	}

	stripCaptions() {
		this.elements = stripCaptions(this.elements);
		return this;
	}

	addMultiImageElements() {
		this.elements = addMultiImageElements(this.elements);
		return this;
	}

	addTitles() {
		this.elements = addTitles(this.elements);
		return this;
	}

	addCaptionsToMultis() {
		this.elements = addCaptionsToMultis(this.elements);
		return this;
	}

	addCaptionsToImages() {
		this.elements = addCaptionsToImages(this.elements);
		return this;
	}

	removeCredit() {
		this.elements = removeCredit(this.elements);
		return this;
	}
}

const enhance = (
	elements: CAPIElement[],
	isPhotoEssay: boolean,
): CAPIElement[] => {
	if (isPhotoEssay) {
		return (
			new Enhancer(elements)
				// Photo essays by convention have all image captions removed and rely completely on
				// special captions set using the ul/li trick
				.stripCaptions()
				// By convention, photo essays don't include credit for images in the caption
				.removeCredit()
				// Replace pairs of halfWidth images with MultiImageBlockElements
				.addMultiImageElements()
				// Photo essay have a convention of adding titles to images if the subsequent block is a h2
				.addTitles()
				// If any MultiImageBlockElement is followed by a ul/l caption, delete the special caption
				// element and use the value for the multi image `caption` prop
				.addCaptionsToMultis()
				// In photo essays, we also use ul captions for normal images as well
				.addCaptionsToImages().elements
		);
	}

	return (
		new Enhancer(elements)
			// Replace pairs of halfWidth images with MultiImageBlockElements
			.addMultiImageElements()
			// If any MultiImageBlockElement is followed by a ul/l caption, delete the special caption
			// element and use the value for the multi image `caption` prop
			.addCaptionsToMultis().elements
	);
};

export const enhanceImages = (blocks: Block[], format: CAPIFormat): Block[] => {
	const isPhotoEssay = format.design === 'PhotoEssayDesign';

	return blocks.map((block: Block) => {
		return {
			...block,
			elements: enhance(block.elements, isPhotoEssay),
		};
	});
};

import { JSDOM } from 'jsdom';

const isFalseH3 = (element: CAPIElement): boolean => {
	if (!element) return false;
	// Checks if this element is a 'false h3' based on the convention: <p><strong><H3 text</strong></p>
	if (
		element._type !== 'model.dotcomrendering.pageElements.TextBlockElement'
	) {
		return false;
	}
	const frag = JSDOM.fragment(element.html);
	if (!frag || !frag.firstElementChild) return false;
	const html = frag.firstElementChild.outerHTML;
	// The following things must be true for an element to be a faux H3
	const hasPwrapper = frag.firstElementChild.nodeName === 'P';
	const containsStrongtags =
		frag.firstElementChild.outerHTML.includes('<strong>');
	const doesNotContainLinks =
		!frag.firstElementChild.outerHTML.includes('<a>');
	const htmlLength = html.length;
	const startStrong = html.substr(0, 11) === '<p><strong>';
	const endsStrong = html.substr(htmlLength - 13) === '</strong></p>';

	return (
		hasPwrapper &&
		containsStrongtags &&
		doesNotContainLinks &&
		startStrong &&
		endsStrong
	);
};

const extractH3 = (element: CAPIElement): string => {
	// Extract the text based on the convention: <p><strong><H3 text</strong></p>
	const textElement = element as TextBlockElement;
	const frag = JSDOM.fragment(textElement.html);
	if (isFalseH3(element)) {
		return (
			frag.firstElementChild?.innerHTML
				.split('<strong>')
				.join('')
				.split('</strong>')
				.join('') || ''
		);
	}
	return '';
};

const isStarRating = (element: CAPIElement): boolean => {
	const isStar = (charactor: string): boolean => {
		return charactor === 'â˜…' || charactor === 'â˜†';
	};

	if (!element) return false;
	// Checks if this element is a 'star rating' based on the convention: <p>â˜…â˜…â˜…â˜…â˜†</p>
	if (element._type !== 'model.dotcomrendering.pageElements.TextBlockElement')
		return false;
	const frag = JSDOM.fragment(element.html);
	const hasPTags = frag.firstElementChild?.nodeName === 'P';
	const text = frag.textContent || '';
	// Loop the string making sure each letter is a star
	for (const letter of text) {
		if (!isStar(letter)) return false;
	}
	const hasFiveStars = text.length === 5;
	return hasPTags && hasFiveStars;
};

const extractStarCount = (element: CAPIElement): number => {
	const isSelectedStar = (charactor: string): boolean => {
		return charactor === 'â˜…';
	};
	// Returns the count of stars
	const textElement = element as TextBlockElement;
	const frag = JSDOM.fragment(textElement.html);
	const text = frag.textContent || '';
	// Loop the string counting selected stars
	let starCount = 0;
	for (const letter of text) {
		if (isSelectedStar(letter)) starCount += 1;
	}
	return starCount;
};

const isStarableImage = (element: CAPIElement | undefined): boolean => {
	return (
		element?._type ===
			'model.dotcomrendering.pageElements.ImageBlockElement' &&
		element.role !== 'thumbnail'
	);
};

const starifyImages = (elements: CAPIElement[]): CAPIElement[] => {
	const starified: CAPIElement[] = [];
	let previousRating: number | undefined;
	elements.forEach((thisElement, index) => {
		switch (thisElement._type) {
			case 'model.dotcomrendering.pageElements.TextBlockElement':
				if (
					isStarRating(thisElement) &&
					isStarableImage(elements[index + 1])
				) {
					// Remember this rating so we can add it to the next element
					previousRating = extractStarCount(thisElement);
				} else {
					// Pass through
					starified.push(thisElement);
				}
				break;
			case 'model.dotcomrendering.pageElements.ImageBlockElement':
				if (
					previousRating !== undefined &&
					isStarableImage(thisElement)
				) {
					// Add this image using the rating we remembered
					starified.push({
						...thisElement,
						starRating: previousRating,
					});
					previousRating = undefined;
				} else {
					// Pass through
					starified.push(thisElement);
				}
				break;
			default:
				// Pass through
				starified.push(thisElement);
		}
	});
	return starified;
};

const inlineStarRatings = (elements: CAPIElement[]): CAPIElement[] => {
	const withStars: CAPIElement[] = [];
	elements.forEach((thisElement) => {
		if (
			thisElement._type ===
				'model.dotcomrendering.pageElements.TextBlockElement' &&
			isStarRating(thisElement)
		) {
			const rating = extractStarCount(thisElement);
			// Inline this image
			withStars.push({
				_type: 'model.dotcomrendering.pageElements.StarRatingBlockElement',
				elementId: thisElement.elementId,
				rating,
				size: 'large',
			});
		} else {
			// Pass through
			withStars.push(thisElement);
		}
	});
	return withStars;
};

const makeThumbnailsRound = (elements: CAPIElement[]): CAPIElement[] => {
	const inlined: CAPIElement[] = [];
	elements.forEach((thisElement) => {
		if (
			thisElement._type ===
				'model.dotcomrendering.pageElements.ImageBlockElement' &&
			thisElement.role === 'thumbnail'
		) {
			// Make this image round
			inlined.push({
				...thisElement,
				isAvatar: true,
			} as ImageBlockElement);
		} else {
			// Pass through
			inlined.push(thisElement);
		}
	});
	return inlined;
};

const isItemLink = (element: CAPIElement): boolean => {
	if (!element) return false;
	// Checks if this element is a 'item link' based on the convention: <ul> <li>...</li> </ul>
	if (
		element._type !== 'model.dotcomrendering.pageElements.TextBlockElement'
	) {
		return false;
	}
	const frag = JSDOM.fragment(element.html);
	if (!frag || !frag.firstElementChild) return false;

	const hasULWrapper = frag.firstElementChild.nodeName === 'UL';
	const hasOnlyOneChild = frag.firstElementChild.childElementCount === 1;
	const hasLINestedWrapper =
		frag.firstElementChild.firstElementChild?.nodeName === 'LI';

	return hasULWrapper && hasOnlyOneChild && hasLINestedWrapper;
};

const removeGlobalH2Styles = (elements: CAPIElement[]): CAPIElement[] => {
	/**
	 * Article pages come with some global style rules, one of which affects h2
	 * tags. But for numbered lists we don't want these styles because we use
	 * these html elements to represents our special titles. Rather than start a
	 * css war, this enhancer uses the `data-ignore` attribute which is a contract
	 * established to allow global styles to be ignored.
	 *
	 * All h2 tags inside an article of Design: NumberedList have this attirbute
	 * set.
	 */
	const withH2StylesIgnored: CAPIElement[] = [];
	elements.forEach((thisElement) => {
		if (
			thisElement._type ===
			'model.dotcomrendering.pageElements.SubheadingBlockElement'
		) {
			withH2StylesIgnored.push({
				...thisElement,
				html: thisElement.html.replace(
					'<h2>',
					'<h2 data-ignore="global-h2-styling">',
				),
			});
		} else {
			// Pass through
			withH2StylesIgnored.push(thisElement);
		}
	});
	return withH2StylesIgnored;
};

const addH3s = (elements: CAPIElement[]): CAPIElement[] => {
	/**
	 * Why not just add H3s in Composer?
	 * Truth is, you can't. So to get around this there's a convention that says if
	 * you insert <p><strong>Faux H3!</strong>,</p> then we replace it with a h3 tag
	 * instead.
	 *
	 * Note. H3s don't have any styles so we have to add them. In Frontend, they use
	 * a 'fauxH3' class for this. In DCR we add `globalH3Styles` which was added at
	 * the same time as this code.
	 */
	const withH3s: CAPIElement[] = [];
	let previousItem: CAPIElement | undefined;
	elements.forEach((thisElement) => {
		if (
			thisElement._type ===
				'model.dotcomrendering.pageElements.TextBlockElement' &&
			isFalseH3(thisElement)
		) {
			const h3Text = extractH3(thisElement);

			// To avoid having to depend on the ordering of the enhancer (which could easily be a source of bugs)
			// We determine if previous items are `ItemLinkBlockElement` through type and `isItemLink` functions
			const isPreviousItemLink =
				previousItem?._type ===
					'model.dotcomrendering.pageElements.ItemLinkBlockElement' ||
				(previousItem && isItemLink(previousItem));

			withH3s.push(
				{
					_type: 'model.dotcomrendering.pageElements.DividerBlockElement',
					size: 'full',
					spaceAbove: isPreviousItemLink ? 'loose' : 'tight',
				},
				{
					...thisElement,
					html: `<h3>${h3Text}</h3>`,
				},
			);
		} else {
			// Pass through
			withH3s.push(thisElement);
		}
		previousItem = thisElement;
	});
	return withH3s;
};

const addItemLinks = (elements: CAPIElement[]): CAPIElement[] => {
	const withItemLink: CAPIElement[] = [];
	elements.forEach((thisElement) => {
		if (
			thisElement._type ===
				'model.dotcomrendering.pageElements.TextBlockElement' &&
			isItemLink(thisElement)
		) {
			withItemLink.push(
				{
					_type: 'model.dotcomrendering.pageElements.DividerBlockElement',
					size: 'full',
					spaceAbove: 'tight',
				},
				{
					...thisElement,
					_type: 'model.dotcomrendering.pageElements.ItemLinkBlockElement',
				},
			);
		} else {
			// Pass through
			withItemLink.push(thisElement);
		}
	});
	return withItemLink;
};

const addTitles = (
	elements: CAPIElement[],
	format: CAPIFormat,
): CAPIElement[] => {
	/**
	 * Why not just add H3s in Composer?
	 * Truth is, you can't. So to get around this there's a convention that says if
	 * you insert <p><strong>Faux H3!</strong>,</p> then we replace it with a h3 tag
	 * instead.
	 *
	 * Note. H3s don't have any styles so we have to add them. In Frontend, they use
	 * a 'fauxH3' class for this. In DCR we add `globalH3Styles` which was added at
	 * the same time as this code.
	 */
	const withTitles: CAPIElement[] = [];
	let position = 1;
	elements.forEach((thisElement) => {
		if (
			thisElement._type ===
			'model.dotcomrendering.pageElements.SubheadingBlockElement'
		) {
			const { html } = thisElement;
			withTitles.push(
				{
					_type: 'model.dotcomrendering.pageElements.DividerBlockElement',
					size: 'full',
					spaceAbove: 'loose',
				},
				{
					_type: 'model.dotcomrendering.pageElements.NumberedTitleBlockElement',
					elementId: thisElement.elementId,
					position,
					html,
					format,
				},
			);
			position += 1;
		} else {
			// Pass through
			withTitles.push(thisElement);
		}
	});
	return withTitles;
};

class Enhancer {
	elements: CAPIElement[];

	format: CAPIFormat;

	constructor(elements: CAPIElement[], format: CAPIFormat) {
		this.elements = elements;
		this.format = format;
	}

	makeThumbnailsRound() {
		this.elements = makeThumbnailsRound(this.elements);
		return this;
	}

	addTitles() {
		this.elements = addTitles(this.elements, this.format);
		return this;
	}

	removeGlobalH2Styles() {
		this.elements = removeGlobalH2Styles(this.elements);
		return this;
	}

	addH3s() {
		this.elements = addH3s(this.elements);
		return this;
	}

	addItemLinks() {
		this.elements = addItemLinks(this.elements);
		return this;
	}

	starifyImages() {
		this.elements = starifyImages(this.elements);
		return this;
	}

	inlineStarRatings() {
		this.elements = inlineStarRatings(this.elements);
		return this;
	}
}

const enhance = (
	elements: CAPIElement[],
	format: CAPIFormat,
): CAPIElement[] => {
	return (
		new Enhancer(elements, format)
			// Add the data-ignore='global-h2-styling' attribute
			.removeGlobalH2Styles()
			// Turn false h3s into real ones
			.addH3s()
			// Add item links
			.addItemLinks()
			// Add numbered titles
			.addTitles()
			// Overlay stars onto images
			.starifyImages()
			// Turn ascii stars into components
			.inlineStarRatings()
			// Thumbnail images should be round
			.makeThumbnailsRound().elements
	);
};

export const enhanceNumberedLists = (
	blocks: Block[],
	format: CAPIFormat,
): Block[] => {
	const isNumberedList = format.display === 'NumberedListDisplay';

	if (!isNumberedList) {
		return blocks;
	}

	return blocks.map((block: Block) => {
		return {
			...block,
			elements: enhance(block.elements, format),
		};
	});
};

const removeTweetClass = (elements: CAPIElement[]): CAPIElement[] => {
	const enhanced: CAPIElement[] = [];
	elements.forEach((element) => {
		switch (element._type) {
			case 'model.dotcomrendering.pageElements.TweetBlockElement':
				// Parse the embed block element, find the iframe and add
				// the alt text as a title attribute if it exists
				// https://dequeuniversity.com/tips/provide-iframe-titles
				const withoutTweetClass = element.html.replace(
					'twitter-tweet',
					'nojs-tweet',
				);
				enhanced.push({
					...element,
					html: withoutTweetClass,
				});
				break;
			default:
				enhanced.push(element);
		}
	});
	return enhanced;
};

export const enhanceTweets = (blocks: Block[]): Block[] =>
	blocks.map((block: Block) => {
		return {
			...block,
			elements: removeTweetClass(block.elements),
		};
	});

import { ArticleDesign, ArticleSpecial } from '@guardian/libs';
import { decideFormat } from '../web/lib/decideFormat';
import { getDataLinkNameCard } from '../web/lib/getDataLinkName';

/**
 *
 * This function makes the decision about when we use the parent's (the
 * container's) format property to override the styling of a sublink, and
 * when we allow the sublink to express its own styling.
 *
 * Eg. If you had a sublink to a lifestyle article, when should it use pink for
 * the kicker and when would that not look right
 *
 * @returns the format property that we will use to style the sublink
 */
const decidePresentationFormat = ({
	linkFormat,
	containerFormat,
	containerPalette,
}: {
	linkFormat?: ArticleFormat;
	containerFormat: ArticleFormat;
	containerPalette?: DCRContainerPalette;
}): ArticleFormat => {
	// Some sublinks are to fronts and so don't have a `format` property
	if (!linkFormat) return containerFormat;
	// If the container has a special palette, use the container format
	if (containerPalette) return containerFormat;
	// These types of article styles have background styles that sublinks
	// need to respect so we use the container format here
	if (
		containerFormat.design === ArticleDesign.LiveBlog ||
		containerFormat.design === ArticleDesign.Gallery ||
		containerFormat.design === ArticleDesign.Audio ||
		containerFormat.design === ArticleDesign.Video ||
		containerFormat.theme === ArticleSpecial.SpecialReport ||
		containerFormat.design === ArticleDesign.Analysis
	)
		return containerFormat;
	// Otherwise, we can allow the sublink to express its own styling
	return linkFormat;
};

const enhanceSupportingContent = (
	supportingContent: FESupportingContent[],
	format: ArticleFormat,
	containerPalette?: DCRContainerPalette,
): DCRSupportingContent[] => {
	return supportingContent.map((subLink) => {
		// This is the actual DCR format for this sublink
		const linkFormat = subLink.format
			? decideFormat(subLink.format)
			: undefined;
		// This is the format used to decide how the sublink looks (we vary this based
		// on the container background colour)
		const presentationFormat = decidePresentationFormat({
			linkFormat,
			containerFormat: format,
			containerPalette,
		});
		return {
			format: presentationFormat,
			headline: subLink.header?.headline || '',
			url: subLink.properties.href || subLink.header?.url,
			kickerText:
				subLink.header?.kicker?.item?.properties.kickerText ||
				(linkFormat && linkFormat.design === ArticleDesign.LiveBlog
					? 'Live'
					: undefined),
		};
	});
};

const enhanceCards = (
	collections: FEFrontCard[],
	containerPalette?: DCRContainerPalette,
): DCRFrontCard[] =>
	collections
		.filter(
			(card: FEFrontCard): card is FEFrontCard & { format: CAPIFormat } =>
				!!card.format,
		)
		.map((faciaCard, index) => {
			const format = decideFormat(faciaCard.format);
			const group = `${faciaCard.card.group}${
				faciaCard.display.isBoosted ? '+' : ''
			}`;
			const dataLinkName = getDataLinkNameCard(format, group, index + 1);
			return {
				format,
				dataLinkName,
				url: faciaCard.header.url,
				headline: faciaCard.header.headline,
				trailText: faciaCard.card.trailText,
				webPublicationDate: faciaCard.card.webPublicationDateOption
					? new Date(
							faciaCard.card.webPublicationDateOption,
					  ).toISOString()
					: undefined,
				image: faciaCard.properties.maybeContent?.trail.trailPicture
					?.allImages[0].url,
				kickerText:
					faciaCard.header.kicker?.item?.properties.kickerText,
				supportingContent: faciaCard.supportingContent
					? enhanceSupportingContent(
							faciaCard.supportingContent,
							format,
							containerPalette,
					  )
					: undefined,
			};
		});

const decideContainerPalette = (
	metadata?: { type: FEContainerPalette }[],
): DCRContainerPalette | undefined => {
	switch (metadata?.[0]?.type) {
		case 'EventPalette':
			return 'EventPalette';
		case 'SombreAltPalette':
			return 'SombreAltPalette';
		case 'EventAltPalette':
			return 'EventAltPalette';
		case 'InvestigationPalette':
			return 'InvestigationPalette';
		case 'LongRunningAltPalette':
			return 'LongRunningAltPalette';
		case 'LongRunningPalette':
			return 'LongRunningPalette';
		case 'SombrePalette':
			return 'SombrePalette';
		case 'BreakingPalette':
			return 'BreakingPalette';
		case 'Canonical':
		case 'Dynamo':
		case 'Special':
		case 'DynamoLike':
		case 'Breaking':
		case 'Podcast':
		case 'Branded':
		default:
			return undefined;
	}
};

export const enhanceCollections = (
	collections: FECollectionType[],
): DCRCollectionType[] => {
	return collections.map((collection) => {
		const { id, displayName, collectionType } = collection;
		const containerPalette = decideContainerPalette(
			collection.config.metadata,
		);
		return {
			id,
			displayName,
			collectionType,
			containerPalette,
			curated: enhanceCards(collection.curated, containerPalette),
			backfill: enhanceCards(collection.backfill, containerPalette),
			treats: enhanceCards(collection.treats, containerPalette),
		};
	});
};

// All GA fields should  fall back to default values -

const filterTags = (
	tags: CAPIArticleType['tags'],
	tagType: 'Contributor' | 'Keyword' | 'Tone' | 'Series', // Letâ€™s make a decision to keep this tag getter small and well defined, we don't really want to use tags
): TagType['id'] | '' => {
	const tagArr = tags.filter((tag) => tag.type === tagType);
	const arrOfvalues =
		tagArr.length > 0 &&
		tagArr.reduce(
			(prev: Array<TagType['id']>, tag) => prev.concat(tag.id),
			[],
		);

	return (arrOfvalues && arrOfvalues.join(',')) || '';
};

// Annoyingly we ping GA with commissioningdesk as the title of the tag, not the id so handle that separately
const getCommissioningDesk = (
	tags: CAPIArticleType['tags'],
): TagType['title'] | '' => {
	const tag = tags.find((thisTag) =>
		thisTag.id.includes('tracking/commissioningdesk'),
	);
	return (tag && tag.title) || '';
};

const convertToLegacyPillar = (theme: CAPITheme): LegacyPillar => {
	switch (theme) {
		case 'NewsPillar':
			return 'news';
		case 'OpinionPillar':
			return 'opinion';
		case 'SportPillar':
			return 'sport';
		case 'CulturePillar':
			return 'culture';
		case 'LifestylePillar':
			return 'lifestyle';
		default:
			return 'news';
	}
};

const formatStringForGa = (string: string): string =>
	string.toLowerCase().split(' ').join('');

// we should not bring down the website if a trackable field is missing!
export const extract = (data: CAPIArticleType): GADataType => ({
	webTitle: data.webTitle,
	pillar: convertToLegacyPillar(data.format.theme),
	section: data.sectionName || '',
	contentType: formatStringForGa(data.contentType),
	commissioningDesks: formatStringForGa(getCommissioningDesk(data.tags)),
	contentId: data.pageId,
	authorIds: filterTags(data.tags, 'Contributor'),
	keywordIds: filterTags(data.tags, 'Keyword'),
	toneIds: filterTags(data.tags, 'Tone'),
	seriesId: filterTags(data.tags, 'Series'),
	isHosted: 'false', // TODO - This is missing from the Frontend Data model
	edition: data.editionId.toLowerCase() as Edition,
	beaconUrl: data.beaconURL,
});

import get from 'lodash-es/get';
import { findPillar } from './find-pillar';

type ObjectType = { [key: string]: any };

const getString = (
	obj: ObjectType,
	selector: string,
	fallbackValue?: string,
): string => {
	const found = get(obj, selector);
	if (typeof found === 'string') {
		return found;
	}
	if (fallbackValue !== undefined) {
		return fallbackValue;
	}

	throw new Error(
		`expected string at '${selector}', got '${found}', in '${JSON.stringify(
			obj,
		)}'`,
	);
};

const getArray = <T>(
	obj: ObjectType,
	selector: string,
	fallbackValue?: T[],
): T[] => {
	const found = get(obj, selector);

	if (Array.isArray(found)) {
		return found;
	}
	if (fallbackValue !== undefined) {
		return fallbackValue;
	}

	throw new Error(
		`expected array at '${selector}', got '${found}', in '${JSON.stringify(
			obj,
		)}'`,
	);
};

const getLink = (
	data: ObjectType,
	{ isPillar }: { isPillar: boolean },
): LinkType => {
	const title = getString(data, 'title');
	return {
		title,
		longTitle: getString(data, 'longTitle', '') || title,
		url: getString(data, 'url'),
		pillar: isPillar ? findPillar(getString(data, 'title')) : undefined,
