// TODO refactor now that we know data must be CAPINavType.
export const extractNAV = (data: any): NavType => {
	let pillars = getArray<any>(data, 'pillars');

	pillars = pillars.map((link) => getLink(link, { isPillar: true }));

	const subnav = get(data, 'subNavSections') as undefined | { parent?: any };

	return {
		pillars,
		otherLinks: {
			url: '', // unused
			title: 'More',
			longTitle: 'More',
			more: true,
			children: getArray<ObjectType>(data, 'otherLinks', []).map((l) =>
				getLink(l, { isPillar: false }),
			),
		},
		brandExtensions: getArray<ObjectType>(data, 'brandExtensions', []).map(
			(l) => getLink(l, { isPillar: false }),
		),
		currentNavLink: getString(data, 'currentNavLinkTitle', ''),
		subNavSections: subnav
			? {
					parent: subnav.parent
						? getLink(subnav.parent, { isPillar: false })
						: undefined,
					links: getArray<ObjectType>(subnav, 'links').map((l) =>
						getLink(l, { isPillar: false }),
					),
			  }
			: undefined,
		readerRevenueLinks: buildRRLinkModel(data),
	};
};

import { ArticlePillar, ArticleSpecial } from '@guardian/libs';

export const findPillar: (
	name: string,
	tags?: TagType[],
) => ArticleTheme | undefined = (name, tags?) => {
	// Flag paid content for Labs pillar (for styling purposes)
	const isPaidContent = (tag: TagType) =>
		tag.type === 'Tone' && tag.id === 'tone/advertisement-features';

	if (tags && tags.some(isPaidContent)) {
		return ArticleSpecial.Labs;
	}

	const pillar: string = name.toLowerCase();

	switch (pillar) {
		// The pillar name is "arts" in CAPI, but "culture" everywhere else
		case 'arts':
		case 'culture':
			return ArticlePillar.Culture;
		case 'opinion':
			return ArticlePillar.Opinion;
		case 'news':
			return ArticlePillar.News;
		case 'sport':
			return ArticlePillar.Sport;
		case 'lifestyle':
			return ArticlePillar.Lifestyle;
		default:
			return undefined;
	}
};

type HTMLTag = keyof HTMLElementTagNameMap;

type Prefix = `<${HTMLTag}${'' | ` ${string}`}>`;
type Suffix = `</${HTMLTag}>`;

const stripTags = (html: string, prefix: Prefix, suffix: Suffix) =>
	html.slice(prefix.length, html.length - suffix.length);

/**
 * This method unwraps a string of HTML from an enclosing tag.
 *
 * If the HTML passed in matches any of the fixes, in order, we will unwrap.
 * A fix matches if it starts with the prefix and ends with the suffix provided.
 *
 * - `willUnwrap === true`:
 * 	Return the html with the prefix and suffix stripped off,
 * 	as well as the matching element that we want to rewrap the HTML in.
 *
 * - `willUnwrap === false`:
 * 	Return the original HTML string and the `p` unwrappedElement.
 */
export const unwrapHtml = ({
	fixes,
	html,
}: {
	fixes: {
		prefix: Prefix;
		suffix: Suffix;
		unwrappedElement?: HTMLTag;
	}[];
	html: string;
}): {
	unwrappedHtml: string;
	willUnwrap: boolean;
	unwrappedElement: HTMLTag;
} => {
	const matchingFix = fixes.find(
		({ prefix, suffix }) =>
			html.startsWith(prefix) && html.endsWith(suffix),
	);

	const unwrappedHtml = matchingFix
		? // Chop off the start and end tags if we have matches to unwrap the html
		  stripTags(html, matchingFix.prefix, matchingFix.suffix)
		: html;

	/** `p` is the default unwrappedElement */
	const unwrappedElement = matchingFix?.unwrappedElement || 'p';

	return {
		unwrappedHtml,
		willUnwrap: !!matchingFix,
		unwrappedElement,
	};
};

export type { HTMLTag };

