import type { NextFunction, Request, Response } from 'express';
import { render as renderAMPArticle } from '../amp/server';
import {
	renderArticle,
	renderArticleJson,
	renderBlocks,
	renderFront,
	renderInteractive,
	renderKeyEvents,
} from '../web/server';

// see https://www.npmjs.com/package/webpack-hot-server-Middleware
// for more info
export const devServer = () => {
	return async (req: Request, res: Response, next: NextFunction) => {
		switch (req.path) {
			case '/Article':
				return renderArticle(req, res);
			case '/ArticleJson':
				return renderArticleJson(req, res);
			case '/AMPArticle':
				return renderAMPArticle(req, res);
			case '/Interactive':
				return renderInteractive(req, res);
			case '/AMPInteractive':
				return renderAMPArticle(req, res);
			case '/Blocks':
				return renderBlocks(req, res);
			case '/KeyEvents':
				return renderKeyEvents(req, res);
			case '/Front':
				return renderFront(req, res);
			default:
				next();
		}
	};
};

import type { PutMetricDataInput } from '@aws-sdk/client-cloudwatch';
import {
	CloudWatchClient,
	PutMetricDataCommand,
} from '@aws-sdk/client-cloudwatch';

interface Metric {
	send: () => void;
}

process.env.AWS_PROFILE = 'frontend';

const cloudWatchClient = new CloudWatchClient({ region: 'eu-west-1' });

// how frequently we send metrics to aws in ms
const METRICS_TIME_RESOLUTION = 60 * 1000;

const sendMetric = (m: any[]) => {
	if (m.length === 0) {
		return;
	}

	const params: PutMetricDataInput = {
		MetricData: m,
		Namespace: 'Application',
	};

	const command = new PutMetricDataCommand(params);

	cloudWatchClient.send(command, (err: any) => {
		if (err) {
			// `err` is typed as `any` in the library:
			// https://github.com/aws/aws-sdk-js-v3/blob/main/packages/smithy-client/src/client.ts#L41-L43
			// eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
			console.error(err, err.stack);
		}
	});
};

// handles sending matrics to AWS

const collectAndSendAWSMetrics = (...metrics: Metric[]) => {
	setInterval(() => {
		metrics.forEach((m) => m.send());
	}, METRICS_TIME_RESOLUTION);
};

// to record things like latency

const TimingMetric = (app: string, stage: string, metricName: string) => {
	const values: number[] = [];

	return {
		recordDuration: (n: number) => {
			values.push(n);
		},

		send: () => {
			sendMetric(
				values.map((v) => ({
					Dimensions: [
						{
							Name: 'ApplicationName',
							Value: app,
						},
						{
							Name: 'Stage',
							Value: stage,
						},
					],
					MetricName: metricName,
					Unit: 'Milliseconds',
					Value: v,
				})),
			);

			values.length = 0;
		},
	};
};

// to record memory or file sizes

const BytesMetric = (app: string, stage: string, metricName: string) => {
	const values: number[] = [];

	return {
		record: (n: number) => {
			values.push(n);
		},

		send: () => {
			sendMetric(
				values.map((v) => ({
					Dimensions: [
						{
							Name: 'ApplicationName',
							Value: app,
						},
						{
							Name: 'Stage',
							Value: stage,
						},
					],
					MetricName: metricName,
					Unit: 'Bytes',
					Value: v,
				})),
			);

			values.length = 0;
		},
	};
};

export { collectAndSendAWSMetrics, BytesMetric, TimingMetric };

import type {
	GetParametersByPathCommandOutput,
	GetParametersByPathRequest,
	Parameter,
} from '@aws-sdk/client-ssm';
import { GetParametersByPathCommand, SSMClient } from '@aws-sdk/client-ssm';

process.env.AWS_PROFILE = 'frontend';
const ssm = new SSMClient({ region: 'eu-west-1' });
const STACK = 'frontend';

interface ConfigMap {
	[key: string]: any;
}

interface GuardianConfiguration {
	getParameter: (key: string) => string;
	getAllParameters: () => any;
	size: () => number;
}

// gets params from AWS parameter store. This is a PAGED api, the token
// indicates the next set of results to get (or undefined for the first call)

const getParams = function getAWSParameterStoreParameters(
	stage: string,
	token: string | undefined = undefined,
): Promise<GetParametersByPathCommandOutput> {
	const params: GetParametersByPathRequest = {
		Path: `/${STACK}/${stage}/`,
		Recursive: true,
		WithDecryption: true,
		NextToken: token,
	};

	const command = new GetParametersByPathCommand(params);
	return ssm.send(command);
};

// a recursive function to retrieve all pages of guardian configuration

const getAllParams = function getGuardianConfigurationRecursiveStep(
	stage: string,
	params: Parameter[] = [],
	token: string | undefined = undefined,
): Promise<Parameter[]> {
	return getParams(stage, token).then((response) => {
		if (!response.NextToken) {
			return params;
		}
		return getAllParams(
			stage,
			params.concat(response.Parameters || []),
			response.NextToken === '' ? undefined : response.NextToken,
		);
	});
};

// returns a configuration object

const getGuardianConfiguration = (
	stage: string,
): Promise<GuardianConfiguration> => {
	return getAllParams(stage).then((params) => {
		const configuration: ConfigMap = params.reduce(
			(map, p) =>
				p.Name
					? {
							...map,
							[p.Name]: p.Value,
					  }
					: { ...map },
			{},
		);

		return {
			getParameter: (key: string) =>
				configuration[`/${STACK}/${stage}/${key}`],
			getAllParameters: () => configuration,
			size: () => params.length,
		};
	});
};

export { getGuardianConfiguration, GuardianConfiguration };

import os from 'os';
