import {
	cmp,
	getConsentFor,
	onConsentChange,
} from '@guardian/consent-management-platform';
import type { ConsentState } from '@guardian/consent-management-platform/dist/types';
import { getCookie, log } from '@guardian/libs';
import { getLocaleCode } from '../../lib/getCountryCode';
import { injectPrivacySettingsLink } from '../../lib/injectPrivacySettingsLink';
import { loadScript } from '../../lib/loadScript';
import type { OphanAction, OphanComponentType } from '../ophan/ophan';
import { submitComponentEvent } from '../ophan/ophan';
import { startup } from '../startup';

const trackPerformance = (
	timingCategory: string,
	timingVar: any,
	timingLabel: string,
): void => {
	const { ga } = window;

	if (!ga) {
		return;
	}

	if (window.performance && window.performance.now) {
		ga(
			'allEditorialPropertyTracker.send',
			'timing',
			timingCategory,
			timingVar,
			Math.round(window.performance.now()),
			timingLabel,
		);
	}
};

const init = async (): Promise<void> => {
	/**
	 * Keep this file in sync with CONSENT_TIMING in static/src/javascripts/boot.js in frontend
	 * mark: CONSENT_TIMING
	 */
	if (!window.guardian.config.switches.consentManagement) return; // CMP turned off!
	const browserId = getCookie({ name: 'bwid', shouldMemoize: true });
	const { pageViewId } = window.guardian.config.ophan;

	// Track when CMP will show with GA
	cmp.willShowPrivacyMessage()
		.then((willShow) => {
			trackPerformance(
				'consent',
				'acquired',
				willShow ? 'new' : 'existing',
			);
		})
		.catch((e) =>
			log('dotcom', `CMP willShowPrivacyMessage - error: ${e}`),
		);

	onConsentChange((consentState: ConsentState) => {
		if (!consentState) return;
		// Register changes in consent state with Ophan

		/*
			(Date: February 2022. Author: Pascal. This is a work in progress...)

			### General Context

			We are sending consent data from the client side to the datalake through Ophan, using Ophan Component Events.

			We are using Ophan Component Events because consent events do not have a first class type in the Ophan pipeline
			(this may change in the future)

			At the moment we are using MANAGE_CONSENT as the default action while we develop this code.

			### Encoding Conventions

			At the moment, the payload is essentially contained in the `labels` attribute (an array of strings) of the
			component object. We are going to encode consent data for more than one jusridiction. To make this unambiguous
			we are going to use a specific convention.

			Each element of the array represents a particular attribute. Note that the collection of attributes is different
			from one jurisdiction to another. To have an unambiguous reading we prefix each attribute value with a code
			that helps identify the attribute type.

			An element of the array is then always of the form "<code>:<value>"

			Codes:
				01: Specify the jurisdiction. Accepted values: "TCF.v2", "CCPA" and "AUS"
				02: TCF.v2 Consent UUID
				03: TCF.v2 Consent string
				04: CCPA (Consent) UUID
				05: CCPA Do not sell, boolean, with the serialization: true -> "true", false -> "false"
			    06: AUS (Consent) UUID # Note that the cookie is called "ccpaUUID"
				07: AUS consentStatus

			For TCF.v2, an exmaple of array is ["01:TCF.v2", "02:<consent UUID>"", "03:<consent string>""]
			For CCPA,   an exmaple of array is ["01:CCPA", "04:<consent UUID>", "05:true"]
			For AUS,    an exmaple of array is ["01:AUS", "06:<consent UUID>", "07:consentedAll"]

			Note: it is possible to deprecate CODES, but they cannot the reused.
		*/

		const decideConsentCarrierLabels = () => {
			if (consentState.tcfv2) {
				const consentUUID = getCookie({ name: 'consentUUID' }) || '';
				const consentString = consentState.tcfv2.tcString;
				return [
					'01:TCF.v2',
					`02:${consentUUID}`,
					`03:${consentString}`,
				];
			}
			if (consentState.ccpa) {
				const ccpaUUID = getCookie({ name: 'ccpaUUID' }) || '';
				const flag = consentState.ccpa.doNotSell ? 'true' : 'false';
				return ['01:CCPA', `04:${ccpaUUID}`, `05:${flag}`];
			}
			if (consentState.aus) {
				const ccpaUUID = getCookie({ name: 'ccpaUUID' }) || '';
				const consentStatus =
					getCookie({ name: 'consentStatus' }) || '';
				return ['01:AUS', `06:${ccpaUUID}`, `07:${consentStatus}`];
			}
			return [];
		};

		const componentType: OphanComponentType = 'CONSENT';

		const action: OphanAction = 'MANAGE_CONSENT';
		const event = {
			component: {
				componentType,
				products: [],
				labels: decideConsentCarrierLabels(),
			},
			action,
		};

		submitComponentEvent(event);

		// Check if we have consent for GA so that if the reader removes consent for tracking we
		// remove ga from the page
		const consentGivenForGA = getConsentFor(
			'google-analytics',
			consentState,
		);
		if (consentGivenForGA) {
			Promise.all([
				loadScript('https://www.google-analytics.com/analytics.js'),
				loadScript(window.guardian.gaPath),
			])
				.then(() => {
					log('dotcom', 'GA script loaded');
				})
				.catch((e) => console.error(`GA - error: ${e}`));
		} else {
			// Disable Google Analytics
			// Note. We should never be able to directly set things to the global window object
			// but in this case we want to stub things for testing, so it's ok to ignore this rule
			// @ts-expect-error
			window.ga = null;
		}
	});

	document.onreadystatechange = () => {
		if (
			document.readyState === 'interactive' ||
			document.readyState === 'complete'
		) {
			// Manually updates the footer DOM because it's not hydrated
			injectPrivacySettingsLink();
		}
	};

	cmp.init({
		pubData: {
			platform: 'next-gen',
			// If `undefined`, the resulting consent signal cannot be joined to a page view.
			browserId: browserId ?? undefined,
			pageViewId,
		},
		country: (await getLocaleCode()) || undefined,
	});
	log('dotcom', 'CMP initialised');

	return Promise.resolve();
};

startup('bootCmp', null, init);

import '../webpackPublicPath';
import dynamicImportPolyfill from 'dynamic-import-polyfill';
import { startup } from '../startup';

// Provides an import function to use for dynamic imports. **Only works on
// browsers that cut the mustard (support modules).**
const initialiseDynamicImport = () => {
	try {
		// eslint-disable-next-line @typescript-eslint/no-implied-eval
		window.guardianPolyfilledImport = new Function(
			'url',
			`return import(url)`,
		) as (url: string) => Promise<any>;
	} catch (e) {
		dynamicImportPolyfill.initialize({
			importFunctionName: 'guardianPolyfilledImport', // must be a direct property of the window
		});
	}
};

// Provides an import function to use for dynamic imports. **Designed for
// legacy browsers. Dynamic loads a ~4k bundle.**
const initialiseDynamicImportLegacy = () => {
	return import(/* webpackChunkName: "shimport" */ '@guardian/shimport').then(
		(shimport) => {
			shimport.initialise(); // note this adds a __shimport__ global
			window.guardianPolyfilledImport = shimport.load;
		},
	);
};

const init = (): Promise<void> => {
	window.guardianPolyfilledImport = (url: string) =>
		Promise.reject(
			new Error(`import not polyfilled; attempted import(${url})`),
		);

	if (window.guardian.mustardCut) {
		return Promise.resolve(initialiseDynamicImport());
	}

	return initialiseDynamicImportLegacy();
};

startup('dynamicImport', null, init);

import { getCookie } from '@guardian/libs';
import { getCLS, getFID, getLCP } from 'web-vitals';

interface TrackerConfig {
	name: string;
	id: string;
	sampleRate: number;
	siteSpeedSampleRate: number;
}

const tracker: TrackerConfig = {
	name: 'allEditorialPropertyTracker',
	id: 'UA-78705427-1',
	sampleRate: 100,
	siteSpeedSampleRate: 1, // TODO Should be set to 0.1 when rolling out to wider audience
};
const set = `${tracker.name}.set`;
const send = `${tracker.name}.send`;

const getQueryParam = (
	key: string,
	queryString: string,
): string | undefined => {
	const params = queryString.substring(1).split('&');
	const pairs = params.map((x) => x.split('='));

	return pairs
		.filter((xs) => xs.length === 2 && xs[0] === key)
		.map((xs) => xs[1])[0];
};

export const init = (): void => {
	const coldQueue = (...args: any[]) => {
		(window.ga.q = window.ga.q || []).push(args);
	};

	window.ga = window.ga || (coldQueue as UniversalAnalytics.ga);

	window.GoogleAnalyticsObject = 'ga';
	window.ga.l = +new Date();

	window.ga('create', tracker.id, 'auto', tracker.name, {
		sampleRate: tracker.sampleRate,
		siteSpeedSampleRate: tracker.siteSpeedSampleRate,
	});
};

type CoreVitalsArgsType = {
	name: string;
	delta: number;
	id: string;
};

// https://www.npmjs.com/package/web-vitals#using-analyticsjs
const sendCoreVital = ({ name, delta, id }: CoreVitalsArgsType): void => {
	const { ga } = window;

	if (!ga) {
		return;
	}

	ga(send, 'event', {
		eventCategory: 'Web Vitals',
		eventAction: name,
		// Google Analytics metrics must be integers, so the value is rounded.
		// For CLS the value is first multiplied by 1000 for greater precision
		// (note: increase the multiplier for greater precision if needed).
		eventValue: Math.round(name === 'CLS' ? delta * 1000 : delta),
		// The `id` value will be unique to the current page load. When sending
		// multiple values from the same page (e.g. for CLS), Google Analytics can
		// compute a total by grouping on this ID (note: requires `eventLabel` to
		// be a dimension in your report).
		eventLabel: id,
		// Use a non-interaction event to avoid affecting bounce rate.
		nonInteraction: true,
	});
};

export const sendPageView = (): void => {
	const { GAData } = window.guardian;
	const userCookie = getCookie({ name: 'GU_U', shouldMemoize: true });
	const { ga } = window;

	ga(set, 'forceSSL', true);
	ga(set, 'title', GAData.webTitle);
	ga(set, 'anonymizeIp', true);
	/** *************************************************************************************
	 * Custom dimensions common to all platforms across the whole Guardian estate          *
	 ************************************************************************************** */
	ga(set, 'dimension3', 'theguardian.com'); /* Platform */
	/** *************************************************************************************
	 * Custom dimensions for 'editorial' platforms (this site, the mobile apps, etc.)      *
	 * Some of these will be undefined for non-content pages, but that's fine.             *
	 ************************************************************************************** */
	ga(set, 'dimension4', GAData.section);
	ga(set, 'dimension5', GAData.contentType);
	ga(set, 'dimension6', GAData.commissioningDesks);
	ga(set, 'dimension7', GAData.contentId);
	ga(set, 'dimension8', GAData.authorIds);
	ga(set, 'dimension9', GAData.keywordIds);
	ga(set, 'dimension10', GAData.toneIds);
	ga(set, 'dimension11', GAData.seriesId);
	ga(set, 'dimension16', (userCookie && 'true') || 'false');
	ga(set, 'dimension21', getQueryParam('INTCMP', window.location.search)); // internal campaign code
	ga(set, 'dimension22', getQueryParam('CMP_BUNIT', window.location.search)); // campaign business unit
	ga(set, 'dimension23', getQueryParam('CMP_TU', window.location.search)); // campaign team
	ga(set, 'dimension26', GAData.isHosted);
	ga(set, 'dimension27', navigator.userAgent); // I bet you a pint
	ga(set, 'dimension29', window.location.href); // That both of these are already tracked.
	ga(set, 'dimension30', GAData.edition);

	// TODO: sponsor logos
	// ga(set, 'dimension31', GAData.sponsorLogos);

	// TODO: commercial branding
	// ga(set, 'dimension42', 'GAData.brandingType');

	ga(set, 'dimension43', 'dotcom-rendering');
	ga(set, 'dimension50', GAData.pillar);

	if (window.location.hash === '#fbLogin') {
		ga(set, 'referrer', null);
		window.location.hash = '';
	}

	try {
		const NG_STORAGE_KEY = 'gu.analytics.referrerVars';
		const referrerVarsData = window.sessionStorage.getItem(NG_STORAGE_KEY);
		const referrerVars: { [key: string]: any } = JSON.parse(
			referrerVarsData || '{}',
		);
		if (referrerVars.value) {
			const value = referrerVars.value as {
				time: number;
				tag: string;
				path: string;
			};
			const d = new Date().getTime();
			if (d - value.time < 60 * 1000) {
				// One minute
				ga(send, 'event', 'Click', 'Internal', value.tag, {
					nonInteraction: true, // to avoid affecting bounce rate
					dimension12: value.path,
				});
			}
			window.sessionStorage.removeItem(NG_STORAGE_KEY);
		}
	} catch (e) {
		// do nothing
	}

	ga(send, 'pageview');

	// //////////////////////
	// Core Vitals Reporting
	// Supported only in Chromium but npm module tested in all our supported browsers
	// https://www.npmjs.com/package/web-vitals#browser-support

	// Only send for roughly 5% of users
	// We want all or nothing on the corevitals so that they can be easily compared for a single pageview
	// so we do this here rather than in the sendCoreVital function
	const randomPerc = Math.random() * 100;
	const coreVitalsSampleRate = 5;

	if (coreVitalsSampleRate >= randomPerc) {
		// CLS and LCP are captured when the page lifecycle changes to 'hidden'.
		// https://developers.google.com/web/updates/2018/07/page-lifecycle-api#advice-hidden
		getCLS(sendCoreVital); // https://github.com/GoogleChrome/web-vitals#getcls (This is actually DCLS, as doesn't track CLS in iframes, see https://github.com/WICG/layout-instability#cumulative-scores)
		getLCP(sendCoreVital); // https://github.com/GoogleChrome/web-vitals#getlcp

		// FID is captured when a user interacts with the page
		getFID(sendCoreVital); // https://github.com/GoogleChrome/web-vitals#getfid
	}
};

export const trackNonClickInteraction = (actionName: string): void => {
	const { ga } = window;

	if (ga) {
		ga(send, 'event', 'Interaction', actionName, {
			/**
			 * set nonInteraction to avoid affecting bounce rate
			 * https://support.google.com/analytics/answer/1033068#NonInteractionEvents
			 */
			nonInteraction: true,
		});
	} else {
		const error = new Error("window.ga doesn't exist");
		window.guardian.modules.sentry.reportError(
