import { log } from '@guardian/libs';
import { h, hydrate, render } from 'preact';
import { initPerf } from '../initPerf';

/**
 * This function dynamically imports and then hydrates a specific component in
 * a specific part of the page
 *
 * If the content being hydrated is not present in the dom then React renders
 * it. This is how portals (non server side rendered content) work
 *
 * @param name The name of the component we want to hydrate
 * @param data The deserialised props we want to use for hydration
 * @param element The location on the DOM where the component to hydrate exists
 */
export const doHydration = (name: string, data: any, element: HTMLElement) => {
	// If this function has already been run for an element then don't try to
	// run it a second time
	const alreadyHydrated = element.dataset.guReady;
	if (alreadyHydrated) return;

	const { start, end } = initPerf(`hydrate-${name}`);
	start();
	import(
		/* webpackInclude: /\.importable\.tsx$/ */
		/* webpackChunkName: "[request]" */
		`../../components/${name}.importable`
	)
		.then((module) => {
			const clientOnly = element.getAttribute('clientOnly') === 'true';

			if (clientOnly) {
				element.querySelector('[data-name="placeholder"]')?.remove();
				log('dotcom', `Rendering island ${name}`);
				render(h(module[name], data), element);
			} else {
				log('dotcom', `Hydrating island ${name}`);
				hydrate(h(module[name], data), element);
			}

			element.setAttribute('data-gu-ready', 'true');
			end();
		})
		.catch((error) => {
			if (name && error.message.includes(name)) {
				console.error(
					`ðŸš¨ Error importing ${name}. Components must live in the root of /components and follow the [MyComponent].importable.tsx naming convention ðŸš¨`,
				);
			}
			throw error;
		});
};

/**
 * getName takes the given html element and returns its name attribute
 *
 * We expect the element to always be a `gu-*` custom element
 *
 * @param marker : The html element that we want to read the name attribute from;
 * @returns
 */
export const getName = (marker: HTMLElement): string | null => {
	const name = marker.getAttribute('name');
	if (!name) {
		console.error(
			`ðŸš¨ Error - no name attribute supplied. We need name to know what component to import ðŸš¨`,
		);
	}
	return name;
};

/**
 * getProps takes the given html element and returns its props attibute
 *
 * We expect the element to always be a `gu-*` custom element
 *
 * @param marker : The html element that we want to read the props attribute from;
 * @returns
 */
export const getProps = (marker: HTMLElement): { [key: string]: unknown } => {
	const serialised = marker.getAttribute('props');
	let props: { [key: string]: unknown };
	try {
		props = serialised && JSON.parse(serialised);
	} catch (error: unknown) {
		console.error(
			`ðŸš¨ Error parsing props. Is this data serialisable? ${serialised} ðŸš¨`,
		);
		throw error;
	}
	return props;
};

import { doHydration } from './doHydration';
import { getName } from './getName';
import { getProps } from './getProps';
import { whenIdle } from './whenIdle';
import { whenVisible } from './whenVisible';

export const initHydration = (elements: NodeListOf<Element>) => {
	/**
	 * Partial Hydration / React Islands
	 *
	 * The code here looks for parts of the dom that have been marked using the `gu-island`
	 * marker, hydrating/rendering each one using the following properties:
	 *
	 * deferUntil - Used to optionally defer execution
	 * name - The name of the component. Used to dynamically import the code
	 * props - The data for the component that has been serialised in the dom
	 * element - The `gu-island` custom element which is wrapping the content
	 */
	elements.forEach((element) => {
		if (element instanceof HTMLElement) {
			const name = getName(element);
			const props = getProps(element);

			if (!name) return;

			const deferUntil = element.getAttribute('deferuntil');
			switch (deferUntil) {
				case 'idle': {
					whenIdle(() => {
						doHydration(name, props, element);
					});
					break;
				}
				case 'visible': {
					whenVisible(element, () => {
						doHydration(name, props, element);
					});
					break;
				}
				default: {
					doHydration(name, props, element);
				}
			}
		}
	});
};

/**
 * whenIdle exectures the given callback when the browser is 'idle'
 *
 * @param callback Fired when requestIdleCallback runs. If requestIdleCallback is not available after 300ms
 */
export const whenIdle = (callback: () => void) => {
	if ('requestIdleCallback' in window) {
		window.requestIdleCallback(callback, { timeout: 500 });
	} else {
		setTimeout(callback, 300);
	}
};

/**
 * Use this function to delay execution of something until an element is visible
 * in the viewport
 *
 * @param element : The html element that we want to observe;
 * @param callback : This is fired when the element is visible in the viewport
 */
export const whenVisible = (element: HTMLElement, callback: () => void) => {
	if ('IntersectionObserver' in window) {
		const io = new IntersectionObserver(([entry]) => {
			if (!entry.isIntersecting) return;
			// Disconnect this IntersectionObserver once seen
			io.disconnect();
			callback();
		});

		io.observe(element);
	} else {
		// IntersectionObserver is not supported so failover to calling back at the end of the call stack
		setTimeout(() => callback(), 0);
	}
};

import '../webpackPublicPath';
import { isObject } from '@guardian/libs';
import { startup } from '../startup';

// No trailing slash!
const allowedOrigins = ['https://www.theguardian.com'];
const init = (): Promise<void> => {
	const allIframes: HTMLIFrameElement[] = [].slice.call(
		document.querySelectorAll('.email-sub__iframe'),
	);

	// Tell the iframes to resize once this script is loaded
	// Otherwise, earlier resize events might be missed
	// So we don't have to load this script as a priority on each load
	allIframes.forEach((iframe) => {
		if (iframe && iframe.contentWindow)
			iframe.contentWindow.postMessage(
				'resize',
				'https://www.theguardian.com',
			);
	});

	window.addEventListener('message', (event) => {
		if (!allowedOrigins.includes(event.origin)) return;

		const iframes: HTMLIFrameElement[] = allIframes.filter((i) => {
			try {
				if (i.contentWindow !== null && event.source !== null) {
					return i.contentWindow === event.source;
				}
			} catch (e) {
				return false;
			}
			return false;
		});
		if (iframes.length !== 0) {
			try {
				const message: unknown = JSON.parse(event.data);
				if (!isObject(message) || typeof message.type !== 'string')
					return;

				switch (message.type) {
					case 'set-height':
						iframes.forEach((iframe) => {
							if (typeof message.value === 'number') {
								iframe.height = `${message.value}`;
							} else if (typeof message.value === 'string') {
								const value = parseInt(message.value, 10);
								if (Number.isInteger(value)) {
									iframe.height = `${value}`;
								}
							}
						});
						break;
					default:
				}
				// eslint-disable-next-line no-empty
			} catch (e) {}
		}
	});

	return Promise.resolve();
};

