const now = new Date().valueOf();
const nowIso = new Date(now).toISOString();

const oneMinute = 60 * 1000 * 1;
const fifteenSeconds = 1000 * 15;
const twentySeconds = 20 * 1000;
const thirtySeconds = 30 * 1000;

describe('updateTimeElements', () => {
	beforeEach(() => {
		MockDate.reset();
	});

	it('only updates time elements that have the data-relativeformat and datetime attributes', async () => {
		const html = `
			<div>
				<time data-testid="1" datetime="${nowIso}">I am missing data-relativeformat</time>
				<div data-testid="2" datetime="${nowIso}" data-relativeformat="med">I am a div, not time</div>
				<time data-testid="3" data-relativeformat="med">I am missing datatime</time>
				<time data-testid="4" datetime="${nowIso}" data-relativeformat="med">Original value</time>
			</div>`;
		setup(html);
		MockDate.set(now + fifteenSeconds);
		updateTimeElements();
		expect(screen.getByTestId('1')).toHaveTextContent(
			'I am missing data-relativeformat',
		);
		expect(screen.getByTestId('2')).toHaveTextContent(
			'I am a div, not time',
		);
		expect(screen.getByTestId('3')).toHaveTextContent(
			'I am missing datatime',
		);
		expect(screen.getByTestId('4')).toHaveTextContent('15s');
	});

	it('fails gracefully if format is invalid', async () => {
		const html = `<time data-testid="1" datetime="${nowIso}" data-relativeformat="INVALID">Original value</time>`;
		setup(html);
		MockDate.set(now + fifteenSeconds);
		updateTimeElements();
		expect(screen.getByTestId('1')).toHaveTextContent('Original value');
	});

	it('updates the text shown in time elements as time progresses', async () => {
		const html = `<time data-testid="1" datetime="${nowIso}" data-relativeformat="med">now</time>`;
		setup(html);
		MockDate.set(now);
		updateTimeElements();
		expect(screen.getByTestId('1')).toHaveTextContent('now');
		MockDate.set(now + fifteenSeconds);
		updateTimeElements();
		expect(screen.getByTestId('1')).toHaveTextContent('15s');
		MockDate.set(now + thirtySeconds);
		updateTimeElements();
		expect(screen.getByTestId('1')).toHaveTextContent('30s');
		MockDate.set(now + oneMinute);
		updateTimeElements();
		expect(screen.getByTestId('1')).toHaveTextContent('1m');
	});

	it('respects if long format is passed', async () => {
		const html = `<time data-testid="1" datetime="${nowIso}" data-relativeformat="long">1 second ago</time>`;
		setup(html);
		MockDate.set(now + fifteenSeconds);
		updateTimeElements();
		expect(screen.getByTestId('1')).toHaveTextContent('15 seconds ago');
	});

	it('respects if short format is passed', async () => {
		const html = `<time data-testid="1" datetime="${nowIso}" data-relativeformat="short">now</time>`;
		setup(html);
		MockDate.set(now + twentySeconds);
		updateTimeElements();
		expect(screen.getByTestId('1')).toHaveTextContent('20s');
	});
});

import { timeAgo } from '@guardian/libs';

export const updateTimeElement = (element: Element): void => {
	if (element instanceof HTMLElement) {
		// Get required values
		const { relativeformat: relativeFormat } = element.dataset;
		const absoluteTime = element.getAttribute('datetime');
		if (!absoluteTime || !relativeFormat) return;

		let newTime;
		switch (relativeFormat) {
			case 'short':
			case 'med':
				newTime = timeAgo(new Date(absoluteTime).getTime(), {
					verbose: false,
				});
				break;
			case 'long':
				newTime = timeAgo(new Date(absoluteTime).getTime(), {
					verbose: true,
				});
				break;
		}
		const oldTime = element.innerText;
		if (newTime && newTime !== oldTime) element.innerHTML = newTime;
	}
};

export const updateTimeElements = (): void => {
	document
		.querySelectorAll('time[data-relativeformat]')
		.forEach(updateTimeElement);
};

import '../webpackPublicPath';
import { isAdBlockInUse } from '@guardian/commercial-core';
import { startup } from '../startup';

const init = async (): Promise<void> => {
	// Sentry lets you configure sampleRate to reduce the volume of events sent
	// but this filter only happens _after_ the library is loaded. The Guardian
	// measures page views in the billions so we only want to log 1% of errors that
	// happen but if we used sampleRate to do this we'd be needlessly downloading
	// Sentry 99% of the time. So instead we just do some basic math here
	// and use that to prevent the Sentry script from ever loading.
	const randomCentile = Math.floor(Math.random() * 100) + 1; // A number between 1 - 100
	if (randomCentile <= 99) {
		// 99% of the time we don't want to remotely log errors with Sentry and so
		// we just console them out
		window.guardian.modules.sentry.reportError = (error) => {
			console.error(error);
		};
		return; // Don't initialise Sentry
	}
	// The other 1% of the time (randomCentile === 100) we continue
	try {
		// Downloading and initiliasing Sentry is asynchronous so we need a way
		// to ensure injection only happens once and to capture any other errors that
		// might happen while this script is loading
		let injected = false;
		const queue: Error[] = [];

		// Function that gets called when an error happens before Sentry is ready
		const injectSentry = async (error?: Error) => {
			// Remember this error for later
			if (error) queue.push(error);

			// Only inject once
			if (injected) {
				return;
			}
			injected = true;

			// Make this call blocking. We are queing errors while we wait for this code to run
			// so we won't miss any and by waiting here we ensure we will never make calls we
			// expect to be blocked
			const adBlockInUse: boolean = await isAdBlockInUse();
			if (adBlockInUse) {
				// Ad Blockers prevent calls to Sentry from working so don't try to load the lib
				return;
			}

			// Load sentry.ts
			const { reportError } = await import(
				/* webpackChunkName: "sentry" */ './sentry'
			);

			// Sentry takes over control of the window.onerror and
			// window.onunhandledrejection listeners but we need to
			// manually redefine our own custom error reporting function
			window.guardian.modules.sentry.reportError = reportError;

			// Now that we have the real reportError function available,
			// send any queued errors
			while (queue.length) {
				const queuedError = queue.shift();
				if (queuedError) reportError(queuedError);
			}
		};

		// This is how we lazy load Sentry. We setup custom functions and
		// listeners to inject Sentry when an error happens
		window.onerror = (message, url, line, column, error) =>
			injectSentry(error);
		window.onunhandledrejection = (event: undefined | { reason?: any }) =>
			event && injectSentry(event.reason);
		window.guardian.modules.sentry.reportError = (error) => {
			injectSentry(error).catch((e) =>
				console.error(`injectSentry - error: ${e}`),
			);
		};
	} catch {
		// We failed to setup Sentry :(
	}
};

startup('sentryLoader', null, init);

