export interface Reporter {
	report: (err: Error, tags: { [key: string]: string }) => void;
}

const measure = (name: string, task: () => Promise<void>): void => {
	const { start, end } = initPerf(name);

	start();

	task()
		// You could use 'finally' here to prevent this duplication but finally isn't supported
		// in Chrome 59 which is used by Cypress in headless mode so if you do it will
		// break the Cypress tests on CI
		// See: https://github.com/cypress-io/cypress/issues/2651#issuecomment-432698837
		.then(() => {
			end();
		})
		.catch(() => {
			end();
		});
};

export const startup = <A>(
	name: string,
	helpers: A,
	task: (helpers: A) => Promise<void>,
): void => {
	const measureMe = () => {
		measure(name, task.bind(null, helpers));
	};
	if (window.guardian.mustardCut || window.guardian.polyfilled) {
		measureMe();
	} else {
		window.guardian.queue.push(measureMe);
	}
};

type HeightEventType = { source: { name: string } };

export const updateIframeHeight = (queryString: string): Promise<void> => {
	const iframes: HTMLIFrameElement[] = [].slice.call(
		document.querySelectorAll(queryString),
	);

	window.addEventListener('message', (event) => {
		const iframe: HTMLIFrameElement | undefined = iframes.find((i) => {
			try {
				return i.name === (event as HeightEventType).source.name;
			} catch (e) {
				return false;
			}
		});
		if (iframe) {
			try {
				const message = JSON.parse(event.data) as {
					type: string;
					value: string;
				};
				switch (message.type) {
					case 'set-height':
						iframe.height = message.value;
						break;
					default:
				}
				// eslint-disable-next-line no-empty
			} catch (e) {}
		}
	});

	iframes.forEach((iframe) => {
		const src = (iframe.getAttribute('srcdoc') || '')
			.replace(/<gu-script>/g, '<script>')

			.replace(/<\/gu-script>/g, '<' + '/script>');
		iframe.setAttribute('srcdoc', src);
	});
	return Promise.resolve();
};

