									`,
								]}
							>
								<span
									css={[
										format.theme === ArticleSpecial.Labs
											? jumboLabsFont
											: jumboFont,
										maxWidth,
										invertedStyles(palette),
										immersiveStyles,
										displayBlock,
									]}
								>
									{curly(headlineString)}
								</span>
							</h1>
						</WithAgeWarning>
					);
			}
		}
		case ArticleDisplay.NumberedList:
			return (
				<div
					css={decideBottomPadding({
						format,
						hasStarRating,
						hasAvatar,
					})}
				>
					<WithAgeWarning
						tags={tags}
						webPublicationDateDeprecated={
							webPublicationDateDeprecated
						}
						format={format}
					>
						<h1
							css={[
								boldFont,
								topPadding,
								css`
									color: ${palette.text.headline};
								`,
							]}
						>
							{curly(headlineString)}
						</h1>
					</WithAgeWarning>
				</div>
			);
		case ArticleDisplay.Showcase:
		case ArticleDisplay.Standard:
		default: {
			switch (format.design) {
				case ArticleDesign.Review:
				case ArticleDesign.Recipe:
				case ArticleDesign.Feature:
					return (
						<div
							css={decideBottomPadding({
								format,
								hasStarRating,
								hasAvatar,
							})}
						>
							<WithAgeWarning
								tags={tags}
								webPublicationDateDeprecated={
									webPublicationDateDeprecated
								}
								format={format}
							>
								<h1
									css={[
										boldFont,
										topPadding,
										css`
											color: ${palette.text.headline};
										`,
									]}
								>
									{curly(headlineString)}
								</h1>
							</WithAgeWarning>
						</div>
					);
				case ArticleDesign.Comment:
				case ArticleDesign.Editorial:
					return (
						<div
							css={decideBottomPadding({
								format,
								hasStarRating,
								hasAvatar,
							})}
						>
							<WithAgeWarning
								tags={tags}
								webPublicationDateDeprecated={
									webPublicationDateDeprecated
								}
								format={format}
							>
								<h1
									css={[
										lightFont,
										topPadding,
										css`
											color: ${palette.text.headline};
										`,
									]}
								>
									{curly(headlineString)}
								</h1>
							</WithAgeWarning>
							{byline && (
								<HeadlineByline
									format={format}
									byline={byline}
									tags={tags}
								/>
							)}
						</div>
					);

				case ArticleDesign.Letter:
					return (
						<div
							css={decideBottomPadding({
								format,
								hasStarRating,
								hasAvatar,
							})}
						>
							<WithAgeWarning
								tags={tags}
								webPublicationDateDeprecated={
									webPublicationDateDeprecated
								}
								format={format}
							>
								<h1
									css={[
										lightFont,
										topPadding,
										css`
											color: ${palette.text.headline};
										`,
									]}
								>
									{curly(headlineString)}
								</h1>
