	imagePosition = 'top',
	imagePositionOnMobile = 'left',
	imageSize,
	trailText,
	avatar,
	showClock,
	mediaType,
	mediaDuration,
	kickerText,
	showPulsingDot,
	showSlash,
	commentCount,
	starRating,
	minWidthInPixels,
	dataLinkName,
	branding,
	supportingContent,
	containerPalette,
	showAge = false,
}: Props) => {
	const showCommentCount = commentCount || commentCount === 0;
	const palette = decidePalette(format, containerPalette);
	const { long: longCount, short: shortCount } = formatCount(commentCount);

	const hasSublinks = supportingContent && supportingContent.length > 0;
	const noOfSublinks = (supportingContent && supportingContent.length) || 0;

	const isOpinion =
		format.design === ArticleDesign.Comment ||
		format.design === ArticleDesign.Editorial ||
		format.design === ArticleDesign.Letter;

	const renderAge = decideIfAgeShouldShow({
		containerPalette,
		format,
		showAge,
	});

	return (
		<CardWrapper format={format} containerPalette={containerPalette}>
			<CardLink
				linkTo={linkTo}
				dataLinkName={dataLinkName}
				format={format}
				containerPalette={containerPalette}
			/>
			<CardLayout
				imagePosition={imagePosition}
				imagePositionOnMobile={imagePositionOnMobile}
				minWidthInPixels={minWidthInPixels}
			>
				{imageUrl && (
					<ImageWrapper
						imageSize={imageSize}
						imagePosition={imagePosition}
						imagePositionOnMobile={imagePositionOnMobile}
					>
						<img src={imageUrl} alt="" role="presentation" />
						{starRating !== undefined ? (
							<StarRatingComponent rating={starRating} />
						) : null}
					</ImageWrapper>
				)}
				<ContentWrapper
					imageSize={imageSize}
					imagePosition={imagePosition}
				>
					<Flex>
						<HeadlineWrapper>
							<CardHeadline
								headlineText={headlineText}
								format={format}
								containerPalette={containerPalette}
								size={headlineSize}
								showQuotes={showQuotes}
								kickerText={
									format.design === ArticleDesign.LiveBlog
										? 'Live'
										: kickerText
								}
								showPulsingDot={
									format.design === ArticleDesign.LiveBlog ||
									showPulsingDot
								}
								showSlash={
									format.design === ArticleDesign.LiveBlog ||
									showSlash
								}
								byline={byline}
								showByline={showByline}
							/>
						</HeadlineWrapper>
						{avatar && (
							<Hide when="above" breakpoint="tablet">
								<AvatarContainer>
									<Avatar
										imageSrc={avatar.src}
										imageAlt={avatar.alt}
										containerPalette={containerPalette}
										format={format}
									/>
								</AvatarContainer>
							</Hide>
						)}
					</Flex>
					<div>
						{trailText && (
							<TrailTextWrapper
								containerPalette={containerPalette}
								format={format}
							>
								<div
									dangerouslySetInnerHTML={{
										__html: trailText,
									}}
								/>
							</TrailTextWrapper>
						)}
						{avatar && (
							<Hide when="below" breakpoint="tablet">
								<AvatarContainer>
									<Avatar
										imageSrc={avatar.src}
										imageAlt={avatar.alt}
										containerPalette={containerPalette}
										format={format}
									/>
								</AvatarContainer>
							</Hide>
						)}
						<CardFooter
							format={format}
							age={
								renderAge && webPublicationDate ? (
									<CardAge
										format={format}
										containerPalette={containerPalette}
										webPublicationDate={webPublicationDate}
										showClock={showClock}
									/>
								) : undefined
							}
							mediaMeta={
								(format.design === ArticleDesign.Gallery ||
									format.design === ArticleDesign.Audio ||
									format.design === ArticleDesign.Video) &&
								mediaType ? (
									<MediaMeta
										containerPalette={containerPalette}
										format={format}
										mediaType={mediaType}
										mediaDuration={mediaDuration}
									/>
								) : undefined
							}
							commentCount={
								showCommentCount && longCount && shortCount ? (
									<CardCommentCount
										containerPalette={containerPalette}
										format={format}
										long={longCount}
										short={shortCount}
									/>
								) : undefined
							}
							cardBranding={
								branding ? (
									<CardBranding
										branding={branding}
										format={format}
									/>
								) : undefined
							}
						/>
						{hasSublinks && noOfSublinks <= 2 ? (
							<SupportingContent
								supportingContent={supportingContent}
								alignment="vertical"
							/>
						) : (
							<></>
						)}
					</div>
				</ContentWrapper>
			</CardLayout>
			{hasSublinks && noOfSublinks > 2 ? (
				<SupportingContent
					supportingContent={supportingContent}
					alignment={
						imagePosition === 'top' || imagePosition === 'bottom'
							? 'vertical'
							: 'horizontal'
					}
				/>
			) : (
				<></>
			)}
			{isOpinion && (
				<StraightLines color={palette.border.lines} count={4} />
			)}
		</CardWrapper>
	);
};

