import { unescapeData } from '../../lib/escapeData';

type Props = {
	body: string;
	avatarURL: string;
	profileURL: string;
	profileName: string;
	dateTime: string;
	permalink: string;
};

const commentWrapperStyles = css`
	${textSans.medium()};
	border-left: 1px ${neutral[86]} solid;
	border-top: 1px ${neutral[86]} solid;
	padding-top: ${space[1]}px;
	padding-left: ${space[2]}px;
	margin-top: ${space[3]}px;
`;

const bodyContentStyles = css`
	p {
		word-break: break-word;
		margin-bottom: ${space[2]}px;
	}

	blockquote {
		font-style: italic;
		color: ${textColor.supporting};
		border-left: ${space[1]}px ${neutral[86]} solid;
		margin-top: ${space[1]}px;
		margin-right: 0;
		margin-bottom: ${space[4]}px;
		margin-left: 0;
		padding-left: ${space[3]}px;
	}
`;

const profileWrapperStyles = css`
	display: flex;
	margin-top: ${space[4]}px;
	margin-bottom: ${space[12]}px;
	/* in order to overwrite the a tag style we had to add a second class to give higher order specificity */
	& > a.avatarLink {
		border-bottom: none;
	}

	& > a.avatarLink:hover {
		border-bottom: none;
	}
`;

const usernameWrapperStyles = css`
	display: flex;
	flex-direction: column;
	height: ${space[12]}px;
	/* in order to overwrite the a tag style we had to add a second class to give higher order specificity */
	& > a.permalink {
		color: ${neutral[46]};
		border-bottom: none;
	}
	& > a.permalink:hover {
		color: ${neutral[46]};
		border-bottom: 1px solid ${neutral[46]};
	}
`;

const avatarStyles = css`
	width: ${space[12]}px;
	height: ${space[12]}px;
	margin-right: ${space[2]}px;
`;

const imageStyles = css`
	border-radius: 50%;
	width: 100%;
	height: 100%;
`;

export const CommentBlockComponent = ({
	body,
	avatarURL,
	profileURL,
	profileName,
	dateTime,
	permalink,
}: Props) => {
	return (
		<div css={commentWrapperStyles}>
			<div
				css={bodyContentStyles}
				dangerouslySetInnerHTML={{ __html: unescapeData(body) }}
			/>
			<div css={profileWrapperStyles}>
				<a
					css={avatarStyles}
					// TODO: remove avatarLink
					className="avatarLink"
					href={profileURL}
					aria-hidden="true"
				>
					<img css={imageStyles} src={avatarURL} alt="avatar" />
				</a>
				<div css={usernameWrapperStyles}>
					<a
						css={css`
							${textSans.medium({ fontWeight: 'bold' })};
							width: max-content;
						`}
						href={profileURL}
					>
						{profileName}
					</a>
					{/* TODO: remove permalink */}
					<a href={permalink} className="permalink">
						<time
							css={css`
								${textSans.small()};
							`}
						>
							{dateTime}
						</time>
					</a>
				</div>
			</div>
		</div>
	);
};

import { css } from '@emotion/react';
import { joinUrl } from '@guardian/libs';
import { between, textSans, until } from '@guardian/source-foundations';
import CommentIcon from '../../static/icons/comment.svg';
import { decidePalette } from '../lib/decidePalette';
import { formatCount } from '../lib/formatCount';
import { useDiscussion } from '../lib/useDiscussion';

type Props = {
	format: ArticleFormat;
	discussionApiUrl: string;
	shortUrlId: string;
};

const containerStyles = (palette: Palette) => css`
	display: flex;
	align-self: flex-end;
	flex-direction: column;
	${textSans.medium()};
	font-weight: bold;
	color: ${palette.fill.commentCount};
	padding-top: 5px;

	${until.desktop} {
		color: ${palette.fill.commentCountUntilDesktop};
	}
`;

const iconContainerStyles = css`
	height: 15px;
	margin: 0;
	text-align: right;
	margin-bottom: -2px;
	svg {
		height: 18px;
		width: 18px;
	}
`;

const iconStyles = (palette: Palette) => css`
	fill: ${palette.fill.commentCount};
	${until.desktop} {
		fill: ${palette.fill.commentCountUntilDesktop};
	}
`;

const longStyles = css`
	display: block;

	${between.leftCol.and.wide} {
		display: none;
	}
`;

const shortStyles = css`
	display: none;

	${between.leftCol.and.wide} {
		display: block;
	}
`;

const linkStyles = css`
	color: inherit;
	text-decoration: none;
	:hover {
		text-decoration: underline;
	}
	:visited {
		color: inherit;
	}
`;

export const CommentCount = ({
	format,
	discussionApiUrl,
	shortUrlId,
}: Props) => {
	const { commentCount } = useDiscussion(
		joinUrl(discussionApiUrl, 'discussion', shortUrlId),
	);

	if (!commentCount && commentCount !== 0) return null;

	const { short, long } = formatCount(commentCount);
	const palette = decidePalette(format);

	return (
		<data
			css={containerStyles(palette)}
			data-cy="comment-counts"
			value={`${long} comments on this article`}
		>
			<a
				href="#comments"
				css={linkStyles}
				aria-label={`${short} Comments`}
			>
				<div css={iconContainerStyles}>
					<CommentIcon css={iconStyles(palette)} />
				</div>
				<div
					data-testid="long-comment-count"
					css={longStyles}
					aria-hidden="true"
				>
					{long}
				</div>
				<div
					data-testid="short-comment-count"
					css={shortStyles}
					aria-hidden="true"
				>
					{short}
				</div>
			</a>
		</data>
	);
};

import type { ABTest } from '@guardian/ab-core';
import {
	initCommercialMetrics,
	bypassCommercialMetricsSampling as switchOffSampling,
} from '@guardian/commercial-core';
import { getCookie } from '@guardian/libs';
import { tests } from '../experiments/ab-tests';
import { prebidPriceGranularity } from '../experiments/tests/prebid-price-granularity';
import { useAB } from '../lib/useAB';
import { useAdBlockInUse } from '../lib/useAdBlockInUse';
import { useOnce } from '../lib/useOnce';

type Props = {
	enabled: boolean;
};

export const CommercialMetrics = ({ enabled }: Props) => {
	const ABTestAPI = useAB();
	const adBlockerInUse = useAdBlockInUse();

	useOnce(() => {
		const browserId = getCookie({ name: 'bwid', shouldMemoize: true });
		const pageViewId = window.guardian.config.ophan.pageViewId;

		// Only send metrics if the switch is enabled
		if (!enabled) return;

		// For these tests switch off sampling and collect metrics for 100% of views
		const clientSideTestsToForceMetrics: ABTest[] = [
			/* keep array multi-line */
			prebidPriceGranularity,
		];

		const userInClientSideTestToForceMetrics = ABTestAPI?.allRunnableTests(
			tests,
		).some((test) =>
			clientSideTestsToForceMetrics.map((t) => t.id).includes(test.id),
		);

		const serverSideTestsToForceMetrics: Array<keyof ServerSideTests> = [
			/* keep array multi-line */
		];

		const userInServerSideTestToForceMetrics =
			serverSideTestsToForceMetrics.some((test) =>
				Object.keys(window.guardian.config.tests).includes(test),
			);

		const isDev =
			window.guardian.config.page.isDev ||
			window.location.hostname.includes(
				process.env.HOSTNAME || 'localhost',
			);

		initCommercialMetrics({
			pageViewId,
			browserId: browserId || undefined,
			isDev,
			adBlockerInUse,
		})
			.then(() => {
				if (
					userInClientSideTestToForceMetrics ||
					userInServerSideTestToForceMetrics
				) {
					// TODO: rename this in commercial-core and update here

					void switchOffSampling();
				}
			})
			.catch((e) =>
				console.error(`Error initialising commercial metrics: ${e}`),
			);
	}, [ABTestAPI, adBlockerInUse, enabled]);

	// We donâ€™t render anything
	return null;
};

