import { css } from '@emotion/react';
import type { ArticleFormat } from '@guardian/libs';
import { ArticleDesign } from '@guardian/libs';
import { from, space } from '@guardian/source-foundations';
import { decideContainerOverrides } from '../lib/decideContainerOverrides';
import { ContainerTitle } from './ContainerTitle';
import { ElementContainer } from './ElementContainer';
import { Flex } from './Flex';
import { Hide } from './Hide';
import { LeftColumn } from './LeftColumn';

type Props = {
	title?: string;
	fontColour?: string;
	description?: string;
	url?: string;
	sectionId?: string;
	sideBorders?: boolean;
	centralBorder?: 'partial' | 'full';
	showTopBorder?: boolean;
	padSides?: boolean;
	padContent?: boolean;
	verticalMargins?: boolean;
	backgroundColour?: string;
	borderColour?: string;
	leftContent?: React.ReactNode;
	children?: React.ReactNode;
	stretchRight?: boolean;
	leftColSize?: LeftColSize;
	format?: ArticleFormat;
	ophanComponentName?: string;
	ophanComponentLink?: string;
	containerPalette?: DCRContainerPalette;
	innerBackgroundColour?: string;
};

const containerStyles = css`
	display: flex;
	flex-grow: 1;
	flex-direction: column;
	width: 100%;
`;

const margins = css`
	margin-top: ${space[2]}px;
	/*
   Keep spacing at the bottom of the container consistent at 36px, regardless of
   breakpoint, based on chat with Harry Fisher
*/
	margin-bottom: ${space[9]}px;
`;

const rightMargin = css`
	${from.wide} {
		margin-right: 68px;
	}
`;

const padding = (format?: ArticleFormat) => {
	switch (format?.design) {
		case ArticleDesign.LiveBlog:
		case ArticleDesign.DeadBlog:
			return css`
				padding: 0;

				${from.desktop} {
					padding: 0 10px;
				}
			`;
		default:
			return css`
				padding: 0 10px;
			`;
	}
};

const Container = ({
	children,
	padded,
	verticalMargins,
	stretchRight,
	format,
}: {
	children: React.ReactNode;
	padded: boolean;
	verticalMargins: boolean;
	stretchRight: boolean;
	format?: ArticleFormat;
}) => (
	<div
		css={[
			containerStyles,
			padded && padding(format),
			verticalMargins && margins,
			!stretchRight && rightMargin,
		]}
	>
		{children}
	</div>
);

export const ContainerLayout = ({
	title,
	fontColour,
	description,
	url,
	sectionId,
	sideBorders = false,
	centralBorder,
	showTopBorder = false,
	padSides = true,
	padContent = true,
	verticalMargins = true,
	borderColour,
	backgroundColour,
	children,
	leftContent,
	stretchRight = false,
	leftColSize,
	format,
	ophanComponentLink,
	ophanComponentName,
	containerPalette,
	innerBackgroundColour,
}: Props) => {
	const overrides =
		containerPalette && decideContainerOverrides(containerPalette);
	return (
		<ElementContainer
			sectionId={sectionId}
			showSideBorders={sideBorders}
			showTopBorder={showTopBorder}
			padded={padSides}
			borderColour={borderColour || overrides?.border.container}
			backgroundColour={
				backgroundColour || overrides?.background.container
			}
			element="section"
			ophanComponentLink={ophanComponentLink}
			ophanComponentName={ophanComponentName}
			innerBackgroundColour={innerBackgroundColour}
		>
			<Flex>
				<LeftColumn
					borderType={centralBorder}
					borderColour={borderColour || overrides?.border.container}
					size={leftColSize}
				>
					<>
						<ContainerTitle
							title={title}
							fontColour={fontColour || overrides?.text.container}
							description={description}
							url={url}
						/>
						{leftContent}
					</>
				</LeftColumn>
				<Container
					padded={padContent}
					verticalMargins={verticalMargins}
					stretchRight={stretchRight}
					format={format}
				>
					<Hide when="above" breakpoint="leftCol">
						<ContainerTitle
							title={title}
							fontColour={fontColour || overrides?.text.container}
							description={description}
							url={url}
						/>
					</Hide>
					{children}
				</Container>
			</Flex>
		</ElementContainer>
	);
};

import { ContainerTitle } from './ContainerTitle';

export default {
	component: ContainerTitle,
	title: 'Components/ContainerTitle',
	parameters: {
		viewport: {
			// This has the effect of turning off the viewports addon by default
			defaultViewport: 'doesNotExist',
		},
	},
};

export const DefaultStory = () => {
	return <ContainerTitle title="Title text" />;
};
DefaultStory.story = { name: 'with defaults' };

export const DescriptionStory = () => {
	return (
		<ContainerTitle title="Title text" description="About this content" />
	);
};
DescriptionStory.story = { name: 'with description' };

export const ColouredStory = () => {
	return (
		<ContainerTitle
			title="Title text"
			description="About this content"
			fontColour="green"
		/>
	);
};
ColouredStory.story = { name: 'with colour' };

export const LinkStory = () => {
	return (
		<ContainerTitle
			title="Title text"
			description="About this content"
			url="https://www.theguardian.com"
		/>
	);
};
LinkStory.story = { name: 'with a link' };

import { css } from '@emotion/react';
import {
	between,
	from,
	headline,
	space,
	text,
	until,
} from '@guardian/source-foundations';

const linkStyles = css`
	text-decoration: none;
	color: ${text.anchorSecondary};

	:hover {
		text-decoration: underline;
	}
`;

const headerStyles = (fontColour?: string) => css`
	${headline.xsmall({ fontWeight: 'bold' })};
	color: ${fontColour || text.primary};
	padding-bottom: ${space[2]}px;
	padding-top: ${space[1]}px;
	margin-left: 0;

	${from.tablet} {
		margin-left: 10px;
	}

	${from.leftCol} {
		margin-left: 0;
	}
`;

const descriptionStyles = (fontColour?: string) => css`
	${headline.xxxsmall({ fontWeight: 'medium' })};
	color: ${fontColour || text.supporting};
	p {
		/* Handle paragraphs in the description */
		margin-bottom: ${space[3]}px;
	}
	a {
		color: ${text.primary};
		text-decoration: none;
	}
	${between.tablet.and.leftCol} {
		margin-left: 10px;
	}

	${until.leftCol} {
		margin-bottom: ${space[4]}px;
	}
`;

export const ContainerTitle = ({
	title,
	fontColour,
	description,
	url,
}: {
	title?: string;
	fontColour?: string;
	description?: string;
	url?: string;
}) => {
	if (!title) return null;
	return (
		<>
			{url ? (
				<a css={linkStyles} href={url}>
					<h2 css={headerStyles(fontColour)}>{title}</h2>
				</a>
			) : (
				<h2 css={headerStyles(fontColour)}>{title}</h2>
			)}
			{description && (
				<p
					css={descriptionStyles(fontColour)}
					dangerouslySetInnerHTML={{ __html: description }}
				/>
			)}
		</>
	);
};

