const ALL_OUT_WICKETS = 10;

type Props = {
	scorecardUrl: string;
	match: CricketMatch;
	format: ArticleFormat;
};

const screenReaderOnlyStyle = css`
	border: 0;
	clip: rect(0 0 0 0);
