import { ImageComponent } from './ImageComponent';

type Props = {
	format: ArticleFormat;
	element: ImageBlockElement;
	hideCaption?: boolean;
	title?: string;
	isMainMedia?: boolean;
	starRating?: number;
	isAvatar?: boolean;
};

export const ImageBlockComponent = ({
	format,
	element,
	hideCaption,
	title,
	isMainMedia,
	starRating,
	isAvatar,
}: Props) => {
	const { role } = element;
	return (
		<ImageComponent
			element={element}
			format={format}
			hideCaption={hideCaption}
			isMainMedia={isMainMedia}
			starRating={starRating}
			role={role}
			title={title}
			isAvatar={isAvatar}
		/>
	);
};

import { css } from '@emotion/react';
import { ArticleDesign, ArticleDisplay } from '@guardian/libs';
import {
	between,
	brandAltBackground,
	from,
	headline,
	neutral,
	until,
} from '@guardian/source-foundations';
import { decidePalette } from '../lib/decidePalette';
import { Caption } from './Caption';
import { Hide } from './Hide';
import { Picture } from './Picture';
import { StarRating } from './StarRating/StarRating';

type Props = {
	element: ImageBlockElement;
	role: RoleType;
	format: ArticleFormat;
	hideCaption?: boolean;
	isMainMedia?: boolean;
	starRating?: number;
	title?: string;
	isAvatar?: boolean;
};

const starsWrapper = css`
	background-color: ${brandAltBackground.primary};

	position: absolute;
	${until.tablet} {
		bottom: 0;
	}
	${from.tablet} {
		top: 0;
	}

	/* Stars Padding from largest to smallest width */
	${from.leftCol} {
		padding-left: 5px;
	}

	${between.phablet.and.leftCol} {
		padding-left: 0px;
		margin-left: -0px;
	}

	${until.phablet} {
		padding-left: 10px;
		margin-left: 0px;
	}
`;

const PositionStarRating: React.FC<{ rating: number }> = ({ rating }) => (
	<div css={starsWrapper}>
		<StarRating rating={rating} size="large" />
	</div>
);

const basicTitlePadding = css`
	${until.tablet} {
		padding-top: 4px;
		padding-bottom: 14px;
		padding-left: 20px;
		padding-right: 20px;
	}

	${from.tablet} {
		padding-top: 4px;
		padding-bottom: 17px;
		padding-left: 20px;
		padding-right: 20px;
	}
`;

const moreTitlePadding = css`
	padding-top: 4px;

	${until.tablet} {
		padding-bottom: 14px;
		padding-left: 20px;
		padding-right: 40px;
	}

	${until.mobileLandscape} {
		padding-left: 10px;
	}

	${from.tablet} {
		padding-bottom: 17px;
		padding-left: 20px;
		padding-right: 20px;
	}

	${from.leftCol} {
		padding-left: 160px;
	}

	${from.wide} {
		padding-left: 240px;
	}
`;

const titleWrapper = (palette: Palette) => css`
	position: absolute;
	bottom: 0;
	width: 100%;

	${until.desktop} {
		${headline.xxsmall({ fontWeight: 'light' })}
	}
	${until.phablet} {
		${headline.xxxsmall({ fontWeight: 'light' })}
	}
	${from.desktop} {
		${headline.xsmall({ fontWeight: 'light' })}
	}
	color: ${neutral[100]};
	background: linear-gradient(transparent, ${neutral[0]});

	:before {
		background-color: ${palette.background.imageTitle};
		display: block;
		content: '';
		width: 8.75rem;
		${until.desktop} {
			height: 0.5rem;
		}
		${from.desktop} {
			height: 0.75rem;
		}

		margin-bottom: calc(1.25rem / 3);
	}
`;

const ImageTitle: React.FC<{
	title: string;
	role: RoleType;
	palette: Palette;
}> = ({ title, role, palette }) => {
	switch (role) {
		case 'inline':
		case 'thumbnail':
		case 'halfWidth':
		case 'supporting':
			return (
				<h2 css={[titleWrapper(palette), basicTitlePadding]}>
					{title}
				</h2>
			);
		case 'showcase':
		case 'immersive':
			return (
				<h2 css={[titleWrapper(palette), moreTitlePadding]}>{title}</h2>
			);
	}
};

const Row = ({ children }: { children: React.ReactNode }) => (
	<div
		css={css`
			display: flex;
			flex-direction: row;
		`}
	>
		{children}
	</div>
);

const CaptionToggle = () => (
