import { css } from '@emotion/react';
import type { Breakpoint } from '@guardian/source-foundations';
import { border, from } from '@guardian/source-foundations';
import { joinUrl } from '../../lib/joinUrl';
import { abTestTest } from '../experiments/tests/ab-test-test';
import { decideTrail } from '../lib/decideTrail';
import { useAB } from '../lib/useAB';
import { useApi } from '../lib/useApi';
import { MostViewedFooterGrid } from './MostViewedFooterGrid';
import { MostViewedFooterSecondTierItem } from './MostViewedFooterSecondTierItem';

interface Props {
	sectionName?: string;
	palette: Palette;
	ajaxUrl: string;
}

const stackBelow = (breakpoint: Breakpoint) => css`
	display: flex;
	flex-direction: column;

	${from[breakpoint]} {
		flex-direction: row;
	}
`;

const secondTierStyles = css`
	border-left: 1px solid ${border.secondary};
	border-right: 1px solid ${border.secondary};

	${from.tablet} {
		padding-top: 24px;
	}
`;

function buildSectionUrl(ajaxUrl: string, sectionName?: string) {
	const sectionsWithoutPopular = ['info', 'global'];
	const hasSection =
		sectionName && !sectionsWithoutPopular.includes(sectionName);
	const endpoint = `/most-read${hasSection ? `/${sectionName}` : ''}.json`;
	return joinUrl([ajaxUrl, `${endpoint}?dcr=true`]);
}

function transformTabs(tabs: CAPITrailTabType[]): TrailTabType[] {
	return tabs.map((tab) => ({
		...tab,
		trails: tab.trails.map((trail) => decideTrail(trail)),
	}));
}

export const MostViewedFooterData = ({
	sectionName,
	palette,
	ajaxUrl,
}: Props) => {
	// Example usage of AB Tests
	// Used in the Cypress tests as smoke test of the AB tests framework integration
	const ABTestAPI = useAB();

	const abTestCypressDataAttr =
		(ABTestAPI?.isUserInVariant('AbTestTest', 'control') &&
			'ab-test-control') ||
		(ABTestAPI?.isUserInVariant('AbTestTest', 'variant') &&
			'ab-test-variant') ||
		'ab-test-not-in-test';
	const runnableTest = ABTestAPI?.runnableTest(abTestTest);
	const variantFromRunnable =
		(runnableTest && runnableTest.variantToRun.id) || 'not-runnable';

	const url = buildSectionUrl(ajaxUrl, sectionName);
	const { data, error } = useApi<
		MostViewedFooterPayloadType | CAPITrailTabType[]
	>(url);

	if (error) {
		window.guardian.modules.sentry.reportError(error, 'most-viewed-footer');
		return null;
	}

	if (data) {
		const tabs = 'tabs' in data ? data.tabs : data;
		return (
			<div
				css={css`
					width: 100%;
				`}
				data-cy="mostviewed-footer"
				data-cy-ab-user-in-variant={abTestCypressDataAttr}
				data-cy-ab-runnable-test={variantFromRunnable}
			>
				<MostViewedFooterGrid
					data={transformTabs(tabs)}
					sectionName={sectionName}
					palette={palette}
				/>
				<div css={[stackBelow('tablet'), secondTierStyles]}>
					{'mostCommented' in data && (
						<MostViewedFooterSecondTierItem
							trail={decideTrail(data.mostCommented)}
							title="Most commented"
							showRightBorder={true}
						/>
					)}
					{'mostShared' in data && (
						<MostViewedFooterSecondTierItem
							trail={decideTrail(data.mostShared)}
							title="Most shared"
						/>
					)}
				</div>
			</div>
		);
	}

	return null;
};

import { css } from '@emotion/react';
import {
	border,
	from,
	headline,
	neutral,
	until,
	visuallyHidden,
} from '@guardian/source-foundations';
import { useState } from 'react';
import { MostViewedFooterItem } from './MostViewedFooterItem';

const thinGreySolid = `1px solid ${border.secondary}`;

const hideList = css`
	display: none;
`;

const tabsContainer = css`
	display: flex;
	position: relative;
	border-left: ${thinGreySolid};
	border-right: ${thinGreySolid};
	border-bottom: ${thinGreySolid};

	${until.leftCol} {
		border-top: ${thinGreySolid};
		border-bottom: 0;
	}
`;

const listTab = css`
	font-weight: 700;
	line-height: 1.1;
	background-color: transparent;
	text-transform: capitalize;
	padding: 0 0 0;
	margin-bottom: 16px;
	width: 240px;
	height: 28px;
`;

const firstTab = css`
	border-right: ${thinGreySolid};
`;
const selectedListTabStyles = (palette: Palette) => css`
	/* TODO: Using a pseudo selector here could be faster? */
	box-shadow: inset 0px 4px 0px 0px ${palette.background.mostViewedTab};
	transition: box-shadow 0.3s ease-in-out;
`;

const unselectedStyles = css`
	&:hover {
		box-shadow: inset 0px 4px 0px 0px ${neutral[86]};
		transition: box-shadow 0.3s ease-in-out;
	}
`;

const tabButton = css`
	${headline.xxxsmall()};
	margin: 0;
	border: 0;
	background: transparent;
	padding-right: 6px;
	padding-top: 6px;
	text-align: left;
	text-decoration: none;
	font-weight: 600;
	min-height: 36px;
	display: block;
	width: 100%;

	&:hover {
		cursor: pointer;
	}
`;

const gridContainer = css`
	display: grid;
	grid-auto-flow: column;

	/* One column view */
	grid-template-columns: 1fr;
	grid-template-rows: auto auto auto auto auto auto auto auto auto auto;

	/* Two column view */
	${from.tablet} {
		grid-template-columns: 1fr 1fr;
		grid-template-rows: auto auto auto auto auto;
	}

	/* We set left border on the grid container, and then right border on
    the gridItems to prevent borders doubling up */
	border-left: 1px solid ${border.secondary};
`;

type Props = {
	data: TrailTabType[];
	sectionName?: string;
	palette: Palette;
};

// To avoid having to handle multiple ways of reducing the capitalisation styling
const TabHeading = ({ heading }: { heading: string }) => {
	switch (heading.toLowerCase()) {
		case 'most popular':
			return <span>Most popular</span>;
		default:
			return (
				<span
					css={css`
						text-transform: capitalize;
					`}
					// "Across The Guardian" has a non-breaking space entity between "The" and "Guardian - Eg. "Across The&nbsp;Guardian"
					dangerouslySetInnerHTML={{
						__html: heading,
					}}
				/>
			);
	}
};

export const MostViewedFooterGrid = ({ data, sectionName, palette }: Props) => {
	const [selectedTabIndex, setSelectedTabIndex] = useState<number>(0);
	return (
		<div>
			{Array.isArray(data) && data.length > 1 && (
				<ul css={tabsContainer} role="tablist">
					{data.map((tab: TrailTabType, i: number) => {
						const isSelected = i === selectedTabIndex;
						const isFirst = i === 0;
						const selectedStyles = selectedListTabStyles(palette);
						return (
							<li
								css={[
									listTab,
									isSelected
										? selectedStyles
										: unselectedStyles,
									isFirst && firstTab,
								]}
								role="tab"
								aria-selected={isSelected}
								aria-controls={`tabs-popular-${i}`}
								id={`tabs-popular-${i}-tab`}
								data-cy={`tab-heading-${i}`}
								key={`tabs-popular-${i}-tab`}
								data-link-name={tab.heading}
								data-chromatic="ignore"
							>
								<button
									css={tabButton}
									onClick={() => setSelectedTabIndex(i)}
								>
									<span
										css={css`
											${visuallyHidden};
										`}
									>
										Most viewed{' '}
									</span>

									<TabHeading heading={tab.heading} />
								</button>
							</li>
						);
					})}
				</ul>
			)}
			{data.map((tab: TrailTabType, i: number) => (
				<ol
					css={[gridContainer, i !== selectedTabIndex && hideList]}
					id={`tabs-popular-${i}`}
					data-cy={`tab-body-${i}`}
					key={`tabs-popular-${i}`}
					role="tabpanel"
					aria-labelledby={`tabs-popular-${i}-tab`}
					data-link-name={tab.heading}
					data-testid={tab.heading}
					data-link-context={`most-read/${sectionName}`}
				>
					{(tab.trails || []).map((trail: TrailType, ii: number) => (
						<MostViewedFooterItem
							key={trail.url}
							trail={trail}
							position={ii + 1}
						/>
					))}
				</ol>
			))}
		</div>
	);
};

import { css } from '@emotion/react';
import { ArticleDesign } from '@guardian/libs';
import {
	border,
	headline,
	neutral,
	text,
	until,
} from '@guardian/source-foundations';
import { AgeWarning } from './AgeWarning';
import { BigNumber } from './BigNumber/BigNumber';
import { LinkHeadline } from './LinkHeadline';

const gridItem = (position: number) => css`
	position: relative;

	${until.leftCol} {
		/* Below leftCol always set top border */
		border-top: 1px solid ${border.secondary};
	}
	/* Above leftCol, don't apply a top border on the 1st and 6th
       items to prevent double borders */
	border-top: ${position !== 1 &&
	position !== 6 &&
	`1px solid ${border.secondary}`};

	/* The left border is set on the container */
	border-right: 1px solid ${border.secondary};
	min-height: 3.25rem;

	&:hover {
		cursor: pointer;
	}

	&:hover,
	:focus {
		background: ${neutral[97]};
	}
`;

const bigNumber = css`
	position: absolute;
	top: 0.375rem;
	left: 0.625rem;
	fill: ${text.primary};
`;

const headlineHeader = css`
	padding: 0.1875rem 0.625rem 1.125rem 4.6875rem;
	word-wrap: break-word;
	overflow: hidden;
`;

const headlineLink = css`
	text-decoration: none;
	color: ${text.anchorSecondary};
	font-weight: 500;
	${headline.xxxsmall()};

	display: block; /* To ensure focus outline works okay */
`;

const ageWarningStyles = css`
	padding-left: 4.6875rem;
	margin-top: -16px;
	margin-bottom: 16px;
`;

type Props = {
	trail: TrailType;
	position: number;
};

export const MostViewedFooterItem = ({ trail, position }: Props) => (
	<li css={gridItem(position)} data-link-name={`${position} | text`}>
		<a css={headlineLink} href={trail.url} data-link-name="article">
			<span css={bigNumber}>
				<BigNumber index={position} />
			</span>
			<div css={headlineHeader}>
				{trail.format.design === ArticleDesign.LiveBlog ? (
					<LinkHeadline
						headlineText={trail.headline}
						format={trail.format}
						size="small"
						kickerText="Live"
						showSlash={true}
						showPulsingDot={true}
						showQuotes={false}
					/>
				) : (
					<LinkHeadline
						headlineText={trail.headline}
						format={trail.format}
						size="small"
						showQuotes={
							trail.format.design === ArticleDesign.Comment ||
							trail.format.design === ArticleDesign.Letter
						}
					/>
				)}
			</div>
			{trail.ageWarning && (
				<div css={ageWarningStyles}>
					<AgeWarning age={trail.ageWarning} size="small" />
				</div>
			)}
		</a>
	</li>
);

