import { css } from '@emotion/react';
import { useEffect } from 'react';
import { decideTrail } from '../lib/decideTrail';
import { revealStyles } from '../lib/revealStyles';
import { useApi } from '../lib/useApi';
import { Placeholder } from './Placeholder';

type Props = {
	url: string;
	limit: number; // Limit the number of items shown (the api often returns more)
	ophanComponentName: OphanComponentName;
	Container: React.FC<OnwardsType>;
	format: ArticleFormat;
	isCuratedContent?: boolean;
};

type OnwardsResponse = {
	trails: CAPITrailType[];
	heading: string;
	displayname: string;
	description: string;
};

const minHeight = css`
	min-height: 300px;
`;

export const OnwardsData = ({
	url,
	limit,
	ophanComponentName,
	Container,
	format,
	isCuratedContent,
}: Props) => {
	const { data, loading, error } = useApi<OnwardsResponse>(url);

	const buildTrails = (
		trails: CAPITrailType[],
		trailLimit: number,
	): TrailType[] => {
		return trails.slice(0, trailLimit).map(decideTrail);
	};

	useEffect(() => {
		if (data) {
			const pendingElements = document.querySelectorAll<HTMLElement>(
				'.onwards > .pending',
			);
			pendingElements.forEach((element) => {
				element.classList.add('reveal');
				element.classList.remove('pending');
			});
		}
	});

	if (error) {
		// Send the error to Sentry and then prevent the element from rendering
		window.guardian.modules.sentry.reportError(error, 'onwards-lower');
		return null;
	}

	if (loading) {
		return (
			<Placeholder
				// 300 is a best guess
				height={300}
				shouldShimmer={false}
				backgroundColor="white"
			/>
		);
	}

	if (data?.trails) {
		return (
			<div css={[minHeight, revealStyles]} className="onwards">
				<div className="pending">
					<Container
						heading={data.heading || data.displayname} // Sometimes the api returns heading as 'displayName'
						trails={buildTrails(data.trails, limit)}
						description={data.description}
						ophanComponentName={ophanComponentName}
						format={format}
						isCuratedContent={isCuratedContent}
					/>
				</div>
			</div>
		);
	}

	return null;
};

import { ArticleDesign } from '@guardian/libs';
import { formatAttrString } from '../lib/formatAttrString';
import { useComments } from '../lib/useComments';
import { ContainerTitle } from './ContainerTitle';
import { ExactlyFive } from './ExactlyFive';
import { Flex } from './Flex';
import { FourOrLess } from './FourOrLess';
import { Hide } from './Hide';
import { LeftColumn } from './LeftColumn';
import { MoreThanFive } from './MoreThanFive';
import { OnwardsContainer } from './OnwardsContainer';
import { Spotlight } from './Spotlight';

const decideLayout = (trails: TrailType[]) => {
	switch (trails.length) {
		case 1:
			return <Spotlight content={trails} />;
		case 2:
		case 3:
		case 4:
			return <FourOrLess content={trails} />;
		case 5:
			return <ExactlyFive content={trails} />;
		case 6:
		case 7:
		case 8:
		default:
			return <MoreThanFive content={trails} />;
	}
};

export const OnwardsLayout: React.FC<OnwardsType> = (
	data: OnwardsType,
	format: ArticleFormat,
) => {
	const sections = useComments([data]);

	return (
		<>
			{sections.map((section, index) => (
				<Flex key={`${section.heading}-${index}`}>
					<LeftColumn
						borderType="partial"
						size={
							format.design === ArticleDesign.LiveBlog ||
							format.design === ArticleDesign.DeadBlog
								? 'wide'
								: 'compact'
						}
					>
						<ContainerTitle
							title={section.heading}
							description={section.description}
							url={section.url}
						/>
					</LeftColumn>
					<OnwardsContainer
						dataComponentName={section.ophanComponentName}
						dataLinkName={formatAttrString(section.heading)}
					>
						<Hide when="above" breakpoint="leftCol">
							<ContainerTitle
								title={section.heading}
								description={section.description}
								url={section.url}
							/>
						</Hide>
						{decideLayout(section.trails.slice(0, 8))}
					</OnwardsContainer>
				</Flex>
			))}
		</>
	);
};

