const partiallyLeft = css`
	width: 220px;
	margin-left: -10px;
	margin-right: 10px;
	padding-left: 10px;
	padding-right: 10px;
	clear: left;
	float: left;

	${until.mobileMedium} {
		width: 100%;
	}

	${from.leftCol} {
		margin-left: -120px;
	}

	:after {
		left: 10px;
		border-radius: 0 0 25px;

		${from.leftCol} {
			border-radius: 0 0 0 25px;
			left: 0;
			margin-left: 85px;
		}
	}
`;
const fullyLeft = css`
	margin-left: -10px;
	margin-right: 10px;
	padding-left: 10px;
	padding-right: 10px;
	clear: left;
	float: left;

	${until.mobileLandscape} {
		width: 100%;
	}
	${from.mobileLandscape} {
		width: 220px;
	}
	${from.leftCol} {
		width: 140px;
		margin-left: -150px;
	}
	${from.wide} {
		width: 200px;
		margin-left: -215px;
	}
`;

const partiallyInline = css`
	margin-left: 0;
	padding-left: 10px;
	padding-right: 10px;
	display: block;

	${from.mobileLandscape} {
		margin-left: -20px;
	}
	${from.phablet} {
		margin-left: -10px;
	}
	${from.leftCol} {
		margin-left: -38px;
	}

	:after {
		border-radius: 0 0 25px;
		left: 10px;

		${from.phablet} {
			left: 10px;
		}
		${from.leftCol} {
			left: 27px;
		}
	}
`;

const fullyInline = css`
	margin-left: -10px;
	display: block;
`;

function decidePosition(role: string, design: ArticleDesign) {
	if (design === ArticleDesign.PhotoEssay) {
		return role === 'supporting' ? fullyLeft : fullyInline;
	}
	return role === 'supporting' ? partiallyLeft : partiallyInline;
}

function decideFont(role: string) {
	if (role === 'supporting') {
		return css`
			${headline.xxsmall({ fontWeight: 'light' })};
		`;
	}
	return css`
		${headline.xsmall({ fontWeight: 'light' })};
	`;
}

export const PullQuoteBlockComponent: React.FC<{
	html?: string;
	palette: Palette;
	design: ArticleDesign;
	role: string;
	attribution?: string;
}> = ({ html, palette, design, attribution, role }) => {
	if (!html) return <></>;
	switch (design) {
		case ArticleDesign.Editorial:
		case ArticleDesign.Letter:
		case ArticleDesign.Comment:
			return (
				<aside
					css={[
						decidePosition(role, design),
						css`
							${headline.xxsmall({ fontWeight: 'light' })};
							line-height: 25px;
							position: relative;
							/* TODO: Source foundation doesn't have this colour, once it does, remove the hex below */
							/* stylelint-disable-next-line color-no-hex */
							background-color: #fbe6d5;
							padding-top: 6px;
							padding-bottom: 12px;
							margin-bottom: 28px;

							:after {
								content: '';
								width: 25px;
								height: 25px;
								bottom: -25px;
								position: absolute;
								/* TODO: Source foundation doesn't have this colour, once it does, remove the hex below */
								/* stylelint-disable-next-line color-no-hex */
								background-color: #fbe6d5;
							}
						`,
					]}
				>
					<QuoteIcon colour={palette.fill.quoteIcon} />
					<blockquote
						css={css`
							display: inline;
						`}
						dangerouslySetInnerHTML={{
							__html: unescapeData(html),
						}}
					/>
					<footer>
						<cite>{attribution}</cite>
					</footer>
				</aside>
			);
		case ArticleDesign.PhotoEssay:
			return (
				<aside
					css={[
						decidePosition(role, design),
						decideFont(role),
						css`
							color: ${palette.text.pullQuote};
							line-height: 25px;
							position: relative;
							padding-left: 10px;
							padding-right: 10px;
							padding-top: 6px;
							padding-bottom: 12px;
							margin-bottom: 16px;
						`,
					]}
				>
					<QuoteIcon colour={palette.fill.quoteIcon} />
					<blockquote
						dangerouslySetInnerHTML={{
							__html: unescapeData(html),
						}}
					/>
					<footer>
						<cite
							css={css`
								color: ${text.supporting};
							`}
						>
							{attribution}
						</cite>
					</footer>
				</aside>
			);
		default:
			return (
				<aside
					css={[
						decidePosition(role, design),
						css`
							${headline.xxsmall({ fontWeight: 'bold' })};
							line-height: 25px;
							position: relative;
							background-color: ${neutral[97]};
							padding-left: 10px;
							padding-right: 10px;
							padding-top: 6px;
							padding-bottom: 12px;
							margin-bottom: 1.75rem;
							color: ${palette.text.pullQuote};

							:after {
								content: '';
								width: 25px;
								height: 25px;
								bottom: -25px;
								position: absolute;
								background-color: ${neutral[97]};
							}
						`,
					]}
				>
					<QuoteIcon colour={palette.fill.quoteIcon} />
					<blockquote
						css={css`
							display: inline;
						`}
						dangerouslySetInnerHTML={{
							__html: unescapeData(html),
						}}
					/>
					<footer>
						<cite
							css={css`
								color: ${palette.text.pullQuoteAttribution};
							`}
						>
							{attribution}
						</cite>
					</footer>
				</aside>
			);
	}
};

import { css, keyframes } from '@emotion/react';
import { storage } from '@guardian/libs';
import { useEffect, useState } from 'react';

const dotStyles = (colour?: string) => css`
	color: ${colour && colour};
	:before {
		border-radius: 62.5rem;
		display: inline-block;
		position: relative;
		background-color: currentColor;
		width: 0.75em;
		height: 0.75em;
		content: '';
		margin-right: 0.1875rem;
		vertical-align: initial;
	}
`;

const livePulse = keyframes`
    0% {opacity: 1;}
    10% {opacity: .25;}
    40% {opacity: 1;}
    100% {opacity: 1;}
`;

const animate = css`
	animation: ${livePulse} 1s infinite;
	@media (prefers-reduced-motion: reduce) {
		animation: none;
	}
`;

interface Props {
	colour?: string;
}

export const PulsingDot = ({ colour }: Props) => {
	const [shouldFlash, setShouldFlash] = useState(false);

	useEffect(() => {
		// Respect the accessibility flag set here
		// https://www.theguardian.com/help/accessibility-help

		const flashingPreference = storage.local.get(
			'gu.prefs.accessibility.flashing-elements',
		);

		// flashingPreference is null if no preference exists and explicitly
		// false when the reader has said they don't want flashing
		setShouldFlash(flashingPreference !== false);
	}, []);

	return <span css={[dotStyles(colour), shouldFlash && animate]} />;
};

