	${from.wide} {
		width: 8.5rem;
		height: 8.5rem;
	}

	/* TODO remove the default img styling in ArticleBody.tsx - do we need direct element styling? */
	img {
		width: 100%;
		height: 100%;
	}
`;

const paidForBranding = css`
	${textSans.xxsmall()};
	font-weight: bold;
	color: ${text.supporting};
`;

const starWrapper = css`
	background-color: ${brandAltBackground.primary};
	display: inline-block;
`;

const readMoreText: (contentType: string) => string = (contentType) => {
	switch (contentType) {
		case 'audio':
			return 'Listen';
		case 'gallery':
			return 'View Gallery';
		case 'video':
			return 'Watch';
		default:
			return 'Read more';
	}
};

const getMainContributor: (tags: TagType[]) => string = (tags) => {
	const contributorTags = tags.filter((t) => t.type === 'Contributor');
	return contributorTags.length > 0 ? contributorTags[0].title : '';
};

const imageStyles = css`
	width: 100%;
	height: auto;
`;

export const RichLink = ({
	richLinkIndex,
	cardStyle,
	imageData,
	headlineText,
	contentType,
	url,
	starRating,
	linkFormat,
	format,
	tags,
	sponsorName,
	contributorImage,
	isPlaceholder,
}: Props) => {
	const palette = decidePalette(linkFormat);
	const linkText =
		cardStyle === 'letters' ? `${headlineText} | Letters ` : headlineText;

	const imageCardStyles = ['news', 'letters', 'media', 'feature'];
	const parentIsBlog =
		format.design === ArticleDesign.LiveBlog ||
		format.design === ArticleDesign.DeadBlog;

	const showImage =
		imageData &&
		imageData.thumbnailUrl &&
		imageCardStyles.includes(cardStyle) &&
		!parentIsBlog;
	const isPaidContent = tags
		? tags.filter((t) => t.id === 'tone/advertisement-features').length > 0
		: false;
	const isOpinion = cardStyle === 'comment';
	const mainContributor = getMainContributor(tags);
	const isLabs = linkFormat.theme === ArticleSpecial.Labs;

	return (
		<div
			data-print-layout="hide"
			data-link-name={`rich-link-${richLinkIndex} | ${richLinkIndex}`}
			data-component="rich-link"
			css={pillarBackground(palette)}
			data-name={(isPlaceholder && 'placeholder') || ''}
		>
			<div css={neutralBackground}>
				<a css={richLinkLink} href={url}>
					<div css={richLinkTopBorder(palette)} />
					{showImage && (
						<div>
							<img
								css={imageStyles}
								src={imageData.thumbnailUrl}
								alt={imageData.altText}
								width={imageData.width}
								height={imageData.height}
							/>
						</div>
					)}
					<div css={richLinkElements}>
						<div css={richLinkHeader}>
							<div
								css={isLabs ? labsRichLinkTitle : richLinkTitle}
							>
								{isOpinion && (
									<>
										<Hide when="above" breakpoint="wide">
											<QuoteIcon
												colour={palette.fill.quoteIcon}
											/>
										</Hide>
										<Hide when="below" breakpoint="wide">
											<QuoteIcon
												colour={palette.fill.quoteIcon}
											/>
										</Hide>
									</>
								)}
								{linkText}
							</div>
							{isOpinion && (
								<div css={[byline, textColour(palette)]}>
									{mainContributor}
								</div>
							)}
							{starRating && starRating > 0 && (
								<div css={starWrapper}>
									<StarRating
										rating={starRating}
										size="medium"
									/>
								</div>
							)}
							{isPaidContent && sponsorName && (
								<div css={paidForBranding}>
									Paid for by {sponsorName}
								</div>
							)}
						</div>
						{isOpinion && contributorImage && (
							<div css={contributorImageWrapper}>
								<Avatar
									imageSrc={contributorImage}
									imageAlt={mainContributor}
									format={format}
								/>
							</div>
						)}
						<div css={richLinkReadMore(palette)}>
							<ArrowInCircle />
							<div
								css={
									isLabs
										? labsReadMoreTextStyle
										: readMoreTextStyle
								}
							>
								{readMoreText(contentType)}
							</div>
						</div>
					</div>
				</a>
			</div>
		</div>
	);
};

import { ArticleDesign, ArticleDisplay, ArticleSpecial } from '@guardian/libs';
import { decideFormat } from '../lib/decideFormat';
import { useApi } from '../lib/useApi';
import type { RichLinkImageData } from './RichLink';
import { RichLink } from './RichLink';

type Props = {
	element: RichLinkBlockElement;
	ajaxUrl: string;
	richLinkIndex: number;
	format: ArticleFormat;
};

interface CAPIRichLinkType {
	cardStyle: RichLinkCardType;
	thumbnailUrl: string;
	headline: string;
	contentType: ContentType;
	url: string;
	tags: TagType[];
	sponsorName: string;
	format: CAPIFormat;
	starRating?: number;
	contributorImage?: string;
	imageAsset: ImageAsset;
}
interface ImageAsset {
	index: number;
	fields: ImageAssetFields;
	mediaType: string;
	url: string;
}
interface ImageAssetFields {
	displayCredit: string;
	source: string;
	photographer: string;
	isMaster: string;
	altText: string;
	height: string;
	credit: string;
	mediaId: string;
	width: string;
}

const buildUrl: (element: RichLinkBlockElement, ajaxUrl: string) => string = (
	element,
	ajaxUrl,
) => {
	const path = new URL(element.url).pathname;
	return `${ajaxUrl}/embed/card${path}.json?dcr=true`;
};

export const RichLinkComponent = ({
	element,
	ajaxUrl,
	richLinkIndex,
	format,
}: Props) => {
	const url = buildUrl(element, ajaxUrl);
	const { data, error } = useApi<CAPIRichLinkType>(url);

	if (error) {
		// Send the error to Sentry
		window.guardian.modules.sentry.reportError(error, 'rich-link');
	}

	if (data) {
		const richLinkImageData: RichLinkImageData = {
			thumbnailUrl: data.thumbnailUrl,
			altText: data.imageAsset.fields.altText,
			width: data.imageAsset.fields.width,
			height: data.imageAsset.fields.height,
		};

		return (
			// This is the enhanced rich link rendered on the client
			<RichLink
				richLinkIndex={richLinkIndex}
				cardStyle={data.cardStyle}
				imageData={richLinkImageData}
				headlineText={data.headline}
				contentType={data.contentType}
				url={data.url}
				starRating={data.starRating}
				linkFormat={decideFormat(data.format)}
				format={format}
				tags={data.tags}
				sponsorName={data.sponsorName}
				contributorImage={data.contributorImage}
			/>
		);
	}

	const defaultFormat = {
		display: ArticleDisplay.Standard,
		design: ArticleDesign.Standard,
		// We default to SpecialReport here purely because the greys of this theme
		// look better as the defaults
		theme: ArticleSpecial.SpecialReport,
	};

	return (
		// Render a default (basic) rich link on the server
		<RichLink
			richLinkIndex={richLinkIndex}
			cardStyle="news"
			imageData={{
				thumbnailUrl: '',
				altText: '',
				width: '',
				height: '',
			}}
			headlineText={element.text}
			contentType="article"
			url={element.url}
			linkFormat={defaultFormat}
			format={defaultFormat}
			tags={[]}
			sponsorName=""
			isPlaceholder={true}
		/>
	);
};

