export const SeriesSectionLink = ({
	format,
	tags,
	sectionLabel,
	sectionUrl,
	guardianBaseURL,
	badge,
	isMatch,
}: Props) => {
	const observerTag = tags.find(
		(tag) => tag.type === 'Publication' && tag.title === 'The Observer',
	);
	const isCommentIsFree = tags.some(
		(tag) => tag.id === 'commentisfree/commentisfree',
	);
	const seriesTag = tags.find(
		(tag) =>
			tag.type === 'Blog' ||
			tag.type === 'Series' ||
			(tag.type === 'Publication' && tag.title === 'The Observer'),
	);

	// If we have a tag, use it to show 2 section titles
	// Observer opinion (commentisfree) articles should prioritise
	// the publication tag over the commentisfree tag.
	const tag = observerTag && isCommentIsFree ? observerTag : seriesTag;

	const hasSeriesTag = tag && tag.type === 'Series';

	const isLabs = format.theme === ArticleSpecial.Labs;

	const palette = decidePalette(format);

	const seriesTitleColour = isMatch
		? palette.text.seriesTitleWhenMatch
		: palette.text.seriesTitle;

	const sectionTitleColour = isMatch
		? palette.text.seriesTitleWhenMatch
		: palette.text.sectionTitle;

	switch (format.display) {
		case ArticleDisplay.Immersive: {
			switch (format.design) {
				case ArticleDesign.Comment:
				case ArticleDesign.Editorial:
				case ArticleDesign.Letter: {
					if (tag) {
						// We have a tag, we're not immersive, show both series and section titles
						return (
							// Sometimes the tags/titles are shown inline, sometimes stacked
							<div css={!badge && rowBelowLeftCol(format)}>
								<a
									href={`${guardianBaseURL}/${tag.id}`}
									css={[
										sectionLabelLink,
										marginRight,
										fontStyles(format),
										breakWord,
										css`
											color: ${seriesTitleColour};
											background-color: ${palette
												.background.seriesTitle};
											box-shadow: -6px 0 0 0
													${palette.background
														.seriesTitle},
												6px 0 0 0
													${palette.background
														.seriesTitle};
										`,
									]}
									data-component="series"
									data-link-name="article series"
								>
									<span>{tag.title}</span>
								</a>

								<Hide when="below" breakpoint="tablet">
									<a
										href={`${guardianBaseURL}/${sectionUrl}`}
										css={[
											sectionLabelLink,
											fontStyles(format),
											displayBlock,
											breakWord,
											css`
												color: ${palette.text
													.sectionTitle};
												background-color: ${palette
													.background.sectionTitle};
												box-shadow: -6px 0 0 0
														${palette.background
															.seriesTitle},
													6px 0 0 0
														${palette.background
															.seriesTitle};
											`,
										]}
										data-component="section"
										data-link-name="article section"
									>
										<span>{sectionLabel}</span>
									</a>
								</Hide>
							</div>
						);
					}
					// There's no tag so fallback to section title
					return (
						<div css={marginBottom}>
							<a
								href={`${guardianBaseURL}/${sectionUrl}`}
								css={[
									sectionLabelLink,
									marginRight,
									fontStyles(format),
									breakWord,
									css`
										color: ${sectionTitleColour};
										background-color: ${palette.background
											.sectionTitle};
										box-shadow: -6px 0 0 0
												${palette.background
													.seriesTitle},
											6px 0 0 0
												${palette.background
													.seriesTitle};
									`,
								]}
								data-component="section"
								data-link-name="article section"
							>
								<span>{sectionLabel}</span>
							</a>
						</div>
					);
				}
				default: {
					if (hasSeriesTag || isLabs) {
						const title = tag?.title ? tag.title : sectionLabel;
						const linkExt = tag?.id ? tag.id : sectionUrl;
						return (
							<div
								css={badge && immersiveTitleBadgeStyle(palette)}
							>
								{badge && (
									<div
										css={[
											titleBadgeWrapper,
											sectionPadding,
										]}
									>
										<Badge
											imageUrl={badge.imageUrl}
											seriesTag={badge.seriesTag}
										/>
									</div>
								)}

								<a
									css={[
										sectionLabelLink,
										fontStyles(format),
										invertedStyle,
										breakWord,
										!badge && sectionPadding,
										css`
											color: ${seriesTitleColour};
											background-color: ${palette
												.background.seriesTitle};
											box-shadow: -6px 0 0 0
													${palette.background
														.seriesTitle},
												6px 0 0 0
													${palette.background
														.seriesTitle};
										`,
									]}
									href={`${guardianBaseURL}/${linkExt}`}
									data-component="series"
									data-link-name="article series"
								>
									<span>{title}</span>
								</a>
							</div>
						);
					}
					// Immersives show nothing at all if there's no series tag
					return null;
				}
			}
		}
		case ArticleDisplay.Showcase:
		case ArticleDisplay.NumberedList:
		case ArticleDisplay.Standard:
		default: {
			if (tag) {
				// We have a tag, we're not immersive, show both series and section titles
				return (
					// Sometimes the tags/titles are shown inline, sometimes stacked
					<div css={!badge && rowBelowLeftCol(format)}>
						<Hide when="above" breakpoint="desktop">
							{format.design === ArticleDesign.LiveBlog && (
								<span
									css={[
										fontStyles(format),
										css`
											color: ${seriesTitleColour};
										`,
									]}
								>
									<Island deferUntil="idle">
										<PulsingDot />
									</Island>
								</span>
							)}
						</Hide>
						<a
							href={`${guardianBaseURL}/${tag.id}`}
							css={[
								sectionLabelLink,
								css`
									color: ${seriesTitleColour};
									background-color: ${palette.background
										.seriesTitle};
								`,
								marginRight,
								fontStyles(format),
								breakWord,
								css`
									box-shadow: -6px 0 0 0
											${palette.background.seriesTitle},
										6px 0 0 0
											${palette.background.seriesTitle};
								`,
							]}
							data-component="series"
							data-link-name="article series"
						>
							<span>{tag.title}</span>
						</a>

						<Hide when="below" breakpoint="tablet">
							<a
								href={`${guardianBaseURL}/${sectionUrl}`}
								css={[
									sectionLabelLink,
									secondaryFontStyles(format),
									displayBlock,
									breakWord,
									css`
										color: ${sectionTitleColour};
										background-color: ${palette.background
											.sectionTitle};
									`,
								]}
								data-component="section"
								data-link-name="article section"
							>
								<span>{sectionLabel}</span>
							</a>
						</Hide>
					</div>
				);
			}
			// There's no tag so fallback to section title
			return (
				<>
					<Hide when="above" breakpoint="desktop">
						{format.design === ArticleDesign.LiveBlog && (
							<span
								css={[
									fontStyles(format),
									css`
										color: ${seriesTitleColour};
									`,
								]}
							>
								<Island deferUntil="idle">
									<PulsingDot />
								</Island>
							</span>
						)}
					</Hide>
					<a
						href={`${guardianBaseURL}/${sectionUrl}`}
						css={[
							sectionLabelLink,
							css`
								color: ${sectionTitleColour};
								background-color: ${palette.background
									.sectionTitle};
							`,
							marginRight,
							fontStyles(format),
							breakWord,
						]}
						data-component="section"
						data-link-name="article section"
						className={interactiveLegacyClasses.labelLink}
					>
						<span>{sectionLabel}</span>
					</a>
				</>
			);
		}
	}
};

import { AB } from '@guardian/ab-core';
import type { CoreAPIConfig } from '@guardian/ab-core/dist/types';
import { getCookie, log } from '@guardian/libs';
import { getOphanRecordFunction } from '../browser/ophan/ophan';
import { tests } from '../experiments/ab-tests';
import { getCypressSwitches } from '../experiments/cypress-switches';
import { getForcedParticipationsFromUrl } from '../lib/getAbUrlHash';
import { setABTests } from '../lib/useAB';

type Props = {
	abTestSwitches: CoreAPIConfig['abTestSwitches'];
	forcedTestVariants?: CoreAPIConfig['forcedTestVariants'];
	isDev: boolean;
	pageIsSensitive: CoreAPIConfig['pageIsSensitive'];
};

export const SetABTests = ({
	isDev,
	pageIsSensitive,
	abTestSwitches,
	forcedTestVariants,
}: Props) => {
	const mvtId = Number(
		(isDev &&
			getCookie({ name: 'GU_mvt_id_local', shouldMemoize: true })) || // Simplify localhost testing by creating a different mvt id
			getCookie({ name: 'GU_mvt_id', shouldMemoize: true }),
	);
	if (!mvtId) {
		// 0 is default and falsy here

		console.log('There is no MVT ID set, see SetABTests.importable.tsx');
	}
	const ophanRecord = getOphanRecordFunction();
	const windowHash = window.location.hash;
	// Get the forced switches to use for when running within cypress
	// Is empty object if not in cypress
	const cypressAbSwitches = getCypressSwitches();

	const ab = new AB({
		mvtId,
		pageIsSensitive,
		abTestSwitches: {
			...abTestSwitches,
			...cypressAbSwitches, // by adding cypress switches below CAPI, we can override any production switch in Cypress
		},
		arrayOfTestObjects: tests,
		ophanRecord,
		forcedTestVariants: {
			...forcedTestVariants,
			...getForcedParticipationsFromUrl(windowHash),
		},
	});
	setABTests(ab);

	const allRunnableTests = ab.allRunnableTests(tests);
	ab.trackABTests(allRunnableTests);
	ab.registerImpressionEvents(allRunnableTests);
	ab.registerCompleteEvents(allRunnableTests);
	log('dotcom', 'AB tests initialised');

	// we donâ€™t render anything
	return null;
};

