		(context === 'ArticleMeta' || context === 'SubMeta')
	) {
		return css`
			:hover {
				background-color: ${palette.fill.shareIconGrayBackground};
				border-color: ${palette.fill.shareIconGrayBackground};
				fill: white;
			}
		`;
