import type { OphanComponent, OphanComponentEvent } from '@guardian/libs';
import type { CurrentSignInGateABTest } from './types';

type ABTestVariant = {
	name: string;
	variant: string;
};

const isServer = typeof window === 'undefined';

export type ComponentEventParams = {
	componentType: string;
	componentId?: string;
	abTestName: string;
	abTestVariant: string;
	viewId?: string;
	browserId?: string;
	visitId?: string;
};

type ComponentEventWithoutAction = {
	component: OphanComponent;
	value?: string;
	id?: string;
	abTest?: ABTestVariant;
};

const ophan = isServer ? undefined : window.guardian.ophan;

// ophan helper methods
const submitComponentEventTracking = (componentEvent: OphanComponentEvent) => {
	ophan?.record({ componentEvent });
};

export const submitViewEventTracking = (
	componentEvent: ComponentEventWithoutAction,
) =>
	submitComponentEventTracking({
		...componentEvent,
		action: 'VIEW',
	});

export const submitClickEventTracking = (
	componentEvent: ComponentEventWithoutAction,
) =>
	submitComponentEventTracking({
		...componentEvent,
		action: 'CLICK',
	});

export const withComponentId: (id: string) => OphanComponent = (id = '') => ({
	componentType: 'SIGN_IN_GATE',
	id,
});

export const trackLink = (
	componentId: string,
	value: string,
	abTest?: CurrentSignInGateABTest,
): void => {
	const component = withComponentId(componentId);

	submitClickEventTracking({
		component,
		abTest,
		value,
	});
};

// TODO: Add localstorage lib from https://github.com/guardian/libs/pull/1 when it is merged
const localStorageKey = `gu.prefs.sign-in-gate`;

// We use this key for storing the date the gate was dismissed against
const localStorageDismissedDateKey = (
	variant: string,
	name: string,
): string => {
	return `${name}-${variant}`;
};

// We use this key for storing the gate dismissed count against
const localStorageDismissedCountKey = (
	variant: string,
	name: string,
): string => {
	return `gate-dismissed-count-${name}-${variant}`;
};

// Invalid json stored against `localStorageKey` should not break signin gate for a user forever
const getSigninGatePrefsSafely = (): { [key: string]: any } => {
	try {
		const prefs: { [key: string]: any } = JSON.parse(
			localStorage.getItem(localStorageKey) || '{}',
		);

		if (typeof prefs === 'object' && typeof prefs.value === 'object') {
			return prefs.value;
		}
		return {};
	} catch (e) {
		return {};
	}
};

const setSigninGatePrefs = (prefs: any) => {
	localStorage.setItem(localStorageKey, JSON.stringify({ value: prefs }));
};

// set in user preferences that the user has dismissed the gate, set the value to the current ISO date string
// name is optional, but can be used to differentiate between multiple sign in gate tests
//
//
// This is set in local storage with the following shape:
//
// key:   gu.prefs.sign-in-gate
// value: {"testVariantName":"2020-07-01T10:55:09.085Z"}
//
// We set the value using the key, which remains constant
// and add an entry to the object with the testname and variant, and use current ISO date string as the value
export const setUserDismissedGate = (variant: string, name: string): void => {
	try {
		const prefs = getSigninGatePrefsSafely();
		prefs[localStorageDismissedDateKey(variant, name)] =
			new Date().toISOString();
		setSigninGatePrefs(prefs);
	} catch (error) {
		// Alas, sometimes localstorage isn't available
	}
};

export const unsetUserDismissedGate = (variant: string, name: string): void => {
	try {
		const prefs = getSigninGatePrefsSafely();
		delete prefs[localStorageDismissedDateKey(variant, name)];
		setSigninGatePrefs(prefs);
	} catch (error) {
		// Alas, sometimes localstorage isn't available
	}
};

// Check if the user has dismissed the gate by checking the user preferences,
// name is optional, but can be used to differentiate between multiple sign in gate tests
//
// This is set in local storage with the following shape:
//
// key:   gu.prefs.sign-in-gate
// value: {"testVariantName":"2020-07-01T10:55:09.085Z"}
//
// We extract the value using the key, which remains constant
// and the from within the value object we look up the variant we are looking for
export const hasUserDismissedGate = (
	variant: string,
	name: string,
	window?: number, // represents hours - only use if the gate should reshow after X hrs (dismissal window)
): boolean => {
	try {
		const prefs = getSigninGatePrefsSafely();
		// checks if a dismissal occurred within a given window timeframe in hours
		if (window) {
			// checks if prefs is empty, ie. the user has not dismissed gate before.
			if (!prefs[localStorageDismissedDateKey(variant, name)]) {
				return false;
			}

			const dismissalTZ = Date.parse(
				prefs[localStorageDismissedDateKey(variant, name)],
			);
			const hours = (Date.now() - dismissalTZ) / 36e5; //  36e5 is the scientific notation for 60*60*1000, which converts the milliseconds difference into hours.

			if (hours >= window) {
				unsetUserDismissedGate(variant, name);
				return false;
			}
			return true;
		}

		return !!prefs[localStorageDismissedDateKey(variant, name)];
	} catch (error) {
		// Alas, sometimes localstorage isn't available. Please have a sign in gate as an apology
		return false;
	}
};

const retrieveDismissedCount = (variant: string, name: string): number => {
	try {
		const prefs = getSigninGatePrefsSafely();
		const dismissed: any =
			prefs[localStorageDismissedCountKey(variant, name)];

		if (Number.isFinite(dismissed)) {
			return dismissed;
		}
		return 0;
	} catch (error) {
		return 0;
	}
};

// Test whether the user has dismissed the gate variant more than `count` times
export const hasUserDismissedGateMoreThanCount = (
	variant: string,
	name: string,
	count: number,
): boolean => {
	return retrieveDismissedCount(variant, name) > count;
};

// Increment the number of times a user has dismissed this gate variant
export const incrementUserDismissedGateCount = (
	variant: string,
	name: string,
): void => {
	try {
		const prefs = getSigninGatePrefsSafely();
		prefs[localStorageDismissedCountKey(variant, name)] =
			retrieveDismissedCount(variant, name) + 1;
		setSigninGatePrefs(prefs);
	} catch (error) {
		// localstorage isn't available so show the gate
	}
};

// use the dailyArticleCount from the local storage to see how many articles the user has viewed in a day
import { onConsentChange } from '@guardian/consent-management-platform';
import type { ConsentState } from '@guardian/consent-management-platform/dist/types';
import { getLocale } from '@guardian/libs';
import type { DailyArticle } from '../../lib/dailyArticleCount';
import { getDailyArticleCount } from '../../lib/dailyArticleCount';
import { hasUserDismissedGateMoreThanCount } from './dismissGate';
import type { CanShowGateProps } from './types';

// in our case if this is the n-numbered article or higher the user has viewed then set the gate
export const isNPageOrHigherPageView = (n = 2): boolean => {
	// get daily read article count array from local storage
	const [dailyCount = {} as DailyArticle] = getDailyArticleCount() || [];

	const { count = 0 } = dailyCount;

	return count >= n;
};

// determine if the useragent is running iOS 9 (known to be buggy for sign in flow)
export const isIOS9 = (): boolean => {
	// get the browser user agent
	const ua = navigator.userAgent;
	// check useragent if the device is an iOS device
	const appleDevice = /(iPhone|iPod|iPad)/i.test(ua);
	// check useragent if the os is version 9
	const os = /(CPU OS 9_)/i.test(ua);

	// if both true, then it's an apple ios 9 device
	return appleDevice && os;
};

// hide the sign in gate on article types that are not supported
export const isValidContentType = (contentType: string): boolean => {
	// It's safer to definitively *include* types as we
	// know new types will not break the sign-in-gate going forward
	const validTypes = ['Article'];

	return validTypes.some((type: string): boolean => contentType === type);
};

// hide the sign in gate on certain sections of the site, e.g info, about, help etc.
export const isValidSection = (sectionName?: string): boolean => {
	const invalidSections = [
		'about',
		'info',
		'membership',
		'help',
		'guardian-live-australia',
		'gnm-archive',
	];

	// we check for invalid section by reducing the above array, and then NOT the result so we know
	// its a valid section
	return !invalidSections.some(
		(section: string): boolean => sectionName === section,
	);
};

// hide the sign in gate for certain tags on the site
export const isValidTag = (tags: TagType[]): boolean => {
	const invalidTags = ['info/newsletter-sign-up'];

	return !invalidTags.some((invalidTag) =>
		tags.map((tag) => tag.id).includes(invalidTag),
	);
};

export const hasRequiredConsents = (): Promise<boolean> => {
	const hasConsentedToAll = (state: ConsentState) => {
		const consentFlags = state.tcfv2?.consents
			? Object.values(state.tcfv2.consents)
			: [];
		const vendorConsentFlags = state.tcfv2?.vendorConsents
			? Object.values(state.tcfv2.vendorConsents)
			: [];
		const isEmpty =
			consentFlags.length === 0 || vendorConsentFlags.length === 0;

		return (
			!isEmpty && [...consentFlags, ...vendorConsentFlags].every(Boolean)
		);
	};

	return new Promise((resolve) => {
		onConsentChange((state) => {
			if (state.tcfv2) {
				return resolve(hasConsentedToAll(state));
			}

			if (state.ccpa) {
				return resolve(!state.ccpa.doNotSell);
			}

			if (state.aus) {
				return resolve(state.aus.personalisedAdvertising);
			}

			// this shouldn't ever be hit, but this is here as safety
			return resolve(false);
		});
	});
};

export const canShowSignInGate = ({
	isSignedIn,
	currentTest,
	contentType,
	sectionName,
	tags,
	isPaidContent,
	isPreview,
}: CanShowGateProps): Promise<boolean> =>
	Promise.resolve(
		!isSignedIn &&
			!hasUserDismissedGateMoreThanCount(
				currentTest.variant,
				currentTest.name,
				5,
			) &&
			isNPageOrHigherPageView(3) &&
			isValidContentType(contentType) &&
			isValidSection(sectionName) &&
			isValidTag(tags) &&
			// hide the sign in gate on isPaidContent
			!isPaidContent &&
			// hide the sign in gate on internal tools preview &&
			!isPreview &&
			!isIOS9(),
	);

export const canShowMandatoryUs: ({
	isSignedIn,
	currentTest,
	contentType,
	sectionName,
	tags,
	isPaidContent,
	isPreview,
}: CanShowGateProps) => Promise<boolean> = async ({
	isSignedIn,
	currentTest,
	contentType,
	sectionName,
	tags,
	isPaidContent,
	isPreview,
}: CanShowGateProps) => {
	return (
		(await getLocale()) === 'US' &&
		(await hasRequiredConsents()) &&
		(await canShowSignInGate({
			isSignedIn,
			currentTest,
			contentType,
			sectionName,
			tags,
			isPaidContent,
			isPreview,
		}))
	);
};

