type Props = {
	embedUrl?: string;
	height?: number;
	width?: number;
	title?: string;
	format: ArticleFormat;
	caption?: string;
	credit?: string;
	role?: RoleType;
	isTracking: boolean;
	isMainMedia: boolean;
