export const SpotifyBlockComponent = ({
	embedUrl,
	width,
	height,
	title,
	format,
	caption,
	credit,
	role,
	isTracking,
	isMainMedia,
	source,
	sourceDomain,
}: Props) => {
	if (!embedUrl || !title || !width || !height) return null;

	const embedContainer = css`
		iframe {
			width: 100%;
		}
		margin-bottom: 16px;
	`;

	return (
		<ClickToView
			role={role}
			isTracking={isTracking}
			isMainMedia={isMainMedia}
			source={source}
			sourceDomain={sourceDomain}
		>
			<div css={embedContainer} data-cy="spotify-embed">
				<iframe
					src={embedUrl}
					title={title}
					height={height}
					width={width}
					allowFullScreen={true}
				/>
				{caption && (
					<Caption
						captionText={caption}
						format={format}
						credit={credit}
						isMainMedia={isMainMedia}
					/>
				)}
			</div>
		</ClickToView>
	);
};

