const nestedStyles = (format: ArticleFormat, palette: Palette) => {
	const offset = format.display === ArticleDisplay.Immersive ? space[6] : 19;
	return css`
		li {
			margin-bottom: 6px;
			padding-left: ${offset}px;

			p {
				display: inline;
			}
		}

		li:before {
			display: inline-block;
			content: '';
			border-radius: 50%;
			height: 0.7em;
			width: 0.7em;
			margin-right: 7px;
			background-color: ${palette.background.bulletStandfirst};
			margin-left: -${offset}px;
		}

		p {
			margin-bottom: 8px;
		}

		strong {
			font-weight: bold;
		}

		[data-dcr-style='bullet'] {
			display: inline-block;
			content: '';
			border-radius: 50%;
			height: 13px;
			width: 13px;
			margin-right: 2px;
			background-color: ${palette.background.bulletStandfirst};
		}

		a {
			color: ${palette.text.standfirstLink};
			text-decoration: none;
			border-bottom: 1px solid ${palette.border.standfirstLink};
			transition: border-color 0.15s ease-out;
		}
	`;
};

const standfirstStyles = (format: ArticleFormat, palette: Palette) => {
	switch (format.display) {
		case ArticleDisplay.Immersive:
			switch (format.design) {
				case ArticleDesign.PhotoEssay:
					if (format.theme === ArticleSpecial.Labs) {
						return css`
							${textSans.large({})};
							margin-top: ${space[2]}px;
							margin-bottom: ${space[3]}px;
							line-height: 22px;
							max-width: 540px;
							color: ${palette.text.standfirst};
						`;
					}
					return css`
						${headline.xxxsmall({})};
						margin-top: ${space[2]}px;
						margin-bottom: ${space[3]}px;
						line-height: 22px;
						max-width: 540px;
						color: ${palette.text.standfirst};
					`;
				default:
					return css`
						${format.theme === ArticleSpecial.Labs
							? textSans.medium()
							: headline.xsmall({
									fontWeight: 'light',
							  })};
						padding-top: ${space[4]}px;

						max-width: 280px;
						${from.tablet} {
							max-width: 460px;
						}
						color: ${palette.text.standfirst};

						li:before {
							height: 17px;
							width: 17px;
						}
					`;
			}

		case ArticleDisplay.NumberedList:
			return css`
				${headline.xxsmall({
					fontWeight: 'bold',
				})};
				margin-bottom: ${space[3]}px;
				max-width: 540px;
				color: ${palette.text.standfirst};
			`;

		case ArticleDisplay.Showcase:
		case ArticleDisplay.Standard:
		default: {
			switch (format.design) {
				case ArticleDesign.Editorial:
				case ArticleDesign.Letter:
				case ArticleDesign.Comment:
				case ArticleDesign.Feature:
				case ArticleDesign.Recipe:
				case ArticleDesign.Review:
					return css`
						${headline.xxsmall({
							fontWeight: 'light',
						})};
						margin-bottom: ${space[3]}px;
						max-width: 540px;
						color: ${palette.text.standfirst};
						li:before {
							height: 15px;
							width: 15px;
						}
					`;
				case ArticleDesign.LiveBlog:
				case ArticleDesign.DeadBlog:
					return css`
						${headline.xxxsmall({
							fontWeight: 'bold',
						})};
						line-height: 20px;
						margin-top: ${space[1]}px;
						margin-bottom: ${space[3]}px;
						max-width: 540px;
						color: ${palette.text.standfirst};
					`;
				default:
					switch (format.theme) {
						case ArticleSpecial.Labs:
							return css`
								${textSans.medium()}
								margin-bottom: ${space[3]}px;
								max-width: 540px;
								color: ${palette.text.standfirst};
								a {
									color: ${neutral[7]};
									border-bottom: 1px solid ${neutral[60]};
								}
							`;
						default:
							return css`
								${headline.xxxsmall({
									fontWeight: 'bold',
								})};
								line-height: 20px;
								margin-bottom: ${space[3]}px;
								max-width: 540px;
								color: ${palette.text.standfirst};
							`;
					}
			}
		}
	}
};

const hoverStyles = (palette: Palette) => {
	return css`
		a:hover {
			border-bottom: solid 1px ${palette.hover.standfirstLink};
		}
	`;
};

export const Standfirst = ({ format, standfirst }: Props) => {
	const palette = decidePalette(format);

	return (
		<div
			css={[
				nestedStyles(format, palette),
				standfirstStyles(format, palette),
				hoverStyles(palette),
			]}
			className={
				format.design === ArticleDesign.Interactive
					? interactiveLegacyClasses.standFirst
					: ''
			}
			dangerouslySetInnerHTML={{
				__html: sanitise(standfirst, {
					allowedTags: false, // Leave tags from CAPI alone
					allowedAttributes: false, // Leave attributes from CAPI alone
					transformTags: {
						a: (tagName, attribs) => ({
							tagName, // Just return anchors as is
							attribs: {
								...attribs, // Merge into the existing attributes
								'data-link-name': 'in standfirst link', // Add the data-link-name for Ophan to anchors
							},
						}),
					},
				}),
			}}
		/>
	);
};

