	decideLineCount,
	decideLineEffect,
	getCurrentPillar,
} from '../lib/layoutHelpers';
import { BannerWrapper, SendToBack, Stuck } from './lib/stickiness';

const HeadlineGrid = ({ children }: { children: React.ReactNode }) => (
	<div
		css={css`
			/* IE Fallback */
			display: flex;
			flex-direction: column;
			${until.desktop} {
				margin-left: 0px;
			}
			${from.desktop} {
				margin-left: 240px;
			}
			@supports (display: grid) {
				display: grid;
				width: 100%;
				margin-left: 0;
				grid-column-gap: 20px;
				/*
					Explanation of each unit of grid-template-columns
					Main content
					Right Column
				*/
				${from.desktop} {
					grid-template-columns: 220px 1fr;
					grid-template-areas: 'title	headline';
				}
				${until.desktop} {
					grid-template-columns: 1fr; /* Main content */
					grid-template-areas:
						'title'
						'headline';
				}
				${until.tablet} {
					grid-column-gap: 0px;
				}
			}
		`}
	>
		{children}
	</div>
);

const StandFirstGrid = ({ children }: { children: React.ReactNode }) => (
	<div
		css={css`
			/* IE Fallback */
			display: flex;
			flex-direction: column;
			${until.desktop} {
				margin-left: 0px;
			}
			${from.desktop} {
				margin-left: 240px;
			}
			@supports (display: grid) {
				display: grid;
				width: 100%;
				margin-left: 0;
				grid-column-gap: 20px;
				${until.desktop} {
					grid-template-columns: 1fr; /* Main content */
					grid-template-areas:
						'standfirst'
						'lines'
						'meta';
					grid-column-gap: 0px;
				}
				${from.desktop} {
					grid-template-columns: 220px 1fr;
					grid-template-areas: 'lastupdated standfirst';
				}
			}
		`}
	>
		{children}
	</div>
);

const LiveGrid = ({ children }: { children: React.ReactNode }) => (
	<div
		css={css`
			/* IE Fallback */
			display: flex;
			flex-direction: column;
			${until.desktop} {
				margin-left: 0px;
			}
			${from.desktop} {
				margin-left: 320px;
			}
			@supports (display: grid) {
				display: grid;
				width: 100%;
				margin-left: 0;
				grid-column-gap: 0px;

				/*
					Explanation of each unit of grid-template-columns
					Main content
					Right Column
				*/
				/* from desktop define fixed body width */
				${from.desktop} {
					grid-column-gap: 20px;
					grid-template-columns: 240px 700px;
					grid-template-areas:
						'info		media'
						'info		body';
				}
				/* from wide define fixed body width */
				${from.wide} {
					grid-column-gap: 20px;
					grid-template-columns: 240px 700px 1fr;
					grid-template-areas:
						'info  		 media     right-column'
						'info		 body      right-column';
				}
				/* until desktop define fixed body width */
				${until.desktop} {
					grid-template-columns: 1fr; /* Main content */
					grid-template-areas:
						'media'
						'info'
						'body';
				}
			}
		`}
	>
		{children}
	</div>
);

const maxWidth = css`
	${from.desktop} {
		max-width: 700px;
	}
`;

const sticky = css`
	${from.desktop} {
		position: sticky;
		top: 10px;
	}
`;

const keyEventsMargins = css`
	margin-bottom: ${space[3]}px;
	${from.desktop} {
		margin-top: ${space[1]}px;
		margin-bottom: 0;
	}
`;

const sidePaddingDesktop = css`
	${from.desktop} {
		padding-left: ${space[5]}px;
	}
`;

const bodyWrapper = css`
	margin-bottom: ${space[3]}px;
	${from.desktop} {
		margin-bottom: 0;
	}
`;

const starWrapper = css`
	margin-bottom: 18px;
	margin-top: 6px;
	background-color: ${brandAltBackground.primary};
	display: inline-block;
	${until.phablet} {
		padding-left: 20px;
		margin-left: -20px;
	}
	${until.leftCol} {
		padding-left: 0px;
		margin-left: -0px;
	}
	padding-left: 10px;
	margin-left: -10px;
`;

interface Props {
	CAPIArticle: CAPIArticleType;
	NAV: NavType;
	format: ArticleFormat;
}

export const LiveLayout = ({ CAPIArticle, NAV, format }: Props) => {
	const {
		config: { isPaidContent, host },
	} = CAPIArticle;

	const adTargeting: AdTargeting = buildAdTargeting({
		isAdFreeUser: CAPIArticle.isAdFreeUser,
		isSensitive: CAPIArticle.config.isSensitive,
		videoDuration: CAPIArticle.config.videoDuration,
		edition: CAPIArticle.config.edition,
		section: CAPIArticle.config.section,
		sharedAdTargeting: CAPIArticle.config.sharedAdTargeting,
		adUnit: CAPIArticle.config.adUnit,
	});

	const showBodyEndSlot =
		parse(CAPIArticle.slotMachineFlags || '').showBodyEnd ||
		CAPIArticle.config.switches.slotBodyEnd;

	// TODO:
	// 1) Read 'forceEpic' value from URL parameter and use it to force the slot to render
	// 2) Otherwise, ensure slot only renders if `CAPIArticle.config.shouldHideReaderRevenue` equals false.

	const seriesTag = CAPIArticle.tags.find(
		(tag) => tag.type === 'Series' || tag.type === 'Blog',
	);

	const showOnwardsLower = seriesTag && CAPIArticle.hasStoryPackage;

	// Set a default pagination if it is missing from CAPI
	const pagination: Pagination = CAPIArticle.pagination ?? {
		currentPage: 1,
		totalPages: 1,
	};

	const contributionsServiceUrl = getContributionsServiceUrl(CAPIArticle);

	const { branding } =
		CAPIArticle.commercialProperties[CAPIArticle.editionId];

	const palette = decidePalette(format);

	const footballMatchUrl =
		CAPIArticle.matchType === 'FootballMatchType' && CAPIArticle.matchUrl;

	const cricketMatchUrl =
		CAPIArticle.matchType === 'CricketMatchType' && CAPIArticle.matchUrl;

	return (
		<>
			<div data-print-layout="hide">
				<Stuck>
					<ElementContainer
						showTopBorder={false}
						showSideBorders={false}
						padded={false}
						shouldCenter={false}
						element="aside"
					>
						<HeaderAdSlot
							isAdFreeUser={CAPIArticle.isAdFreeUser}
							shouldHideAds={CAPIArticle.shouldHideAds}
							display={format.display}
						/>
					</ElementContainer>
				</Stuck>
				<SendToBack>
					<ElementContainer
						showTopBorder={false}
						showSideBorders={false}
						padded={false}
						backgroundColour={brandBackground.primary}
						element="header"
					>
						<Header
							edition={CAPIArticle.editionId}
							idUrl={CAPIArticle.config.idUrl}
							mmaUrl={CAPIArticle.config.mmaUrl}
							supporterCTA={
								CAPIArticle.nav.readerRevenueLinks.header
									.supporter
							}
							discussionApiUrl={
								CAPIArticle.config.discussionApiUrl
							}
							urls={CAPIArticle.nav.readerRevenueLinks.header}
							remoteHeader={
								CAPIArticle.config.switches.remoteHeader
							}
							contributionsServiceUrl={contributionsServiceUrl}
						/>
					</ElementContainer>

					<ElementContainer
						showSideBorders={true}
						borderColour={brandLine.primary}
						showTopBorder={false}
						padded={false}
						backgroundColour={brandBackground.primary}
						element="nav"
					>
						<Nav
							nav={NAV}
							format={{
								...format,
								theme: getCurrentPillar(CAPIArticle),
							}}
							subscribeUrl={
								CAPIArticle.nav.readerRevenueLinks.header
									.subscribe
							}
							edition={CAPIArticle.editionId}
						/>
					</ElementContainer>

					{NAV.subNavSections && (
						<ElementContainer
							backgroundColour={palette.background.article}
							padded={false}
							borderColour={palette.border.article}
							element="aside"
						>
							<Island deferUntil="idle">
								<SubNav
									subNavSections={NAV.subNavSections}
									currentNavLink={NAV.currentNavLink}
									format={format}
								/>
							</Island>
						</ElementContainer>
					)}

					<ElementContainer
						backgroundColour={palette.background.article}
						padded={false}
						showTopBorder={false}
						borderColour={palette.border.article}
					>
						<StraightLines
							count={4}
							cssOverrides={css`
								display: block;
							`}
						/>
					</ElementContainer>
				</SendToBack>
			</div>

			<main>
				{footballMatchUrl ? (
					<ContainerLayout
						showTopBorder={false}
						backgroundColour={palette.background.matchNav}
						borderColour={palette.border.headline}
						leftContent={
							<ArticleTitle
								format={format}
								tags={CAPIArticle.tags}
								sectionLabel={CAPIArticle.sectionLabel}
								sectionUrl={CAPIArticle.sectionUrl}
								guardianBaseURL={CAPIArticle.guardianBaseURL}
								badge={CAPIArticle.badge}
								isMatch={true}
							/>
						}
						leftColSize="wide"
						sideBorders={true}
						padContent={false}
						verticalMargins={false}
					>
						<Hide above="leftCol">
							<ArticleTitle
								format={format}
								tags={CAPIArticle.tags}
								sectionLabel={CAPIArticle.sectionLabel}
								sectionUrl={CAPIArticle.sectionUrl}
								guardianBaseURL={CAPIArticle.guardianBaseURL}
								badge={CAPIArticle.badge}
								isMatch={true}
							/>
						</Hide>

						<Island
							deferUntil="visible"
							clientOnly={true}
							placeholderHeight={230}
						>
							<GetMatchNav
								matchUrl={footballMatchUrl}
								format={format}
								headlineString={CAPIArticle.headline}
								tags={CAPIArticle.tags}
								webPublicationDateDeprecated={
									CAPIArticle.webPublicationDateDeprecated
								}
							/>
						</Island>
					</ContainerLayout>
				) : (
					<ElementContainer
						showTopBorder={false}
						backgroundColour={palette.background.header}
						borderColour={palette.border.headline}
					>
						<HeadlineGrid>
							<GridItem area="title">
								<ArticleTitle
									format={format}
									tags={CAPIArticle.tags}
									sectionLabel={CAPIArticle.sectionLabel}
									sectionUrl={CAPIArticle.sectionUrl}
									guardianBaseURL={
										CAPIArticle.guardianBaseURL
									}
									badge={CAPIArticle.badge}
								/>
							</GridItem>
							<GridItem area="headline">
								<div css={maxWidth}>
									{!footballMatchUrl && (
										<ArticleHeadline
											format={format}
											headlineString={
												CAPIArticle.headline
											}
											tags={CAPIArticle.tags}
											byline={CAPIArticle.author.byline}
											webPublicationDateDeprecated={
												CAPIArticle.webPublicationDateDeprecated
											}
											hasStarRating={
												!!CAPIArticle.starRating ||
												CAPIArticle.starRating === 0
											}
										/>
									)}
								</div>
								{CAPIArticle.starRating ||
								CAPIArticle.starRating === 0 ? (
									<div css={starWrapper}>
										<StarRating
											rating={CAPIArticle.starRating}
											size="large"
										/>
									</div>
								) : (
									<></>
								)}
							</GridItem>
						</HeadlineGrid>
					</ElementContainer>
				)}

				<ElementContainer
					showTopBorder={false}
					backgroundColour={palette.background.standfirst}
					borderColour={palette.border.standfirst}
				>
					<StandFirstGrid>
						<GridItem area="standfirst">
							<Standfirst
								format={format}
								standfirst={CAPIArticle.standfirst}
							/>
						</GridItem>
						<GridItem area="lastupdated">
							<Hide until="desktop">
								{CAPIArticle.blocks.length &&
									CAPIArticle.blocks[0].blockLastUpdated && (
										<ArticleLastUpdated
											format={format}
											lastUpdated={
												CAPIArticle.blocks[0]
													.blockLastUpdated
											}
										/>
									)}
							</Hide>
						</GridItem>
						<GridItem area="lines">
							<Hide from="desktop">
								<div css={sidePaddingDesktop}>
									<Lines
										cssOverrides={css`
											display: block;
										`}
										count={decideLineCount(format.design)}
										effect={decideLineEffect(
											format.design,
											format.theme,
										)}
										color={
											format.design ===
											ArticleDesign.LiveBlog
												? 'rgba(255, 255, 255, 0.4)'
												: undefined
										}
									/>
								</div>
							</Hide>
						</GridItem>
						<GridItem area="meta">
							<Hide from="desktop">
								<div css={sidePaddingDesktop}>
									<ArticleMeta
										branding={branding}
										format={format}
										pageId={CAPIArticle.pageId}
										webTitle={CAPIArticle.webTitle}
										author={CAPIArticle.author}
										tags={CAPIArticle.tags}
										primaryDateline={
											CAPIArticle.webPublicationDateDisplay
										}
										secondaryDateline={
											CAPIArticle.webPublicationSecondaryDateDisplay
										}
										isCommentable={
											CAPIArticle.isCommentable
										}
										discussionApiUrl={
											CAPIArticle.config.discussionApiUrl
										}
										shortUrlId={
											CAPIArticle.config.shortUrlId
										}
										ajaxUrl={CAPIArticle.config.ajaxUrl}
										showShareCount={
											CAPIArticle.config.switches
												.serverShareCounts
										}
									/>
								</div>
							</Hide>
						</GridItem>
					</StandFirstGrid>
				</ElementContainer>

				<ElementContainer
					showTopBorder={false}
					borderColour={palette.border.article}
					backgroundColour={palette.background.article}
				>
					<Hide until="desktop">
						<div
							css={css`
								height: ${space[4]}px;
							`}
						/>
					</Hide>
				</ElementContainer>

				{/* This div is used to contain the Toast */}
				<div>
					{format.design === ArticleDesign.LiveBlog && (
						<>
							{/* The Toast component is inserted into this div using a Portal */}
							<div
								id="toast-root"
								css={css`
									position: sticky;
									top: 0;
									${getZIndex('toast')};
									display: flex;
									justify-content: center;
								`}
							/>
							<Island clientOnly={true} deferUntil="idle">
								<Liveness
									pageId={CAPIArticle.pageId}
									webTitle={CAPIArticle.webTitle}
									ajaxUrl={CAPIArticle.config.ajaxUrl}
									filterKeyEvents={
										CAPIArticle.filterKeyEvents
									}
									format={format}
									enhanceTweetsSwitch={
										CAPIArticle.config.switches
											.enhanceTweets
									}
									onFirstPage={pagination.currentPage === 1}
									webURL={CAPIArticle.webURL}
									// We default to string here because the property is optional but we
									// know it will exist for all blogs
									mostRecentBlockId={
										CAPIArticle.mostRecentBlockId || ''
									}
									hasPinnedPost={!!CAPIArticle.pinnedPost}
								/>
							</Island>
						</>
					)}

					<ElementContainer
						showTopBorder={false}
						backgroundColour={palette.background.article}
						borderColour={palette.border.article}
						padded={false}
					>
						<LiveGrid>
							<GridItem area="media">
								<div css={maxWidth}>
									{footballMatchUrl && (
										<Island
											clientOnly={true}
											placeholderHeight={40}
										>
											<GetMatchTabs
												matchUrl={footballMatchUrl}
												format={format}
											/>
										</Island>
									)}
									{cricketMatchUrl && (
										<Island
											clientOnly={true}
											placeholderHeight={172}
										>
											<GetCricketScoreboard
												matchUrl={cricketMatchUrl}
												format={format}
											/>
										</Island>
									)}
									<MainMedia
										format={format}
										elements={CAPIArticle.mainMediaElements}
										adTargeting={adTargeting}
										host={host}
										pageId={CAPIArticle.pageId}
										webTitle={CAPIArticle.webTitle}
										ajaxUrl={CAPIArticle.config.ajaxUrl}
										switches={CAPIArticle.config.switches}
										isSensitive={
											CAPIArticle.config.isSensitive
										}
										isAdFreeUser={CAPIArticle.isAdFreeUser}
									/>
								</div>
							</GridItem>
							<GridItem area="info" element="aside">
								{/* Lines */}
								<Hide until="desktop">
									<div css={[maxWidth, sidePaddingDesktop]}>
										<Lines
											cssOverrides={css`
												display: block;
											`}
											count={decideLineCount(
												format.design,
											)}
											effect={decideLineEffect(
												format.design,
												format.theme,
											)}
										/>
									</div>
								</Hide>
								{/* Meta */}
								<Hide until="desktop">
									<div css={[maxWidth, sidePaddingDesktop]}>
										<ArticleMeta
											branding={branding}
											format={format}
											pageId={CAPIArticle.pageId}
											webTitle={CAPIArticle.webTitle}
											author={CAPIArticle.author}
											tags={CAPIArticle.tags}
											primaryDateline={
												CAPIArticle.webPublicationDateDisplay
											}
											secondaryDateline={
												CAPIArticle.webPublicationSecondaryDateDisplay
											}
											isCommentable={
												CAPIArticle.isCommentable
											}
											discussionApiUrl={
												CAPIArticle.config
													.discussionApiUrl
											}
											shortUrlId={
												CAPIArticle.config.shortUrlId
											}
											ajaxUrl={CAPIArticle.config.ajaxUrl}
											showShareCount={
												CAPIArticle.config.switches
													.serverShareCounts
											}
										/>
									</div>
								</Hide>
								{/* Key events */}
								<div
									css={[
										!footballMatchUrl && sticky,
										keyEventsMargins,
										sidePaddingDesktop,
									]}
								>
									<KeyEventsContainer
										format={format}
										keyEvents={CAPIArticle.keyEvents}
										filterKeyEvents={
											CAPIArticle.filterKeyEvents
										}
									/>
								</div>
								{/* Match stats */}
								{footballMatchUrl && (
									<Island
										deferUntil="visible"
										clientOnly={true}
										placeholderHeight={800}
									>
										<GetMatchStats
											matchUrl={footballMatchUrl}
											format={format}
										/>
									</Island>
								)}
							</GridItem>
							<GridItem area="body">
								<div id="maincontent" css={bodyWrapper}>
									{CAPIArticle.keyEvents.length ? (
										<Hide below="desktop">
											<Island deferUntil="visible">
												<FilterKeyEventsToggle
													filterKeyEvents={
														CAPIArticle.filterKeyEvents
													}
													id="filter-toggle-desktop"
												/>
											</Island>
										</Hide>
									) : (
										<></>
									)}
									<Accordion
										supportsDarkMode={false}
										accordionTitle="Live feed"
										context="liveFeed"
									>
										{CAPIArticle.keyEvents.length ? (
											<Hide above="desktop">
												<Island deferUntil="visible">
													<FilterKeyEventsToggle
														filterKeyEvents={
															CAPIArticle.filterKeyEvents
														}
														id="filter-toggle-mobile"
													/>
												</Island>
											</Hide>
										) : (
											<></>
										)}
										<ArticleContainer format={format}>
											{pagination.currentPage !== 1 && (
												<Pagination
													currentPage={
														pagination.currentPage
													}
													totalPages={
														pagination.totalPages
													}
													newest={pagination.newest}
													oldest={pagination.oldest}
													newer={pagination.newer}
													older={pagination.older}
													format={format}
												/>
											)}
											<ArticleBody
												format={format}
												blocks={CAPIArticle.blocks}
												pinnedPost={
													CAPIArticle.pinnedPost
												}
												adTargeting={adTargeting}
												host={host}
												pageId={CAPIArticle.pageId}
												webTitle={CAPIArticle.webTitle}
												ajaxUrl={
													CAPIArticle.config.ajaxUrl
												}
												section={
													CAPIArticle.config.section
												}
												switches={
													CAPIArticle.config.switches
												}
												isSensitive={
													CAPIArticle.config
														.isSensitive
												}
												isAdFreeUser={
													CAPIArticle.isAdFreeUser
												}
												shouldHideReaderRevenue={
													CAPIArticle.shouldHideReaderRevenue
												}
												tags={CAPIArticle.tags}
												isPaidContent={
													!!CAPIArticle.config
														.isPaidContent
												}
												contributionsServiceUrl={
													contributionsServiceUrl
												}
												contentType={
													CAPIArticle.contentType
												}
												sectionName={
													CAPIArticle.sectionName ||
													''
												}
												isPreview={
													CAPIArticle.config.isPreview
												}
												idUrl={
													CAPIArticle.config.idUrl ||
													''
												}
												isDev={
													!!CAPIArticle.config.isDev
												}
												onFirstPage={
													pagination.currentPage === 1
												}
											/>
											{pagination.totalPages > 1 && (
												<Pagination
													currentPage={
														pagination.currentPage
													}
													totalPages={
														pagination.totalPages
													}
													newest={pagination.newest}
													oldest={pagination.oldest}
													newer={pagination.newer}
													older={pagination.older}
													format={format}
												/>
											)}
											{showBodyEndSlot && (
												<Island clientOnly={true}>
													<SlotBodyEnd
														contentType={
															CAPIArticle.contentType
														}
														contributionsServiceUrl={
															contributionsServiceUrl
														}
														idApiUrl={
															CAPIArticle.config
																.idApiUrl
														}
														isMinuteArticle={
															CAPIArticle.pageType
																.isMinuteArticle
														}
														isPaidContent={
															CAPIArticle.pageType
																.isPaidContent
														}
														keywordsId={
															CAPIArticle.config
																.keywordIds
														}
														pageId={
															CAPIArticle.pageId
														}
														sectionId={
															CAPIArticle.config
																.section
														}
														sectionName={
															CAPIArticle.sectionName
														}
														shouldHideReaderRevenue={
															CAPIArticle.shouldHideReaderRevenue
														}
														stage={
															CAPIArticle.config
																.stage
														}
														tags={CAPIArticle.tags}
