const ShowcaseGrid = ({ children }: { children: React.ReactNode }) => (
	<div
		css={css`
			/* IE Fallback */
			display: flex;
			flex-direction: column;
			${until.leftCol} {
				margin-left: 0px;
			}
			${from.leftCol} {
				margin-left: 151px;
			}
			${from.wide} {
				margin-left: 230px;
			}

			@supports (display: grid) {
				display: grid;
				width: 100%;
				margin-left: 0;

				grid-column-gap: 10px;

				/*
					Explanation of each unit of grid-template-columns

					Left Column (220 - 1px border)
					Vertical grey border
					Main content
					Right Column
				*/
				${from.wide} {
					grid-template-columns: 219px 1px 1fr 300px;
					grid-template-areas:
						'title  border  headline    headline'
						'lines  border  media       media'
						'meta   border  media       media'
						'meta   border  standfirst  right-column'
						'.      border  body        right-column'
						'.      border  .           right-column';
				}

				${until.wide} {
					grid-template-columns: 140px 1px 1fr 300px;
					grid-template-areas:
						'title  border  headline    headline'
						'lines  border  media       media'
						'meta   border  media       media'
						'meta   border  standfirst  right-column'
						'.      border  body        right-column'
						'.      border  .           right-column';
				}

				/*
					Explanation of each unit of grid-template-columns

					Main content
					Right Column
				*/
				${until.leftCol} {
					grid-template-columns: 1fr 300px;
					grid-template-areas:
						'title      right-column'
						'headline   right-column'
						'standfirst right-column'
						'media      right-column'
						'lines      right-column'
						'meta       right-column'
						'body       right-column'
						'.          right-column';
				}

				${until.desktop} {
					grid-column-gap: 0px;
					grid-template-columns: 1fr; /* Main content */
					grid-template-areas:
						'title'
						'headline'
						'standfirst'
						'media'
						'lines'
						'meta'
						'body';
				}

				${until.tablet} {
					grid-column-gap: 0px;
					grid-template-columns: 1fr; /* Main content */
					grid-template-areas:
						'media'
						'title'
						'headline'
						'standfirst'
						'lines'
						'meta'
						'body';
				}
			}
		`}
	>
		{children}
	</div>
);

const maxWidth = css`
	${from.desktop} {
		max-width: 620px;
	}
`;

const stretchLines = css`
	${until.phablet} {
		margin-left: -20px;
		margin-right: -20px;
	}
	${until.mobileLandscape} {
		margin-left: -10px;
		margin-right: -10px;
	}
`;

const mainMediaWrapper = css`
	position: relative;
`;

const PositionHeadline = ({
	design,
	children,
}: {
	design: ArticleDesign;
	children: React.ReactNode;
}) => {
	switch (design) {
		case ArticleDesign.Interview:
			return (
				<div
					css={css`
						${from.leftCol} {
							margin-bottom: -100px;
						}
					`}
				>
					<div css={maxWidth}>{children}</div>
				</div>
			);
		default:
			return <div css={maxWidth}>{children}</div>;
	}
};

interface Props {
	CAPIArticle: CAPIArticleType;
	NAV: NavType;
	format: ArticleFormat;
}

export const ShowcaseLayout = ({
	CAPIArticle,
	NAV,
	format,
}: Props): JSX.Element => {
	const {
		config: { isPaidContent, host },
	} = CAPIArticle;

	const adTargeting: AdTargeting = buildAdTargeting({
		isAdFreeUser: CAPIArticle.isAdFreeUser,
		isSensitive: CAPIArticle.config.isSensitive,
		videoDuration: CAPIArticle.config.videoDuration,
		edition: CAPIArticle.config.edition,
		section: CAPIArticle.config.section,
		sharedAdTargeting: CAPIArticle.config.sharedAdTargeting,
		adUnit: CAPIArticle.config.adUnit,
	});

	const showBodyEndSlot =
		parse(CAPIArticle.slotMachineFlags || '').showBodyEnd ||
		CAPIArticle.config.switches.slotBodyEnd;

	// TODO:
	// 1) Read 'forceEpic' value from URL parameter and use it to force the slot to render
	// 2) Otherwise, ensure slot only renders if `CAPIArticle.config.shouldHideReaderRevenue` equals false.

	const seriesTag = CAPIArticle.tags.find(
		(tag) => tag.type === 'Series' || tag.type === 'Blog',
	);
	const showOnwardsLower = seriesTag && CAPIArticle.hasStoryPackage;

	const showComments = CAPIArticle.isCommentable;

	const { branding } =
		CAPIArticle.commercialProperties[CAPIArticle.editionId];

	const palette = decidePalette(format);

	const contributionsServiceUrl = getContributionsServiceUrl(CAPIArticle);

	return (
		<>
			{format.theme !== ArticleSpecial.Labs ? (
				<>
					<div>
						<Stuck>
							<ElementContainer
								showTopBorder={false}
								showSideBorders={false}
								padded={false}
								shouldCenter={false}
							>
								<HeaderAdSlot
									isAdFreeUser={CAPIArticle.isAdFreeUser}
									shouldHideAds={CAPIArticle.shouldHideAds}
									display={format.display}
								/>
							</ElementContainer>
						</Stuck>
						<SendToBack>
							<ElementContainer
								showTopBorder={false}
								showSideBorders={false}
								padded={false}
								backgroundColour={brandBackground.primary}
								element="header"
							>
								<Header
									edition={CAPIArticle.editionId}
									idUrl={CAPIArticle.config.idUrl}
									mmaUrl={CAPIArticle.config.mmaUrl}
									supporterCTA={
										CAPIArticle.nav.readerRevenueLinks
											.header.supporter
									}
									discussionApiUrl={
										CAPIArticle.config.discussionApiUrl
									}
									urls={
										CAPIArticle.nav.readerRevenueLinks
											.header
									}
									remoteHeader={
										CAPIArticle.config.switches.remoteHeader
									}
									contributionsServiceUrl={
										contributionsServiceUrl
									}
								/>
							</ElementContainer>
							<ElementContainer
								showSideBorders={true}
								borderColour={brandLine.primary}
								showTopBorder={false}
								padded={false}
								backgroundColour={brandBackground.primary}
								element="nav"
							>
								<Nav
									nav={NAV}
									format={{
										...format,
										theme: getCurrentPillar(CAPIArticle),
									}}
									subscribeUrl={
										CAPIArticle.nav.readerRevenueLinks
											.header.subscribe
									}
									edition={CAPIArticle.editionId}
								/>
							</ElementContainer>

							{NAV.subNavSections && (
								<ElementContainer
									backgroundColour={
										palette.background.article
									}
									padded={false}
									element="aside"
								>
									<Island deferUntil="idle">
										<SubNav
											subNavSections={NAV.subNavSections}
											currentNavLink={NAV.currentNavLink}
											format={format}
										/>
									</Island>
								</ElementContainer>
							)}

							<ElementContainer
								backgroundColour={palette.background.article}
								padded={false}
								showTopBorder={false}
							>
								<StraightLines
									count={4}
									cssOverrides={css`
										display: block;
									`}
								/>
							</ElementContainer>
						</SendToBack>
					</div>
				</>
			) : (
				// Else, this is a labs article so just show Nav and the Labs header
				<>
					<div>
						<Stuck zIndex="stickyAdWrapper">
							<ElementContainer
								showTopBorder={false}
								showSideBorders={false}
								padded={false}
							>
								<HeaderAdSlot
									isAdFreeUser={CAPIArticle.isAdFreeUser}
									shouldHideAds={CAPIArticle.shouldHideAds}
									display={format.display}
								/>
							</ElementContainer>
						</Stuck>
						<Stuck zIndex="stickyAdWrapperNav">
							<ElementContainer
								showSideBorders={true}
								borderColour={brandLine.primary}
								showTopBorder={false}
								padded={false}
								backgroundColour={brandBackground.primary}
