import { css } from '@emotion/react';
import { ArticleDesign, ArticleSpecial } from '@guardian/libs';
import type { ArticleFormat } from '@guardian/libs';
import {
	border,
	brandBackground,
	from,
	labs,
} from '@guardian/source-foundations';
import { buildAdTargeting } from '../../../lib/ad-targeting';
import { ArticleHeadline } from '../../components/ArticleHeadline';
import { ArticleTitle } from '../../components/ArticleTitle';
import { Caption } from '../../components/Caption';
import { ContainerLayout } from '../../components/ContainerLayout';
import { ElementContainer } from '../../components/ElementContainer';
import { Island } from '../../components/Island';
import { LabsHeader } from '../../components/LabsHeader.importable';
import { MainMedia } from '../../components/MainMedia';
import { Nav } from '../../components/Nav/Nav';
import { decidePalette } from '../../lib/decidePalette';
import { getZIndex } from '../../lib/getZIndex';
import { getCurrentPillar } from '../../lib/layoutHelpers';
import { Stuck } from '../lib/stickiness';

const hasMainMediaStyles = css`
	height: 80vh;
	/**
    80vh is normally enough but don't let the content shrink vertically too
    much just in case
    */
	min-height: 25rem;
	${from.desktop} {
		height: 100vh;
		min-height: 31.25rem;
	}
	${from.wide} {
		min-height: 50rem;
	}
`;

interface Props {
	CAPIArticle: CAPIArticleType;
	NAV: NavType;
	format: ArticleFormat;
}

const decideCaption = (mainMedia: ImageBlockElement): string => {
	const caption = [];
	if (mainMedia && mainMedia.data && mainMedia.data.caption)
		caption.push(mainMedia.data.caption);
	if (
		mainMedia &&
		mainMedia.displayCredit &&
		mainMedia.data &&
		mainMedia.data.credit
	)
		caption.push(mainMedia.data.credit);
	return caption.join(' ');
};

const Box = ({
	palette,
	children,
}: {
	palette: Palette;
	children: React.ReactNode;
}) => (
	<div
		css={css`
			/*
				This pseudo css shows a black box to the right of the headline
				so that the black background of the inverted text stretches
				all the way right. But only from mobileLandscape because below
				that we want to show a gap. To work properly it needs to wrap
				the healine so it inherits the correct height based on the length
				of the headline text
			*/
			${from.mobileLandscape} {
				position: relative;
				:after {
					content: '';
					display: block;
					position: absolute;
					width: 50%;
					right: 0;
					background-color: ${palette.background.headline};
					${getZIndex('immersiveBlackBox')}
					top: 0;
					bottom: 0;
				}
			}
		`}
	>
		{children}
	</div>
);

export const ImmersiveHeader = ({
	CAPIArticle,
	NAV,
	format,
}: Props): JSX.Element => {
	const {
		config: { host },
	} = CAPIArticle;

	const adTargeting: AdTargeting = buildAdTargeting({
		isAdFreeUser: CAPIArticle.isAdFreeUser,
		isSensitive: CAPIArticle.config.isSensitive,
		videoDuration: CAPIArticle.config.videoDuration,
		edition: CAPIArticle.config.edition,
		section: CAPIArticle.config.section,
		sharedAdTargeting: CAPIArticle.config.sharedAdTargeting,
		adUnit: CAPIArticle.config.adUnit,
	});

	// TODO:
	// 1) Read 'forceEpic' value from URL parameter and use it to force the slot to render
	// 2) Otherwise, ensure slot only renders if `CAPIArticle.config.shouldHideReaderRevenue` equals false.

	const mainMedia = CAPIArticle.mainMediaElements[0] as ImageBlockElement;
	const captionText = decideCaption(mainMedia);

	const HEADLINE_OFFSET = mainMedia ? 120 : 0;

	const LeftColCaption = () => (
		<div
			css={css`
				margin-top: ${HEADLINE_OFFSET}px;
				position: absolute;
				margin-left: 20px;
			`}
		>
			<Caption
				captionText={captionText}
				format={format}
				shouldLimitWidth={true}
				isLeftCol={true}
			/>
		</div>
	);

	const palette = decidePalette(format);

	return (
		<>
			<header
				css={css`
					background-color: ${palette.background.article};
				`}
			>
				<div
					css={[
						mainMedia && hasMainMediaStyles,
						css`
							display: flex;
							flex-direction: column;
						`,
					]}
				>
					<div
						css={css`
							${getZIndex('headerWrapper')}
							order: 0;
						`}
					>
						<ElementContainer
							showSideBorders={false}
							showTopBorder={false}
							padded={false}
							backgroundColour={brandBackground.primary}
							element="nav"
						>
							<Nav
								format={{
									...format,
									theme: getCurrentPillar(CAPIArticle),
								}}
								nav={NAV}
								subscribeUrl={
									CAPIArticle.nav.readerRevenueLinks.header
										.subscribe
								}
								edition={CAPIArticle.editionId}
							/>
						</ElementContainer>
					</div>

					{format.theme === ArticleSpecial.Labs && (
						<Stuck>
							<ElementContainer
								showSideBorders={true}
								showTopBorder={false}
								backgroundColour={labs[400]}
								borderColour={border.primary}
								sectionId="labs-header"
							>
								<Island deferUntil="idle">
									<LabsHeader />
								</Island>
							</ElementContainer>
						</Stuck>
					)}

					<MainMedia
						format={format}
						elements={CAPIArticle.mainMediaElements}
						adTargeting={adTargeting}
						starRating={
							format.design === ArticleDesign.Review &&
							CAPIArticle.starRating
								? CAPIArticle.starRating
								: undefined
						}
						host={host}
						hideCaption={true}
						pageId={CAPIArticle.pageId}
						webTitle={CAPIArticle.webTitle}
						ajaxUrl={CAPIArticle.config.ajaxUrl}
						switches={CAPIArticle.config.switches}
						isAdFreeUser={CAPIArticle.isAdFreeUser}
						isSensitive={CAPIArticle.config.isSensitive}
					/>
				</div>
				{mainMedia && (
					<>
						<div
							css={css`
								margin-top: -${HEADLINE_OFFSET}px;
								/*
                        This z-index is what ensures the headline title text shows above main media. For
                        the actual headline we set the z-index deeper in ArticleHeadline itself so that
                        the text appears above the pseudo Box element
                    */
								position: relative;
								${getZIndex('articleHeadline')};
							`}
						>
							<ContainerLayout
								verticalMargins={false}
								padContent={false}
								padSides={false}
								leftContent={<LeftColCaption />}
							>
								<ArticleTitle
									format={format}
									tags={CAPIArticle.tags}
									sectionLabel={CAPIArticle.sectionLabel}
									sectionUrl={CAPIArticle.sectionUrl}
									guardianBaseURL={
										CAPIArticle.guardianBaseURL
									}
									badge={CAPIArticle.badge}
								/>
							</ContainerLayout>
							<Box palette={palette}>
								<ContainerLayout
									verticalMargins={false}
									padContent={false}
									padSides={false}
								>
									<ArticleHeadline
										format={format}
										headlineString={CAPIArticle.headline}
										tags={CAPIArticle.tags}
										byline={CAPIArticle.author.byline}
										webPublicationDateDeprecated={
											CAPIArticle.webPublicationDateDeprecated
										}
										hasStarRating={
											!!CAPIArticle.starRating ||
											CAPIArticle.starRating === 0
										}
									/>
								</ContainerLayout>
							</Box>
						</div>
					</>
				)}
			</header>
		</>
	);
};

import { css } from '@emotion/react';
import type { ZIndex } from '../../lib/getZIndex';
import { getZIndex, getZIndexImportant } from '../../lib/getZIndex';

type Props = {
	children?: React.ReactNode;
};

type StuckProps = {
	children?: React.ReactNode;
	zIndex?: ZIndex;
};

const stickyStyles = css`
	position: sticky;
	top: 0;
`;

const addZindex = (zIndex: ZIndex = 'stickyAdWrapper') => css`
	${getZIndex(zIndex)}
`;

const whiteBackground = css`
	background-color: white;
`;

const headerWrapper = css`
	position: relative;
	${getZIndex('headerWrapper')}
`;

// The css overrides here are necessary because ad-takeovers can inject css that breaks the banner
// Overflow is visible because puzzles banner needs it to render correctly
const bannerWrapper = css`
	/* stylelint-disable-next-line declaration-no-important */
	position: fixed !important;
	bottom: 0;
	${getZIndexImportant('banner')}
	max-height: 100vh;
	overflow: visible;
	/* stylelint-disable-next-line declaration-no-important */
	width: 100% !important;
	/* stylelint-disable-next-line declaration-no-important */
	background: none !important;
	/* stylelint-disable-next-line declaration-no-important */
	top: auto !important;
`;

export const Stuck = ({ children, zIndex }: StuckProps) => (
	<div css={[stickyStyles, addZindex(zIndex), whiteBackground]}>
		{children}
