import { getBrazeUuid } from '../getBrazeUuid';
import { hasRequiredConsents } from './hasRequiredConsents';

type SuccessResult = {
	isSuccessful: true;
	data: ResultData;
};

type FailureResult = {
	isSuccessful: false;
	failure: {
		field: string;
		data: any;
	};
	data: ResultData;
};

type DependenciesResult = SuccessResult | FailureResult;

type ResultData = { [key: string]: string | boolean };

type DependencyResult = {
	name: string;
	value: Promise<string | boolean | null | undefined | void>;
};
const buildFailureResponse = (name: string, value: any, data: ResultData) => ({
	isSuccessful: false,
	failure: {
		field: name,
		data: value,
	},
	data,
});

const buildDependencies = (
	isSignedIn: boolean,
	idApiUrl: string,
): DependencyResult[] => {
	return [
		{
			name: 'brazeSwitch',
			value: Promise.resolve(window.guardian.config.switches.brazeSwitch),
		},
		{
			name: 'apiKey',
			value: Promise.resolve(window.guardian.config.page.brazeApiKey),
		},
		{
			name: 'brazeUuid',
			value: isSignedIn ? getBrazeUuid(idApiUrl) : Promise.resolve(null),
		},
		{
			name: 'consent',
			value: hasRequiredConsents(),
		},
		{
			name: 'isNotPaidContent',
			value: Promise.resolve(!window.guardian.config.page.isPaidContent),
		},
	];
};

const checkBrazeDependencies = async (
	isSignedIn: boolean,
	idApiUrl: string,
): Promise<DependenciesResult> => {
	const dependencies = buildDependencies(isSignedIn, idApiUrl);

	const data: ResultData = {};

	// I think we could possibly clean this up a bit when we can use
	// Promise.allSettled reliably (it's not available in our current Node
	// version and polyfill.io doesn't have a polyfill yet).
	for (const { name, value } of dependencies) {
		try {
			const result = await value;

			if (result) {
				data[name] = result;
			} else {
				return buildFailureResponse(name, result, data);
			}
		} catch (e) {
			const error = e as Error;
			return buildFailureResponse(
				name,
				error && error.message ? error.message : error,
				data,
			);
		}
	}

	return {
		isSuccessful: true,
		data,
	};
};

export { checkBrazeDependencies };

const FORCE_BRAZE_ALLOWLIST = [
	'preview.gutools.co.uk',
	'preview.code.dev-gutools.co.uk',
	process.env.HOSTNAME || 'localhost',
	'm.thegulocal.com',
];

type Meta = {
	dataFromBraze: {
		[key: string]: string;
	};
	logImpressionWithBraze: () => void;
	logButtonClickWithBraze: (id: number) => void;
};

export const getBrazeMetaFromUrlFragment = (): Meta | null => {
	if (window.location.hash) {
		// This is intended for use on development domains for preview purposes.
		// It won't run in PROD.

		const key = 'force-braze-message';

		const hashString = window.location.hash;

		if (hashString.includes(key)) {
			if (!FORCE_BRAZE_ALLOWLIST.includes(window.location.hostname)) {
				console.log(`${key} is not supported on this domain`);
				return null;
			}

			const forcedMessage = hashString.slice(
				hashString.indexOf(`${key}=`) + key.length + 1,
				hashString.length,
			);

			try {
				const dataFromBraze = JSON.parse(
					decodeURIComponent(forcedMessage),
				);

				return {
					dataFromBraze,
					logImpressionWithBraze: () => {},
					logButtonClickWithBraze: () => {},
				};
			} catch (e) {
				const error = e as Error;
				// Parsing failed. Log a message and fall through.

				console.log(`There was an error with ${key}: `, error.message);
			}
		}
	}

	return null;
};

