import { joinUrl } from '../../lib/joinUrl';

// GET http://discussion.guardianapis.com/discussion-api/comment/3519111/context
// {
//     status: 'ok',
//     commentId: 3519111,
//     commentAncestorId: 3519111,
//     discussionKey: '/p/27y27',
//     discussionWebUrl:
//         'https://www.theguardian.com/commentisfree/cifamerica/2009/may/14/washington-post-torture-libel',
//     discussionApiUrl:
//         'https://discussion.guardianapis.com/discussion-api/discussion//p/27y27?orderBy=oldest&pageSize=20&page=1',
//     orderBy: 'oldest',
//     pageSize: 20,
//     page: 1,
// };

type CommentContextType = {
	status: 'ok' | 'error';
	commentId: number;
	commentAncestorId: number;
	discussionKey: string;
	discussionWebUrl: string;
	discussionApiUrl: string;
	orderBy: OrderByType;
	pageSize: PageSizeType; // TODO: Review these https://trello.com/c/7v4VDNY0/1326-review-page-size-values
	page: number;
};

type OrderByType = 'newest' | 'oldest' | 'recommendations';
type ThreadsType = 'collapsed' | 'expanded' | 'unthreaded';
type PageSizeType = 25 | 50 | 100;

interface FilterOptions {
	orderBy: OrderByType;
	pageSize: PageSizeType;
	threads: ThreadsType;
}

const objAsParams = (obj: { [key: string]: any }): string => {
	const params = Object.keys(obj)
		.map((key) => {
			return `${key}=${obj[key]}`;
		})
		.join('&');

	return `?${params}`;
};

const initFiltersFromLocalStorage = (): FilterOptions => {
	let threads: { [key: string]: ThreadsType } | undefined;
	let pageSize: { [key: string]: PageSizeType } | undefined;
	let orderBy: { [key: string]: OrderByType } | undefined;

	try {
		// Try to read from local storage
		orderBy = JSON.parse(
			localStorage.getItem('gu.prefs.discussion.order') || '{}',
		);
		threads = JSON.parse(
			localStorage.getItem('gu.prefs.discussion.threading') || '{}',
		);
		pageSize = JSON.parse(
			localStorage.getItem('gu.prefs.discussion.pagesize') || '{}',
		);
	} catch (error) {
		// Sometimes it's not possible to access localStorage, we accept this and don't want to
		// capture these errors
		return {
			orderBy: 'newest',
			pageSize: 25,
			threads: 'collapsed',
		};
	}

	// If we found something in LS, use it, otherwise return defaults
	return {
		orderBy: orderBy && orderBy.value ? orderBy.value : 'newest',
		threads: threads && threads.value ? threads.value : 'collapsed',
		pageSize: pageSize && pageSize.value ? pageSize.value : 25,
	};
};

const buildParams = (filters: FilterOptions) => {
	return {
		// Frontend uses the 'recommendations' key to store this options but the api expects
		// 'mostRecommended' so we have to map here to support both
		orderBy:
			filters.orderBy === 'recommendations'
				? 'mostRecommended'
				: filters.orderBy,
		pageSize: filters.pageSize,
		displayThreaded:
			filters.threads === 'collapsed' || filters.threads === 'expanded',
	};
};

export const getCommentContext = async (
	ajaxUrl: string,
	commentId: number,
): Promise<CommentContextType> => {
	const url = joinUrl([ajaxUrl, 'comment', commentId.toString(), 'context']);
	const filters = initFiltersFromLocalStorage();
	const params = buildParams(filters);

	return fetch(url + objAsParams(params))
		.then((response) => {
			if (!response.ok) {
				throw Error(
					response.statusText ||
						`getCommentContext | An api call returned HTTP status ${response.status}`,
				);
			}
			return response;
		})
		.then((response) => response.json())
		.catch((error) => {
			window.guardian.modules.sentry.reportError(
				error,
				'get-comment-page',
			);
		});
};

import { getCookie, getLocale, storage } from '@guardian/libs';
import type { CountryCode } from '@guardian/libs';

const COUNTRY_CODE_KEY = 'GU_geo_country';
const COUNTRY_CODE_KEY_OVERRIDE = 'gu.geo.override';
/*
	Memoizes the location
 */
let locale: CountryCode | null;

/*
	Can be used to override country code and sets it in localStorage
 */
export const overrideCountryCode = (countryCode: CountryCode): void => {
	if (countryCode) {
		storage.local.set(COUNTRY_CODE_KEY_OVERRIDE, countryCode);
		locale = countryCode;
	}
};

/*
	This method can be used as a non async way of getting the country code
	after getLocaleCode has been called.
 */
export const getCountryCodeSync = (): CountryCode | null => {
	return (
		locale ||
		storage.local.get(COUNTRY_CODE_KEY_OVERRIDE) ||
		getCookie({ name: COUNTRY_CODE_KEY, shouldMemoize: true })
	);
};

/*
	This method returns the location of the user from guardian/libs getLocale
	It will return fastly's 'GU_geo_country' if it exists,
	or an overridden geolocation from 'gu.geo.override' localStorage
	or if none of those exists, it will call the geo endpoint to fetch it and set it in `GU_geo_country`
 */
export const getLocaleCode = async (): Promise<CountryCode | null> => {
	return getLocale()
		.then((countryCode) => {
			locale = countryCode;
			return countryCode;
		})
		.catch((error) => {
			console.log(`Error getting location from libs/getLocale`);
			window.guardian.modules.sentry.reportError(
				error,
				'get-country-code',
			);
			locale = getCountryCodeSync();
			return locale;
		});
};

