import { joinUrl } from '../../lib/joinUrl';

function checkForErrors(response: Response) {
	if (!response.ok) {
		throw Error(
			response.statusText ||
				`getUser | An api call returned HTTP status ${response.status}`,
		);
	}
	return response;
}

const callApi = (url: string) => {
	return fetch(url, {
		credentials: 'include',
	})
		.then(checkForErrors)
		.then((response) => response.json());
};

export const getUser = async (ajaxUrl: string): Promise<UserProfile | void> => {
	const url = joinUrl([ajaxUrl, 'profile/me?strict_sanctions_check=false']);
	return callApi(url)
		.catch((error) => {
			window.guardian.modules.sentry.reportError(error, 'get-user');
		})
		.then((json: { userProfile: UserProfile }) => json.userProfile)
		.catch((error) => {
			window.guardian.modules.sentry.reportError(error, 'get-user');
		});
};

/**
 * How do I get a z-index for my new item?
 *
 * Decide a meaningful name for your item and then insert it
 * in the indices array below. The higher up your item visually,
 * then the higher it will be in the z-index stack,
 * 'higher' means _earlier_ in the array.
 *
 * Eg. stickyAdWrapper will be given a higher z-index than bodyArea
 *
 * Once inserted in the array, use getZIndex() to return the css
 *
 * Eg.
 *
 * import { getZIndex } from './getZIndex';
 *
 * const myCss = css`
 *    color: blue;
 *    ${getZIndex('TheGuardian')}
 * `;
 *
 * As new items are added, all z-indexes are adjusted
 */
const indices = [
	// Modals will go here at the top

	// Sticky video and button need to be above everything
	'sticky-video-button',
	'sticky-video',
	'banner',
	'dropdown',
	'burger',
	'expanded-veggie-menu-wrapper',
	'expanded-veggie-menu',

	// Headers with sticky ads
	'stickyAdWrapperLabsHeader',
	'stickyAdWrapper',
	'stickyAdWrapperNav',

	// Edition selector in nav - needs to be below stickyAdWrapper
	'editionDropdown',

	// Liveblog toast
	'toast',

	// Onwards Carousel (Related content etc)
	'onwardsCarousel',

	// Search link should be above The Guardian svg
	'searchHeaderLink',
	'TheGuardian',

	// Wrapper after nav stuff
	'headerWrapper',

	// Article headline (should be above main media)
	'articleHeadline',
	'immersiveBlackBox',

	// Body
	'bodyArea',
	'rightColumnArea',

	// Main media
	'mainMedia',

	// Nested links in cards should sit above the main card link
	// See: https://www.sarasoueidan.com/blog/nested-links/
	'card-nested-link',
	'card-link',
] as const;

// Implementation code - you don't need to change this to get a new index
export type ZIndex = typeof indices[number];

const decideIndex = (name: ZIndex): number | null => {
	const index = indices.indexOf(name);
	if (index === -1) return null; // indexOf returns -1 if there is no match
	return indices.length - index; // reverse the indices: last item gets 1
};

export const getZIndex = (zIndex: ZIndex): string =>
	`z-index: ${decideIndex(zIndex)};`;

export const getZIndexImportant = (zIndex: ZIndex): string =>
	`z-index: ${decideIndex(zIndex)} !important;`;

import { cmp } from '@guardian/consent-management-platform';
import { getPrivacyFramework } from './getPrivacyFramework';

const newPrivacyLinkName = 'privacy-settings';

export const injectPrivacySettingsLink = (): void => {
	const privacyLink = document.querySelector('a[data-link-name=privacy]');

	if (
		!document.querySelector(`a[data-link-name=${newPrivacyLinkName}]`) &&
		privacyLink
	) {
		const privacyLinkListItem = privacyLink.parentElement;

		if (privacyLinkListItem) {
			getPrivacyFramework()
				.then((framework) => {
					const newPrivacyLink = privacyLink.cloneNode(
						false,
					) as Element;

					newPrivacyLink.setAttribute(
						'data-link-name',
						newPrivacyLinkName,
					);
					newPrivacyLink.setAttribute('href', '#');
					newPrivacyLink.innerHTML = framework.ccpa
						? 'California resident â€“ Do Not Sell'
						: 'Privacy settings';

					const newPrivacyLinkListItem =
						privacyLinkListItem.cloneNode(false) as Element;

					newPrivacyLinkListItem.appendChild(newPrivacyLink);

					privacyLinkListItem.insertAdjacentElement(
						'beforebegin',
						newPrivacyLinkListItem,
					);

					newPrivacyLink.addEventListener(
						'click',
						cmp.showPrivacyManager,
					);
				})
				.catch((e) => console.error(`privacy settings - error: ${e}`));
		}
	}
};

// https://stackoverflow.com/a/5624139
function hexToRgb(hex: string): {
	r: number;
	g: number;
	b: number;
} | null {
	// Expand shorthand form (e.g. "03F") to full form (e.g. "0033FF")
	const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
	const fullHex = hex.replace(shorthandRegex, (m, r, g, b) => {
		return `${r}${r}${g}${g}${b}${b}`;
	});

	const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(fullHex);
	return result
		? {
				r: parseInt(result[1], 16),
				g: parseInt(result[2], 16),
				b: parseInt(result[3], 16),
		  }
		: null;
}

function getBrightness(colour: string): number {
	// http://www.w3.org/TR/AERT#color-contrast
	const rgb = hexToRgb(colour);
	return rgb ? (rgb.r * 299 + rgb.g * 587 + rgb.b * 114) / 1000 : 0;
}

// Based on https://github.com/bgrins/TinyColor
export const isLight = (colour: string): boolean => {
	if (!colour.startsWith('#')) {
		return getBrightness(`#${colour}`) > 128;
	}
	return getBrightness(colour) > 128;
};

import { ArticleDesign, ArticlePillar } from '@guardian/libs';
