// This is taken from https://github.com/guardian/libs/blob/main/src/loadScript.ts, we see type errors when
// using the library

export const loadScript = (
	src: string,
	props?: Omit<Partial<HTMLScriptElement>, 'src' | 'onload' | 'onerror'>,
): Promise<Event | undefined> =>
	new Promise((resolve, reject) => {
		// creating this before the check below allows us to compare the resolved `src` values
		const script = document.createElement('script');
		script.src = src;

		// dont inject 2 scripts with the same src
		if (
			Array.from(document.scripts).some(
				({ src: scriptSrc }) => script.src === scriptSrc,
			)
		) {
			return resolve(undefined);
		}

		Object.assign(script, props);

		script.onload = resolve;
		script.onerror = () => {
			reject(new Error(`Failed to load script ${src}`));
		};

		const ref = document.scripts[0];
		ref.parentNode?.insertBefore(script, ref);
	});

import fetch from 'node-fetch';

type Field<T> = Array<{ name: string; value: T }>;

const prepare = <T extends string | number>(
	obj?: Record<string, T>,
): Field<T> | undefined =>
	obj && Object.entries(obj).map(([name, value]) => ({ name, value }));

/**
 * Record a log. Runs server-side.
 * Can be inspected via BigQuery for further analysis.
 * Ensure data protection is involved if capturing
 * any personally identifiable information (PII).
 *
 * ## Good candidate for @guardian/libs
 *
 * @param options.properties Textual values to record.
 * @param options.metrics Numeric values to record.
 */
const recordLog = ({
	label,
	isDev,
	properties,
	metrics,
}: {
	label: string;
	isDev: boolean;
	properties?: Record<string, string>;
	metrics?: Record<string, number>;
}) => {
	const data: {
		label: string;
		properties?: Field<string>;
		metrics?: Field<number>;
	} = {
		label,
		properties: prepare(properties),
		metrics: prepare(metrics),
	};

	const endpoint = isDev
		? 'https://logs.guardianapis.com/log'
		: 'https://logs.code.dev-guardianapis.com/log';

	void fetch(endpoint, {
		method: 'POST',
		body: JSON.stringify(data),
	});
};
export { recordLog };

import { initPerf } from '../browser/initPerf';
import { record } from '../browser/ophan/ophan';

export type MaybeFC = React.FC | null;
type ShowMessage<T> = (meta: T) => MaybeFC;

interface ShouldShow<T> {
	show: true;
	meta: T;
}
interface ShouldNotShow {
	show: false;
}
export type CanShowResult<T> = ShouldShow<T> | ShouldNotShow;

export type Candidate<T> = {
	id: string;
	show: ShowMessage<T>;
	canShow: () => Promise<CanShowResult<T>>;
};

export type CandidateConfig<T> = {
	candidate: Candidate<T>;
	timeoutMillis: number | null;
	reportTiming?: boolean;
};

type CandidateConfigWithTimeout<T> = CandidateConfig<T> & {
	cancelTimeout: () => void;
};

export type SlotConfig = {
	candidates: CandidateConfig<any>[];
	name: string;
};

const recordMessageTimeoutInOphan = (candidateId: string, slotName: string) =>
	record({
		component: `${slotName}-picker-timeout-dcr`,
		value: candidateId,
	});

const timeoutify = <T>(
	candidateConfig: CandidateConfig<T>,
	slotName: string,
): CandidateConfigWithTimeout<T> => {
	let timer: number | undefined;

	const canShow = (): Promise<CanShowResult<T>> =>
		new Promise((resolve) => {
			const perfName = `messagePicker-canShow-${candidateConfig.candidate.id}`;
			const canShowTiming = initPerf(perfName);
			canShowTiming.start();

			if (candidateConfig.timeoutMillis) {
				timer = window.setTimeout(() => {
					recordMessageTimeoutInOphan(
						candidateConfig.candidate.id,
						slotName,
					);
					resolve({ show: false });
				}, candidateConfig.timeoutMillis);
			}

			candidateConfig.candidate
				.canShow()
				.then((result) => {
					resolve(result);

					const canShowTimeTaken = canShowTiming.end();

					if (candidateConfig.reportTiming) {
						record({
							component: perfName,
							value: canShowTimeTaken,
						});
					}
				})
				.catch((e) =>
					console.error(`timeoutify candidate - error: ${e}`),
				);
		});

	const cancelTimeout = () => timer && clearTimeout(timer);

	return {
		...candidateConfig,
		candidate: {
			...candidateConfig.candidate,
			canShow,
		},
		cancelTimeout,
	};
};

const clearAllTimeouts = (messages: CandidateConfigWithTimeout<any>[]) =>
	messages.map((m) => m.cancelTimeout());

const defaultShow = () => null;

interface PendingMessage<T> {
	candidateConfig: CandidateConfigWithTimeout<T>;
	canShow: Promise<CanShowResult<T>>;
}

interface WinningMessage<T> {
	meta: T;
	candidate: Candidate<T>;
}

export const pickMessage = ({
	candidates,
	name,
}: SlotConfig): Promise<() => MaybeFC> =>
	new Promise((resolve) => {
		const candidateConfigsWithTimeout = candidates.map((c) =>
			timeoutify(c, name),
		);
		const results: PendingMessage<any>[] = candidateConfigsWithTimeout.map(
			(candidateConfig) => ({
				candidateConfig,
				canShow: candidateConfig.candidate.canShow(),
			}),
		);

		const winnerResult = results.reduce<
			Promise<WinningMessage<any> | null>
		>(async (winningMessageSoFar, { candidateConfig, canShow }) => {
			if (await winningMessageSoFar) {
				return winningMessageSoFar;
			}

			const result = await canShow;
			candidateConfig.cancelTimeout();
			if (result.show) {
				return {
					candidate: candidateConfig.candidate,
					meta: result.meta,
				};
			}

			return winningMessageSoFar;
		}, Promise.resolve(null));

		winnerResult
			.then((winner) => {
				clearAllTimeouts(candidateConfigsWithTimeout);

				if (winner === null) {
					resolve(defaultShow);
				} else {
					const { candidate, meta } = winner;
					resolve(() => candidate.show(meta));
				}
			})
			.catch((e) => console.error(`pickMessage winner - error: ${e}`));
	});

import type { CountryCode } from '@guardian/libs';
import { getCookie, removeCookie, setCookie } from '@guardian/libs';
import { setAlreadyVisited } from './alreadyVisited';
import {
	HIDE_SUPPORT_MESSAGING_COOKIE,
	RECURRING_CONTRIBUTOR_COOKIE,
	SUPPORT_ONE_OFF_CONTRIBUTION_COOKIE,
	SUPPORT_RECURRING_CONTRIBUTOR_ANNUAL_COOKIE,
	SUPPORT_RECURRING_CONTRIBUTOR_MONTHLY_COOKIE,
} from './contributions';
import { getLocaleCode, overrideCountryCode } from './getCountryCode';

const readerRevenueCookies = [
	HIDE_SUPPORT_MESSAGING_COOKIE,
	RECURRING_CONTRIBUTOR_COOKIE,
	SUPPORT_RECURRING_CONTRIBUTOR_MONTHLY_COOKIE,
	SUPPORT_RECURRING_CONTRIBUTOR_ANNUAL_COOKIE,
	SUPPORT_ONE_OFF_CONTRIBUTION_COOKIE,
];

const clearEpicViewLog = (): void =>
	localStorage.removeItem('gu.contributions.views');

const clearBannerLastClosedAt = (): void => {
	localStorage.removeItem('gu.prefs.engagementBannerLastClosedAt');
	localStorage.removeItem('gu.prefs.subscriptionBannerLastClosedAt');
	localStorage.removeItem('gu.noRRBannerTimestamp');
};

const fakeOneOffContributor = (): void =>
	setCookie({
		name: SUPPORT_ONE_OFF_CONTRIBUTION_COOKIE,
		value: Date.now().toString(),
	});

const MULTIVARIATE_ID_COOKIE = 'GU_mvt_id';
const MAX_CLIENT_MVT_ID = 1000000;
const incrementMvtCookie = (): void => {
	const mvtId = parseInt(
		getCookie({ name: MULTIVARIATE_ID_COOKIE }) || '10',
		10,
	);
	if (mvtId) {
		if (mvtId === MAX_CLIENT_MVT_ID) {
			// Wrap back to 1 if it would exceed the max
			setCookie({ name: MULTIVARIATE_ID_COOKIE, value: '1' });
		} else {
			setCookie({ name: MULTIVARIATE_ID_COOKIE, value: `${mvtId + 1}` });
		}
	}
};
const decrementMvtCookie = (): void => {
	const mvtId = parseInt(
		getCookie({ name: MULTIVARIATE_ID_COOKIE }) || '10',
		10,
	);
	if (mvtId) {
		if (mvtId === 0) {
			// Wrap back to max if it would be less than 0
			setCookie({
				name: MULTIVARIATE_ID_COOKIE,
				value: MAX_CLIENT_MVT_ID.toString(),
			});
		} else {
			setCookie({ name: MULTIVARIATE_ID_COOKIE, value: `${mvtId - 1}` });
		}
	}
};

const clearCommonReaderRevenueStateAndReload = (
	asExistingSupporter: boolean,
	shouldHideReaderRevenue: boolean,
): void => {
	if (shouldHideReaderRevenue) {
		alert(
			'This page has "Prevent membership/contribution appeals" ticked in Composer. Please try a different page',
		);
		return;
	}

	readerRevenueCookies.forEach((cookie) => removeCookie({ name: cookie }));
	clearEpicViewLog();

	if (asExistingSupporter) {
		fakeOneOffContributor();
	}

	window.location.reload();
};

const showMeTheEpic = (
	asExistingSupporter = false,
	shouldHideReaderRevenue: boolean,
): void => {
	clearCommonReaderRevenueStateAndReload(
		asExistingSupporter,
		shouldHideReaderRevenue,
	);
};

const showMeTheBanner = (
	asExistingSupporter = false,
	shouldHideReaderRevenue: boolean,
): void => {
	clearBannerLastClosedAt();
	setAlreadyVisited(2);
	clearCommonReaderRevenueStateAndReload(
		asExistingSupporter,
		shouldHideReaderRevenue,
	);
};

const showNextVariant = (
	asExistingSupporter = false,
	shouldHideReaderRevenue: boolean,
): void => {
	incrementMvtCookie();
	clearCommonReaderRevenueStateAndReload(
		asExistingSupporter,
		shouldHideReaderRevenue,
	);
};

const showPreviousVariant = (
	asExistingSupporter = false,
	shouldHideReaderRevenue: boolean,
): void => {
	decrementMvtCookie();
	clearCommonReaderRevenueStateAndReload(
		asExistingSupporter,
		shouldHideReaderRevenue,
	);
};

const changeGeolocation = (
	asExistingSupporter = false,
	shouldHideReaderRevenue: boolean,
): void => {
	getLocaleCode()
		.then((current) => {
			const geo = window.prompt(
				`Enter two-letter geolocation code (e.g. GB, US, AU). Current is ${current}.`,
			);
			if (geo === 'UK') {
				alert(
					`'UK' is not a valid geolocation - please use 'GB' instead!`,
				);
			} else if (geo) {
				overrideCountryCode(geo as CountryCode);
				clearCommonReaderRevenueStateAndReload(
					asExistingSupporter,
					shouldHideReaderRevenue,
				);
			}
		})
		.catch((e) => console.error(`changeGeolocation - error: ${e}`));
};

type ReaderRevenueDevUtil = (
	asExistingSupporter: boolean,
	shouldHideReaderRevenue: boolean,
) => void;
export interface ReaderRevenueDevUtils {
	changeGeolocation: ReaderRevenueDevUtil;
	showMeTheEpic: ReaderRevenueDevUtil;
	showMeTheBanner: ReaderRevenueDevUtil;
	showNextVariant: ReaderRevenueDevUtil;
	showPreviousVariant: ReaderRevenueDevUtil;
}

const getForcedVariant = (type: 'epic' | 'banner'): string | null => {
	if (URLSearchParams) {
		const params = new URLSearchParams(window.location.search);
		const value = params.get(`force-${type}`);
		if (value) {
			return value;
		}
	}

	return null;
};

export {
	changeGeolocation,
	showMeTheEpic,
	showMeTheBanner,
	showNextVariant,
	showPreviousVariant,
	getForcedVariant,
};

