export const stringifyFileBase64 = (file: File) =>
	new Promise((resolve, reject) => {
		const reader = new FileReader();
		reader.addEventListener(
			'load',
			() => {
				const fileAsBase64 =
					reader &&
					reader.result &&
					reader.result.toString().split(';base64,')[1];
				// remove data:*/*;base64, from the start of the base64 string

				if (fileAsBase64) {
					resolve(fileAsBase64);
				} else {
					reject(
						new Error(
							'Sorry there was a problem with the file you uploaded above. Check the size and type. We only accept images, pdfs and .doc or .docx files',
						),
					);
				}
			},
			false,
		);
		reader.addEventListener('error', () => {
			reject(
				new Error(
					'Sorry there was a problem with the file you uploaded above. Check the size and type. We only accept images, pdfs and .doc or .docx files',
				),
			);
		});
		reader.readAsDataURL(file);
	});

import type { ABTestAPI } from '@guardian/ab-core';
import { mutate } from 'swr';
import useSWRImmutable from 'swr/immutable';

const apiPromise = new Promise<ABTestAPI>(() => {});
const key = 'ab-test-api';

/**
 * A hook which returns the AB Test Api when available,
 * or undefined otherwise.
 *
 * Leverages an immutable SWR to satisfy all requests to the
 * AB Core. As soon as the tests are available, all instances of
 * the useAB hook will render.
 */
export const useAB = (): ABTestAPI | undefined => {
	const { data } = useSWRImmutable(key, () => apiPromise);

	return data;
};

export const setABTests = (api: ABTestAPI) => {
	// eslint-disable-next-line @typescript-eslint/no-floating-promises
	mutate(key, api, false);
};

import { isAdBlockInUse } from '@guardian/commercial-core';
import { useEffect, useState } from 'react';

/**
 * @description
 * useAdBlockInUse provides a custom hook to integrate the isAdBlockInUse
 * promise into a react component
 * */
export const useAdBlockInUse = () => {
	const [isInUse, setIsInUse] = useState<boolean | undefined>();
	useEffect(() => {
		// eslint-disable-next-line @typescript-eslint/no-floating-promises
		isAdBlockInUse().then((blockerDetected) => {
			setIsInUse(blockerDetected);
		});
	}, []);

	return isInUse;
};

import type { SWRConfiguration } from 'swr';
import useSWR from 'swr';

function checkForErrors(response: Response) {
	if (!response.ok) {
		throw Error(
			response.statusText ||
				`useApi | An api call returned HTTP status ${response.status}`,
		);
	}
	return response;
}

const fetcher = (init?: RequestInit) => (url: string) =>
	fetch(url, init)
		.then(checkForErrors)
		.then((res) => res.json());

interface ApiResponse<T> {
	loading: boolean;
	data?: T;
	error?: Error;
}

/**
 * @description
 * A custom hook to make a GET request using the given url using the SWR lib (https://swr.vercel.app/)
 * returns { loading, error, data }
 * @param {String} url - The url to fetch
 * @param {SWRConfiguration} options - The SWR config object - https://swr.vercel.app/docs/options
 * @param {RequestInit} init - The fetch init object - https://developer.mozilla.org/en-US/docs/Web/API/Fetch_API/Using_Fetch#supplying_request_options
 * */
export const useApi = <T,>(
	url?: string,
	options?: SWRConfiguration,
	init?: RequestInit,
): ApiResponse<T> => {
	const { data, error } = useSWR(url, fetcher(init), options);

	return {
		data,
		error,
		loading: !error && !data,
	};
};

import type { BrazeMessagesInterface } from '@guardian/braze-components';
import { NullBrazeMessages } from '@guardian/braze-components/logic';
import useSWRImmutable from 'swr/immutable';
import { buildBrazeMessages } from './braze/buildBrazeMessages';

/**
 * Returns brazeMessages as BrazeMessagesInterface
 *
 * We're using useSWRImmutable to ensure this call is only made once
 * [doc]: https://swr.vercel.app/docs/revalidation#disable-automatic-revalidations
 */
export const useBraze = (
	idApiUrl: string,
): { brazeMessages: BrazeMessagesInterface | undefined } => {
	const { data: brazeMessages, error } = useSWRImmutable(
		'braze-message',
		() => buildBrazeMessages(idApiUrl),
	);

	if (error) {
		return { brazeMessages: new NullBrazeMessages() };
	}

	return { brazeMessages };
};

