import type { MutableRefObject } from 'react';
import { useEffect, useRef, useState } from 'react';

export function useHover<T>(): [MutableRefObject<T>, boolean] {
	const [value, setValue] = useState(false);

	const ref = useRef<any>(null);

	const handleMouseOver = () => setValue(true);
	const handleMouseOut = () => setValue(false);

	useEffect(() => {
		const node: HTMLElement | undefined = ref.current;
		if (node) {
			node.addEventListener('mouseover', handleMouseOver);
			node.addEventListener('mouseout', handleMouseOut);

			return () => {
				node.removeEventListener('mouseover', handleMouseOver);
				node.removeEventListener('mouseout', handleMouseOut);
			};
		}
		return () => {};
	}, []);

	return [ref, value];
}

import libDebounce from 'lodash-es/debounce';
import { useEffect, useRef, useState } from 'react';

/**
 * Custom hook around the `IntersectionObserver`.
 *
 * @param options
 * @param {boolean} [options.debouce] If `true`, debounce triggers by 200ms.
 * By default, trigger instantaneously. Enabling debouncing ensures the target
 * element intersects for at least 200ms before the callback is executed
 * @param {boolean} [options.repeat] If `true`, trigger the hook on
 * all intersections. By default, only trigger on the first intersection.
 * @param {boolean} [options.node] Set the initial node, if known.
 * @returns a tuple containing [isInView, setNode];
 */
const useIsInView = (
	options: IntersectionObserverInit & {
		debounce?: true;
		repeat?: true;
		node?: HTMLElement;
	},
): [boolean, React.Dispatch<React.SetStateAction<HTMLElement | null>>] => {
	const [isInView, setIsInView] = useState<boolean>(false);
	const [node, setNode] = useState<HTMLElement | null>(options.node ?? null);

	const observer = useRef<IntersectionObserver | null>(null);

	const intersectionFn: IntersectionObserverCallback = ([entry]) => {
		if (entry.isIntersecting) {
			setIsInView(true);
		} else if (options.repeat) {
			setIsInView(false);
		}
	};
	const intersectionCallback = options.debounce
		? libDebounce(intersectionFn, 200)
		: intersectionFn;

	useEffect(() => {
		// TODO: can we remove this? Itâ€™s now always cleaned up
		if (observer.current) {
			observer.current.disconnect();
		}

		if (window.IntersectionObserver) {
			observer.current = new window.IntersectionObserver(
				intersectionCallback,
				options,
			);

			if (node) {
				observer.current.observe(node);
			}
		}

		return () => observer.current?.disconnect();
	}, [node, options, intersectionCallback]);

	return [isInView, setNode];
};

export { useIsInView };

import type { Runnable } from '@guardian/ab-core';
import {
	signInGateTests,
	signInGateTestVariantToGateMapping,
} from '../components/SignInGate/signInGate';
import type {
	CurrentSignInGateABTest,
	SignInGateComponent,
} from '../components/SignInGate/types';
import { useAB } from './useAB';

/**
 * @description
 * A custom hook to make which selects the sign in gate (component and ab test)
 * to be displayed on the current page
 * */
export const useSignInGateSelector = ():
	| undefined
	| [SignInGateComponent | null, CurrentSignInGateABTest | null] => {
	const ab = useAB();
	if (!ab) return undefined;

	const test: Runnable | null = ab.firstRunnableTest(signInGateTests);

	if (!test) {
		return [null, null];
	}

	const currentTest: CurrentSignInGateABTest = {
		name: test.dataLinkNames || test.id,
		variant: test.variantToRun.id,
		id: test.id,
	};

	const gateVariant = signInGateTestVariantToGateMapping[currentTest.variant];

	if (!gateVariant) {
		return [null, null];
	}

	return [gateVariant, currentTest];
};

import { useEffect, useState } from 'react';
import type {
	CurrentSignInGateABTest,
	SignInGateComponent,
} from '../components/SignInGate/types';
import { useOnce } from './useOnce';
import { useSignInGateSelector } from './useSignInGateSelector';

type Props = {
	isSignedIn?: boolean;
	contentType: string;
	sectionName?: string;
	tags: TagType[];
	isPaidContent: boolean;
	isPreview: boolean;
};
/**
 * @description
 * A custom hook to determine if a sign in gate will show on the current page
 * @param {Boolean} isSignedIn - Is the user signed in to the guardian
 * */
export const useSignInGateWillShow = ({
	isSignedIn,
	contentType,
	sectionName,
	tags,
	isPaidContent,
	isPreview,
}: Props): boolean | undefined => {
	const [gateVariant, setGateVariant] = useState<
		SignInGateComponent | null | undefined
	>(undefined);
	const [currentTest, setCurrentTest] = useState<
		CurrentSignInGateABTest | null | undefined
	>(undefined);
	const [canShowGate, setCanShowGate] = useState(false);
	const gateSelector = useSignInGateSelector();

	useOnce(() => {
		const [gateSelectorVariant, gateSelectorTest] = gateSelector as [
			SignInGateComponent | null,
			CurrentSignInGateABTest | null,
		];
		setGateVariant(gateSelectorVariant);
		setCurrentTest(gateSelectorTest);
	}, [gateSelector]);

	useEffect(() => {
		if (gateVariant && currentTest) {
			// eslint-disable-next-line @typescript-eslint/no-floating-promises
			gateVariant
				.canShow({
					isSignedIn: !!isSignedIn,
					currentTest,
					contentType,
					sectionName,
					tags,
					isPaidContent,
					isPreview,
				})
				.then(setCanShowGate);
		}
	}, [
		currentTest,
		gateVariant,
		isSignedIn,
		contentType,
		sectionName,
		tags,
		isPaidContent,
		isPreview,
	]);

	return canShowGate && !!gateVariant && !!gateVariant.gate;
};

